INSERT INTO `tbl_timeline` (`timeLineId`, `tourId`, `title`, `description`) VALUES
(36, 176, 'TP. HỒ CHÍ MINH - PHÚ QUỐC', '<p>Tập trung tại s&acirc;n bay T&acirc;n Sơn Nhất, đ&aacute;p chuyến bay TP. HCM &ndash; Ph&uacute; Quốc.&nbsp;<em>(C&aacute;c chuyến bay dự kiến từ 09:45 &ndash; 11:00)</em>.&nbsp;Đến s&acirc;n bay Ph&uacute; Quốc, xe v&agrave; hướng dẫn vi&ecirc;n đ&oacute;n kh&aacute;ch khởi h&agrave;nh tham quan:&nbsp;</p>\r\n\r\n<p><strong>&bull;&nbsp;</strong><strong>Cơ sở sản xuất Rượu Sim</strong><em>&nbsp;(loại vang ri&ecirc;ng của đảo):</em>&nbsp;Kh&aacute;m ph&aacute; đặc sản vang Sim với hương vị độc đ&aacute;o v&agrave; t&igrave;m hiểu quy tr&igrave;nh sản xuất.</p>\r\n\r\n<p><strong>&bull;&nbsp; Vườn Ti&ecirc;u Ph&uacute; Quốc</strong>:&nbsp;Ti&ecirc;u Ph&uacute; Quốc nổi tiếng với hương vị ti&ecirc;u cay nồng, nổi tiếng.</p>\r\n\r\n<p>Ăn trưa với ẩm thực địa phương. Nhận ph&ograve;ng kh&aacute;ch sạn v&agrave; nghỉ ngơi (<em>Nhận ph&ograve;ng từ 14h00, hỗ trợ sớm nếu c&oacute; ph&ograve;ng trống</em>). Chiều, tiếp tục tham quan:</p>\r\n\r\n<p><strong>&bull;&nbsp;Trại rắn Đồng T&acirc;m 2</strong>&nbsp;&ndash; xứ sở c&aacute;c lo&agrave;i rắn: Trải nghiệm thế giới rắn với hơn 100 lo&agrave;i, từ rắn hiền l&agrave;nh đến những lo&agrave;i cực độc.</p>\r\n\r\n<p><strong>&bull;&nbsp;Dinh Cậu, Dinh B&agrave;</strong>&nbsp;&ndash; nơi gửi gắm đức tin của người d&acirc;n xứ đảo.</p>\r\n\r\n<p><strong>&bull;&nbsp;Tượng Đ&agrave;i B&aacute;c Hồ:</strong>&nbsp;Với chủ đề &ldquo;Miền Nam trong tr&aacute;i tim t&ocirc;i&rdquo; cao 20,7 m&eacute;t, nặng hơn 93 tấn v&agrave; l&agrave; biểu tượng về văn h&oacute;a, g&oacute;p phần khẳng định chủ quyền biển, đảo của Tổ quốc.</p>\r\n\r\n<p>Ăn tối tại nh&agrave; h&agrave;ng. Tiếp tục chương tr&igrave;nh:</p>\r\n\r\n<p><strong>&bull;&nbsp;</strong><strong>Tham quan Tơ Lụa Việt</strong>&nbsp;thương hiệu chuy&ecirc;n cung cấp c&aacute;c sản phẩm: chăn, ga gối nệm&hellip;</p>\r\n\r\n<p><strong>&bull;&nbsp;Kh&aacute;m ph&aacute; Grand World</strong>&nbsp;&ndash; Th&agrave;nh phố kh&ocirc;ng ngủ.&nbsp;<em>(Kh&ocirc;ng t&iacute;nh ph&iacute; v&agrave;o cổng &ndash; Chi ph&iacute; vui chơi tự t&uacute;c)</em>&nbsp;Tọa lạc tại vị tr&iacute; trung t&acirc;m Ph&uacute; Quốc United Center, diện t&iacute;ch l&ecirc;n đến 85ha với kiến tr&uacute;c cảnh quan rực rỡ lấy cảm hứng từ ch&acirc;u &Acirc;u. Được x&acirc;y dựng lộng lẫy tại 4 tiểu khu: Quảng trường, Shanghai, Indochine, Mallorca.</p>\r\n\r\n<p><em><strong>&bull;&nbsp;&nbsp;</strong>Show diễn&nbsp;<strong>&ldquo;Tinh Hoa Việt Nam&rdquo;</strong>&nbsp;&ndash; h&agrave;ng ng&agrave;y l&uacute;c 20:15&nbsp;<strong>(chi ph&iacute; v&eacute; tự t&uacute;c).</strong></em></p>\r\n\r\n<p><em><strong>&bull;&nbsp;</strong>Tận hưởng cảm hứng Ch&acirc;u &Acirc;u qua show diễn<strong>&nbsp;Sắc m&agrave;u Venice</strong>: Show diễn miễn ph&iacute;, t&aacute;i hiện c&acirc;u chuyện t&igrave;nh y&ecirc;u l&atilde;ng mạn trong kh&ocirc;ng gian Ch&acirc;u &Acirc;u.</em></p>\r\n\r\n<p>Trở về kh&aacute;ch sạn, nghỉ đ&ecirc;m.</p>'),
(37, 176, 'PHÚ QUỐC - KHÁM PHÁ ĐẢO NGỌC', '<p>Ăn s&aacute;ng. Khởi h&agrave;nh tham quan Nam Đảo:</p>\r\n\r\n<p><strong>&bull;&nbsp;Cơ sở nu&ocirc;i cấy Ngọc Trai</strong>: Kh&aacute;m ph&aacute; qu&aacute; tr&igrave;nh nu&ocirc;i trai lấy ngọc v&agrave; quan s&aacute;t trực tiếp quy tr&igrave;nh mổ trai. Qu&yacute; kh&aacute;ch c&oacute; thể mua trang sức ngọc trai l&agrave;m qu&agrave; lưu niệm.</p>\r\n\r\n<p><strong>&bull;</strong>&nbsp;<strong>Chương tr&igrave;nh T&agrave;u c&acirc;u c&aacute; &ndash; lặn ngắm san h&ocirc;:</strong>&nbsp;Khởi h&agrave;nh từ cảng biển, trải nghiệm c&acirc;u c&aacute; v&agrave; lặn ngắm san h&ocirc; với đầy đủ trang thiết bị.</p>\r\n\r\n<p>Trải nghiệm ăn trưa tr&ecirc;n t&agrave;u với &ldquo;chiến lợi phẩm&rdquo; hải sản biển c&acirc;u được.</p>\r\n\r\n<p>T&agrave;u đưa qu&yacute; kh&aacute;ch trở lại đất liền, tiếp tục tham quan:</p>\r\n\r\n<p><strong>&bull;</strong>&nbsp;<strong>Nh&agrave; t&ugrave; Ph&uacute; Quốc:</strong>&nbsp;Di t&iacute;ch quốc gia đặc biệt.</p>\r\n\r\n<p><strong>&bull;</strong>&nbsp;<strong>Ch&ugrave;a Hộ Quốc</strong>: Ng&ocirc;i ch&ugrave;a đẹp nhất đảo ngọc với vị tr&iacute;&nbsp;<em>&ldquo;tọa sơn hướng thủy&rdquo;.</em></p>\r\n\r\n<p><strong>&bull;</strong>&nbsp;<strong>Cơ sở sản xuất nước mắm</strong>: Kh&aacute;m ph&aacute; l&agrave;ng nghề truyền thống l&agrave;m n&ecirc;n thương hiệu nước mắm Ph&uacute; Quốc nổi tiếng thế giới.</p>\r\n\r\n<p><strong>&bull;</strong>&nbsp;<strong>Thị Trấn Ho&agrave;ng H&ocirc;n</strong>: Địa điểm du lịch h&agrave;ng đầu tại Ph&uacute; Quốc, mang đậm phong c&aacute;ch Địa Trung Hải với c&aacute;c biểu tượng nổi bật:&nbsp;<em>C&aacute;p treo H&ograve;n Thơm, Cầu H&ocirc;n, Th&aacute;p Đồng Hồ&hellip;</em></p>\r\n\r\n<p>Ăn tối buffet tại nh&agrave; h&agrave;ng. Xe đưa kh&aacute;ch về lại kh&aacute;ch sạn. Nghỉ đ&ecirc;m.&nbsp;</p>\r\n\r\n<p><em>***Nếu qu&yacute; kh&aacute;ch kh&ocirc;ng tham quan theo lịch tr&ecirc;n c&oacute; thể tự t&uacute;c với option tham quan sau:</em></p>\r\n\r\n<p><strong>Option tour: Tour Cano Tham quan 4 Đảo (chi ph&iacute; tự t&uacute;c từ: ~ 690,000VNĐ/kh&aacute;ch)</strong></p>\r\n\r\n<p>8h00: Đ&oacute;n kh&aacute;ch tại kh&aacute;ch sạn, tham quan cơ sở nu&ocirc;i cấy ngọc trai.</p>\r\n\r\n<p>✓<strong>&nbsp;H&ograve;n M&acirc;y R&uacute;t:</strong>&nbsp;Check-in v&agrave; tham quan.</p>\r\n\r\n<p>✓&nbsp;<strong>H&ograve;n Gầm Gh&igrave;</strong>: Bơi ngắm san h&ocirc; miễn ph&iacute;.</p>\r\n\r\n<p>✓&nbsp;<strong>H&ograve;n R&otilde;i</strong>: Trải nghiệm c&ocirc;ng vi&ecirc;n san h&ocirc; đi bộ dưới đ&aacute;y biển (chi ph&iacute; tự t&uacute;c).</p>\r\n\r\n<p>✓&nbsp;<strong>H&ograve;n M&oacute;ng Tay</strong>: Tắm biển, nghỉ ngơi, chụp ảnh, quay flycam (miễn ph&iacute;)</p>\r\n\r\n<p>Ăn trưa. Trở về kh&aacute;ch sạn.</p>\r\n\r\n<p>(<em>Nếu qu&yacute; kh&aacute;ch chọn g&oacute;i bao gồm th&ecirc;m C&aacute;p treo H&ograve;n Thơm th&igrave; chương tr&igrave;nh vẫn tiếp tục. Nếu qu&yacute; kh&aacute;ch kh&ocirc;ng đi C&aacute;p treo, cano đưa qu&yacute; kh&aacute;ch về lại cảng. Tuỳ v&agrave;o t&igrave;nh h&igrave;nh thời tiết c&aacute;c h&ograve;n đảo sẽ được linh động nhưng vẫn đảm bảo đủ 4 đảo)</em></p>'),
(38, 176, 'PHÚ QUỐC - TP.HỒ CHÍ MINH', '<p>Ăn s&aacute;ng. L&agrave;m thủ tục trả ph&ograve;ng. Ra s&acirc;n bay l&agrave;m thủ tục đ&aacute;p chuyến bay trở lại TP. HCM.</p>\r\n\r\n<p><em>(C&aacute;c chuyến bay dự kiến sau 12:00)</em>. Đến s&acirc;n bay T&acirc;n Sơn Nhất, kết th&uacute;c chương tr&igrave;nh tham quan!</p>\r\n\r\n<p><em>*** Nếu qu&yacute; kh&aacute;ch book v&eacute; m&aacute;y bay chặng về c&aacute;c chuyến tối c&oacute; thể tự t&uacute;c lựa chọn th&ecirc;m c&aacute;c option tour sau:</em></p>\r\n\r\n<p><strong>1. Vinpearl Safari Ph&uacute; Quốc</strong>&nbsp;<em>(chi ph&iacute; tự t&uacute;c &ndash; gi&aacute; tham khảo: 650,000VNĐ)</em>&nbsp;&ndash; c&ocirc;ng vi&ecirc;n bảo tồn động vật với m&ocirc; h&igrave;nh b&aacute;n hoang d&atilde; đầu ti&ecirc;n của Việt Nam c&oacute; diện t&iacute;ch 380 ha với nhiều lo&agrave;i động vật qu&yacute; hiếm.</p>\r\n\r\n<p><strong>2. Vinwonders</strong>&nbsp;<em>(chi ph&iacute; tự t&uacute;c &ndash; gi&aacute; tham khảo: 950,000VNĐ/kh&aacute;ch)</em>&nbsp;&ndash; l&agrave; một khu giải tr&iacute; tổng hợp với nhiều tr&ograve; chơi trong nh&agrave; v&agrave; ngo&agrave;i trời như:&nbsp;<em>tàu lượn si&ecirc;u tốc, đu quay v&ograve;ng xoay, đĩa quay si&ecirc;u tốc, biểu diễn c&aacute; heo, c&ocirc;ng vi&ecirc;n nước, thủy cung, lễ hội đường phố&hellip;</em></p>\r\n\r\n<p><em><strong>C&aacute;c mốc thời gian c&oacute; gi&aacute; trị tham khảo, t&ugrave;y theo điều kiện thực tế m&agrave; lịch tr&igrave;nh c&oacute; thể thay đổi cho ph&ugrave; hợp.</strong></em></p>'),
(39, 177, 'TP. HCM – ĐÀ NẴNG – SƠN TRÀ', '<p>Đ&oacute;n qu&yacute; kh&aacute;ch tại&nbsp;<strong>s&acirc;n bay T&acirc;n Sơn Nhất</strong>, đ&aacute;p chuyến bay khởi h&agrave;nh đi Đ&agrave; Nẵng (c&aacute;c chuyến bay dự kiến từ 07:00 &ndash; 09:30). Đến Đ&agrave; Nẵng, xe v&agrave; HDV đ&oacute;n kh&aacute;ch.</p>\r\n\r\n<p>Ăn trưa đặc sản Đ&agrave; Nẵng&nbsp;<em>&ldquo;B&aacute;nh tr&aacute;ng thịt heo 2 đầu da &amp; m&igrave; Quảng&rdquo;</em>. Nhận ph&ograve;ng, nghỉ ngơi.</p>\r\n\r\n<p>Buổi chiều, khởi h&agrave;nh tham quan&nbsp;<strong>b&aacute;n đảo Sơn Tr&agrave;</strong>&nbsp;ngắm phố biển từ tr&ecirc;n cao.</p>\r\n\r\n<p><strong>&bull;&nbsp;</strong>Viếng<strong>&nbsp;Linh Ứng Tự</strong>&nbsp;&ndash; nơi c&oacute; tượng Phật B&agrave; cao 67 m&eacute;t cao nhất Việt Nam.</p>\r\n\r\n<p><strong>&bull;&nbsp;C&ocirc;ng vi&ecirc;n kỳ quan thế giới Đ&agrave; Nẵng</strong>&nbsp;&ndash; c&ocirc;ng tr&igrave;nh thu nhỏ m&ocirc; phỏng 9 kỳ quan thế giới v&agrave; c&aacute;c địa danh nổi tiếng tại Việt Nam.</p>\r\n\r\n<p><strong>&bull;&nbsp;</strong>Tắm biển<strong>&nbsp;Mỹ Kh&ecirc;</strong>&nbsp;&ndash; từng được tạp ch&iacute; Forbes b&igrave;nh chọn l&agrave;&nbsp;b&atilde;i biển quyến rũ nhất h&agrave;nh tinh.</p>\r\n\r\n<p>Ăn tối. Tự do thưởng ngoạn du thuyền s&ocirc;ng H&agrave;n ngắm cảnh Đ&agrave; Th&agrave;nh về đ&ecirc;m.&nbsp;<em>(chi ph&iacute; tự t&uacute;c)</em></p>'),
(40, 177, 'ĐÀ NẴNG – CÙ LAO CHÀM – HỘI AN', '<p>Ăn s&aacute;ng buffet tại kh&aacute;ch sạn. Di chuyển v&agrave;o Hội An, đến biển Cửa Đại, ngồi t&agrave;u cao tốc khởi h&agrave;nh đi&nbsp;<strong>C&ugrave; Lao Ch&agrave;m</strong>&nbsp;&ndash; khu dự trữ sinh quyển thế giới.</p>\r\n\r\n<p><strong>&bull; Trải nghiệm cano cao tốc</strong>&nbsp;lướt tr&ecirc;n biển xanh, cano cập cảng B&atilde;i L&agrave;ng C&ugrave; Lao.</p>\r\n\r\n<p><strong>&bull; Khu trưng b&agrave;y nền văn h&oacute;a Sa Huỳnh, khu bảo tồn biển C&ugrave; Lao Ch&agrave;m, ch&ugrave;a Hải Tạng cổ, chợ hải sản T&acirc;n Hiệp</strong>&hellip;</p>\r\n\r\n<p><strong>&bull;&nbsp;</strong>Tham gia c&aacute;c hoạt động lặn biển&nbsp;<em>(Snorkeling)&nbsp;</em>ngắm san h&ocirc;.</p>\r\n\r\n<p>Ăn trưa tại C&ugrave; Lao Ch&agrave;m. Cano đưa kh&aacute;ch trở về đất liền. Tham quan:</p>\r\n\r\n<p><strong>&bull; Phố cổ Hội An</strong>&nbsp;&ndash; du kh&aacute;ch tản bộ kh&aacute;m ph&aacute; c&aacute;c c&ocirc;ng tr&igrave;nh kiến tr&uacute;c nổi tiếng:&nbsp;<em>Ch&ugrave;a Cầu Nhật Bản, c&aacute;c ng&ocirc;i nh&agrave; cổ h&agrave;ng trăm tuổi, Hội Qu&aacute;n Phước Kiến, Xưởng thủ c&ocirc;ng mỹ nghệ</em>. Phố cổ Hội An l&agrave; qu&aacute; khứ v&agrave;ng son của một thương cảng sầm uất thời phong kiến.</p>\r\n\r\n<p>Ăn tối. Khởi h&agrave;nh về lại Đ&agrave; Nẵng. Tự do kh&aacute;m ph&aacute; Đ&agrave; Nẵng về đ&ecirc;m. Nghỉ đ&ecirc;m tại&nbsp;<strong>Đ&agrave; Nẵng</strong>.</p>'),
(41, 179, 'ĐÀ NẴNG - BÁN ĐẢO SƠN TRÀ - MỸ KHÊ', '<p>Tại&nbsp;<strong>s&acirc;n bay Quốc tế Đ&agrave; Nẵng</strong>,&nbsp;xe v&agrave; HDV đ&oacute;n kh&aacute;ch.</p>\r\n\r\n<p>Ăn trưa đặc sản Đ&agrave; Nẵng&nbsp;<em>&ldquo;B&aacute;nh tr&aacute;ng thịt heo 2 đầu da &amp; m&igrave; Quảng&rdquo;</em>. Nhận ph&ograve;ng, nghỉ ngơi.</p>\r\n\r\n<p>Buổi chiều, khởi h&agrave;nh tham quan&nbsp;<strong>b&aacute;n đảo Sơn Tr&agrave;</strong>&nbsp;ngắm phố biển từ tr&ecirc;n cao.</p>\r\n\r\n<p><strong>&bull;&nbsp;</strong>Viếng<strong>&nbsp;Linh Ứng Tự</strong>&nbsp;&ndash; nơi c&oacute; tượng Phật B&agrave; cao 67 m&eacute;t cao nhất Việt Nam.</p>\r\n\r\n<p><strong>&bull;&nbsp;</strong>Tắm biển<strong>&nbsp;Mỹ Kh&ecirc;</strong>&nbsp;&ndash; từng được tạp ch&iacute; Forbes b&igrave;nh chọn l&agrave;&nbsp;b&atilde;i biển quyến rũ nhất h&agrave;nh tinh.</p>\r\n\r\n<p>Ăn tối. Tự do thưởng ngoạn du thuyền s&ocirc;ng H&agrave;n ngắm cảnh Đ&agrave; Th&agrave;nh về đ&ecirc;m.&nbsp;<em>(chi ph&iacute; tự t&uacute;c)</em></p>'),
(42, 179, 'ĐÀ NẴNG - PHỐ CỔ HỘI AN', '<p>Ăn s&aacute;ng buffet tại kh&aacute;ch sạn. Khởi h&agrave;nh tham quan:</p>\r\n\r\n<p><strong>&bull;&nbsp;Cầu T&igrave;nh Y&ecirc;u</strong>&nbsp;v&agrave; check-in với biểu tượng<strong>&nbsp;C&aacute; Ch&eacute;p H&oacute;a Rồng</strong>,&nbsp;<strong>cầu Rồng Đ&agrave; Nẵng</strong>&hellip;</p>\r\n\r\n<p><strong>&bull;&nbsp;Bảo t&agrave;ng Phật Học</strong>&nbsp;&ndash; Bảo t&agrave;ng văn h&oacute;a Phật gi&aacute;o đầu ti&ecirc;n ở Việt Nam.</p>\r\n\r\n<p><strong>&bull;&nbsp;Ch&ugrave;a Quan Thế &Acirc;m</strong>&nbsp;&ndash; tọa lạc tại ch&acirc;n n&uacute;i Kim Sơn, một trong năm ngọn Ngũ H&agrave;nh Sơn.</p>\r\n\r\n<p><strong>&bull;&nbsp;L&agrave;ng nghề đi&ecirc;u khắc đ&aacute;</strong>&nbsp;v&agrave; mua sắm tại cửa h&agrave;ng đặc sản Miền Trung.</p>\r\n\r\n<p>Ăn trưa. Khởi h&agrave;nh tham quan&nbsp;<strong>Phố cổ Hội An</strong>&nbsp;&ndash; du kh&aacute;ch tản bộ kh&aacute;m ph&aacute; c&aacute;c c&ocirc;ng tr&igrave;nh kiến tr&uacute;c nổi tiếng:&nbsp;<em>Ch&ugrave;a Cầu Nhật Bản, c&aacute;c ng&ocirc;i nh&agrave; cổ h&agrave;ng trăm tuổi, Hội Qu&aacute;n Phước Kiến, Xưởng thủ c&ocirc;ng mỹ nghệ</em>. Phố cổ Hội An l&agrave; qu&aacute; khứ v&agrave;ng son của một thương cảng sầm uất thời phong kiến.</p>\r\n\r\n<p>Ăn tối với&nbsp;<strong><em>đặc sản Cao lầu Hội An</em></strong>&nbsp;nổi tiếng. Khởi h&agrave;nh về lại Đ&agrave; Nẵng.</p>\r\n\r\n<p>Tự do kh&aacute;m ph&aacute; Đ&agrave; Nẵng về đ&ecirc;m. Nghỉ đ&ecirc;m tại&nbsp;<strong>Đ&agrave; Nẵng</strong>.</p>'),
(43, 179, 'ĐÀ NẴNG – BÀ NÀ – HUẾ', '<p>Ăn s&aacute;ng buffet tại kh&aacute;ch sạn. Trả ph&ograve;ng. Khởi h&agrave;nh tham quan:</p>\r\n\r\n<p><strong>&bull;&nbsp;Khu du lịch B&agrave; N&agrave; Hills</strong>&nbsp;&ndash; chinh phục B&agrave; N&agrave; bằng hệ thống c&aacute;p treo một d&acirc;y d&agrave;i nhất thế giới 5.801m v&agrave; l&agrave; một trong 10 tuyến c&aacute;p treo ấn tượng nhất thế giới. B&agrave; N&agrave; l&agrave; nơi c&oacute; những khoảnh khoắc giao m&ugrave;a bất ngờ&nbsp;<em>Xu&acirc;n &ndash; Hạ &ndash; Thu &ndash; Đ&ocirc;ng</em>&nbsp;trong một ng&agrave;y.</p>\r\n\r\n<p><strong><em>(chi ph&iacute; c&aacute;p treo B&agrave; N&agrave; tự t&uacute;c)</em></strong></p>\r\n\r\n<p><strong>&bull;&nbsp;Ch&ugrave;a Linh Ứng</strong>&nbsp;với tượng Phật Th&iacute;ch Ca cao 27m, đền thờ&nbsp;<strong>B&agrave; Ch&uacute;a Mẫu Thượng Ng&agrave;n</strong>.</p>\r\n\r\n<p><strong>&bull;&nbsp;Cầu V&agrave;ng</strong>&nbsp;&ndash; c&acirc;y cầu độc đ&aacute;o nằm trong vườn Thi&ecirc;n Thai, được x&acirc;y dựng tr&ecirc;n độ cao 1.400m so với mặt nước biển, được n&acirc;ng đỡ bởi kiến tr&uacute;c h&igrave;nh đ&ocirc;i b&agrave;n tay khổng lồ độc đ&aacute;o.</p>\r\n\r\n<p><strong>&bull;&nbsp;C&ocirc;ng vi&ecirc;n Fantasy Park</strong>&nbsp;với hơn 100 tr&ograve; chơi phi&ecirc;u lưu hấp dẫn, mang đến cho qu&yacute; kh&aacute;ch nhiều cung bậc cảm x&uacute;c bất ngờ v&agrave; th&uacute; vị.</p>\r\n\r\n<p><strong>Ăn trưa buffet tại B&agrave;</strong>&nbsp;<strong>N&agrave;&nbsp;<em>(chi ph&iacute; tự t&uacute;c)</em></strong>.</p>\r\n\r\n<p><em>(Nếu qu&yacute; kh&aacute;ch kh&ocirc;ng tham quan B&agrave; N&agrave; th&igrave; tự t&uacute;c ăn trưa v&agrave; tự t&uacute;c chi ph&iacute; nhập đo&agrave;n tại điểm hẹn).</em></p>\r\n\r\n<p>Khởi h&agrave;nh đi&nbsp;<strong>Huế</strong>&nbsp;đi xuy&ecirc;n&nbsp;<strong>hầm đường bộ đ&egrave;o Hải V&acirc;n</strong>. Tr&ecirc;n đường di chuyển, tham quan:</p>\r\n\r\n<p><strong>&bull;&nbsp;L&agrave;ng ch&agrave;i Lăng C&ocirc;</strong>&nbsp;chụp h&igrave;nh lưu niệm.</p>\r\n\r\n<p><strong>&bull;&nbsp;Ch&ugrave;a Thi&ecirc;n Mụ</strong>&nbsp;cổ k&iacute;nh l&agrave; nơi lưu giữ nhiều cổ vật c&oacute; gi&aacute; trị về mặt lịch sử v&agrave; nghệ thuật.</p>\r\n\r\n<p>Nhận ph&ograve;ng kh&aacute;ch sạn.</p>\r\n\r\n<p>Ăn tối với đặc sản xứ Huế&nbsp;<em>(B&aacute;nh b&egrave;o, lọc, nậm, kho&aacute;i&hellip;).</em></p>\r\n\r\n<p>Xu&ocirc;́ng thuy&ecirc;̀n R&ocirc;̀ng thưởng thức&nbsp;<strong>Ca Hu&ecirc;́ tr&ecirc;n s&ocirc;ng Hương</strong>&nbsp;&ndash; nét văn hóa đ&ocirc;̣c đáo của xứ Hu&ecirc;́.</p>\r\n\r\n<p>Nghỉ đ&ecirc;m tại Huế.</p>'),
(44, 179, 'HUẾ – ĐÀ NẴNG – TP. HCM', '<p>Ăn s&aacute;ng buffet tại kh&aacute;ch sạn. Trả ph&ograve;ng. Khởi h&agrave;nh tham quan:</p>\r\n\r\n<p><strong>&bull;&nbsp;Đại Nội &ndash; Ho&agrave;ng cung triều Nguyễn</strong>, được x&acirc;y dựng từ đầu thế kỷ XIX đến nửa đầu thế kỷ XX, l&agrave; một trong số c&aacute;c di t&iacute;ch thuộc cụm Quần thể di t&iacute;ch Cố đ&ocirc; Huế được c&ocirc;ng nhận l&agrave; Di sản Văn ho&aacute; Thế giới UNESCO từ năm 1993. To&agrave;n cảnh Đại Nội Huế c&ograve;n lưu giữ nhiều dấu ấn đặc sắc của triều đ&igrave;nh phong kiến nh&agrave; Nguyễn h&agrave;ng trăm năm.</p>\r\n\r\n<p><strong>&bull;&nbsp;L&agrave;ng hương Thủy Xu&acirc;n</strong>&nbsp;&ndash; L&agrave;ng nghề l&agrave;m hương truyền thống hơn 700 năm của xứ Huế. T&igrave;m hiểu về quy tr&igrave;nh v&agrave; c&aacute;c c&ocirc;ng đoạn l&agrave;m hương, tự do chụp ảnh lưu niệm.</p>\r\n\r\n<p>Ăn trưa. Di chuyển ra s&acirc;n bay đ&aacute;p chuyến bay về lại TP. HCM.&nbsp;<strong>S&acirc;n bay Huế</strong>&nbsp;<em>(c&aacute;c chuyến bay dự kiến sau 14:00).</em>&nbsp;<strong>S&acirc;n bay Đ&agrave; Nẵng</strong>&nbsp;<em>(c&aacute;c chuyến bay dự kiến sau 17:00)</em>.&nbsp;</p>\r\n\r\n<p>Chia tay đo&agrave;n v&agrave; hẹn gặp lại. Kết th&uacute;c chương tr&igrave;nh tham quan.</p>\r\n\r\n<p><strong><em>C&aacute;c mốc thời gian c&oacute; gi&aacute; trị tham khảo, t&ugrave;y theo điều kiện thực tế m&agrave; lịch tr&igrave;nh c&oacute; thể thay đổi cho ph&ugrave; hợp.</em></strong></p>'),
(45, 181, 'TP. HỒ CHÍ MINH – HÀ NỘI', '<p>Tập trung tại s&acirc;n bay T&acirc;n Sơn Nhất đ&aacute;p chuyến bay TP. HCM &ndash; H&Agrave; NỘI.&nbsp;<em>(C&aacute;c chuyến bay dự kiến từ 06:00 &ndash; 09:00).</em>&nbsp;Xe v&agrave; HDV đ&oacute;n kh&aacute;ch tại s&acirc;n bay Nội B&agrave;i, đo&agrave;n di chuyển về trung t&acirc;m H&agrave; Nội. Ăn trưa. Nhận ph&ograve;ng kh&aacute;ch sạn. Khởi h&agrave;nh tham quan:</p>\r\n\r\n<p><strong>&bull;</strong>&nbsp;<strong>Hồ T&acirc;y</strong>&nbsp;&ndash; Hồ lớn nhất H&agrave; Nội,&nbsp;<strong>ch&ugrave;a Trấn Quốc, đền Qu&aacute;n Th&aacute;nh</strong>.</p>\r\n\r\n<p><strong>&bull;</strong>&nbsp;<strong>Văn Miếu &ndash; Quốc Tử Gi&aacute;m</strong>&nbsp;&ndash; trường đại học đầu ti&ecirc;n của Việt Nam; tại đ&acirc;y c&oacute; hoạt động xin chữ đầu xu&acirc;n bởi c&aacute;c &Ocirc;ng Đồ &ndash; tiếp nối truyền thống văn h&oacute;a Tết của Việt Nam.</p>\r\n\r\n<p><strong>&bull;</strong>&nbsp;Ngồi xe điện tham quan&nbsp;<strong>phố cổ H&agrave; Nội</strong>&nbsp;với phố H&agrave;ng Ngang, H&agrave;ng Đ&agrave;o, H&agrave;ng B&ocirc;ng&hellip; ngắm Hồ Gươm, cầu Th&ecirc; H&uacute;c, đền Ngọc Sơn.</p>\r\n\r\n<p>Ăn tối. Tự do kh&aacute;m ph&aacute; thủ đ&ocirc; H&agrave; Nội về đ&ecirc;m. Nghỉ đ&ecirc;m tại H&agrave; Nội.</p>'),
(46, 181, 'HÀ NỘI – TRÀNG AN – BÁI ĐÍNH – HẠ LONG', '<p>Ăn s&aacute;ng. L&agrave;m thủ tục trả ph&ograve;ng. Khởi h&agrave;nh tham quan:</p>\r\n\r\n<p><strong>&bull;&nbsp;Khu du lịch Tr&agrave;ng An &ndash; Ninh B&igrave;nh</strong>&nbsp;đ&atilde; được UNESCO c&ocirc;ng nhận l&agrave; Di sản Văn h&oacute;a v&agrave; Thi&ecirc;n nhi&ecirc;n Thế giới v&agrave;o năm 2014. Du kh&aacute;ch ngồi thuyền thưởng ngoạn vẻ đẹp hoang sơ được v&iacute; như &ldquo;Vịnh Hạ Long tr&ecirc;n cạn&rdquo; với v&ocirc; v&agrave;n c&aacute;c hạng động, hệ thống s&ocirc;ng, suối chảy tr&agrave;n trong c&aacute;c thung lũng, c&aacute;c hang xuy&ecirc;n thủy động, c&aacute;c d&atilde;y n&uacute;i đ&aacute; v&ocirc;i tr&ugrave;ng điệp&hellip;</p>\r\n\r\n<p><em>(Lưu &yacute;: Trường hợp Tr&agrave;ng An qu&aacute; tải, kh&ocirc;ng đảm bảo thời gian cho du kh&aacute;ch xuống Hạ Long, c&ocirc;ng ty sẽ đối ứng điểm tham quan thay thế:&nbsp;<strong>Tam Cốc &ndash; được mệnh danh l&agrave; &ldquo;Hạ Long tr&ecirc;n cạn&rdquo;)</strong></em></p>\r\n\r\n<p>Ăn trưa với đặc sản&nbsp;<strong>cơm ch&aacute;y d&ecirc; n&uacute;i.</strong></p>\r\n\r\n<p><strong>&bull;&nbsp;</strong>&nbsp;Tham quan&nbsp;<strong>ch&ugrave;a B&aacute;i Đ&iacute;nh</strong>&nbsp;&ndash; ng&ocirc;i ch&ugrave;a lớn nổi tiếng tại Miền Bắc, l&agrave;m lễ cầu ph&uacute;c l&agrave;nh.</p>\r\n\r\n<p>Rời&nbsp;<strong>Ninh B&igrave;nh</strong>, theo quốc lộ 10 đi Hạ Long. Tr&ecirc;n đường đi qu&yacute; kh&aacute;ch sẽ được cảm nhận cuộc sống, phong cảnh đặc trưng của đồng bằng Bắc Bộ với cảnh l&agrave;ng qu&ecirc; thanh b&igrave;nh qua c&aacute;c tỉnh Nam Định, Th&aacute;i B&igrave;nh, Hải Ph&ograve;ng.</p>\r\n\r\n<p>Ăn tối tại Hạ Long. Tự do kh&aacute;m ph&aacute;&nbsp;<strong>chợ đ&ecirc;m Hạ Long</strong>. Nghỉ đ&ecirc;m tại Hạ Long.</p>'),
(47, 181, 'HẠ LONG – YÊN TỬ – HÀ NỘI', '<p>Ăn s&aacute;ng. Trả ph&ograve;ng kh&aacute;ch sạn. Khởi h&agrave;nh tham quan:</p>\r\n\r\n<p><strong>&bull;&nbsp;Vịnh Hạ Long</strong>&nbsp;&ndash; Di sản thi&ecirc;n nhi&ecirc;n Thế giới được UNESCO c&ocirc;ng nhận. Chi&ecirc;m ngưỡng vẻ đẹp huyền b&iacute; của h&agrave;ng ng&agrave;n đảo đ&aacute; v&agrave; c&aacute;c hang động kỳ th&uacute;. Tham quan động Thi&ecirc;n Cung, ngắm cảnh L&agrave;ng Ch&agrave;i, h&ograve;n Ấm, h&ograve;n R&ugrave;a, h&ograve;n Đỉnh Hương, h&ograve;n Ch&oacute; Đ&aacute;, h&ograve;n G&agrave; Chọi&hellip;</p>\r\n\r\n<p>Ăn trưa. Chiều tham quan:</p>\r\n\r\n<p><strong>&bull;&nbsp;</strong>&nbsp;<strong>Khu danh thắng đất Phật Y&ecirc;n Tử</strong>&nbsp;&ndash; đất tổ của Thiền Ph&aacute;i Tr&uacute;c L&acirc;m. Qu&yacute; kh&aacute;ch đi c&aacute;p treo, tham quan Vườn Th&aacute;p Tổ, Ch&ugrave;a Hoa Y&ecirc;n&nbsp;<em><strong>(đ&atilde; bao gồm v&eacute; c&aacute;p treo Y&ecirc;n Tử chặng Hoa Y&ecirc;n).</strong></em></p>\r\n\r\n<p>Di chuyển về H&agrave; Nội, tr&ecirc;n đường dừng ch&acirc;n thưởng thức đặc sản b&aacute;nh đậu xanh Hải Dương.</p>\r\n\r\n<p>Ăn tối. Nhận ph&ograve;ng. Nghỉ đ&ecirc;m tại H&agrave; Nội.</p>'),
(48, 181, 'HÀ NỘI – LÀO CAI – SAPA', '<p>Ăn s&aacute;ng. Trả ph&ograve;ng kh&aacute;ch sạn. Khởi h&agrave;nh tham quan:</p>\r\n\r\n<p><strong>&bull;</strong>&nbsp;<strong>Lăng Chủ Tịch Hồ Ch&iacute; Minh, Phủ Chủ Tịch, ch&ugrave;a Một Cột, nh&agrave; s&agrave;n, ao c&aacute; B&aacute;c Hồ&hellip;</strong>&nbsp;<em><strong>(Lưu &yacute;: Lăng B&aacute;c kh&ocirc;ng mở cửa viếng v&agrave;o thứ 2, thứ 6 v&agrave; c&aacute;c buổi chiều trong tuần).</strong></em></p>\r\n\r\n<p><strong>&bull; L&agrave;ng gốm B&aacute;t Tr&agrave;ng</strong>&nbsp;&ndash; nơi giữ nguy&ecirc;n n&eacute;t đẹp văn h&oacute;a truyền thống của l&agrave;ng cổ Bắc Bộ xưa với kh&ocirc;ng kh&iacute; đ&oacute;n Tết cổ truyền đầu năm mới. Khởi đầu một năm &ldquo;Vạn sự như &yacute;&rdquo;!</p>\r\n\r\n<p><strong>Ăn trưa với cỗ Tết truyền thống Bắc Bộ</strong>&nbsp;được nấu bởi c&aacute;c gia đ&igrave;nh nghệ nh&acirc;n tại B&aacute;t Tr&agrave;ng.</p>\r\n\r\n<p>Khởi h&agrave;nh đi&nbsp;<strong>SaPa</strong>&nbsp;&ndash; được người Ph&aacute;p xưa gọi l&agrave;&nbsp;<em>&ldquo;Kinh đ&ocirc; m&ugrave;a h&egrave; của xứ Bắc Kỳ&rdquo;</em>, chạy theo đường cao tốc Nội B&agrave;i &ndash; L&agrave;o Cai d&agrave;i 250 km. Ngắm phong cảnh tuyệt đẹp tr&ecirc;n cung đường T&acirc;y Bắc, dừng ch&acirc;n nghỉ ngơi. Đến SaPa, nhận ph&ograve;ng.</p>\r\n\r\n<p>Ăn tối. Tự do kh&aacute;m ph&aacute; SaPa về đ&ecirc;m hoặc thưởng thức c&aacute;c m&oacute;n ăn đặc trưng của đồng b&agrave;o v&ugrave;ng T&acirc;y Bắc:&nbsp;<em>Thắng Cố, cơm lam, lợn cắp n&aacute;ch, khoai nướng, bắp nướng&hellip;</em></p>'),
(49, 181, 'SAPA – BẢN CÁT CÁT – FANSIPAN', '<p>Ăn s&aacute;ng. Khởi h&agrave;nh tham quan&nbsp;<strong>bản C&aacute;t C&aacute;t.</strong></p>\r\n\r\n<p><strong>&bull;</strong>&nbsp;Tản bộ kh&aacute;m ph&aacute;&nbsp;<strong>bản C&aacute;t C&aacute;t</strong>&nbsp;của người H&rsquo;M&ocirc;ng, được người Ph&aacute;p ph&aacute;t hiện v&agrave; chọn l&agrave;m khu nghỉ dưỡng cho c&aacute;c quan chức thuộc địa từ đầu thế kỷ XX. Đến với bản C&aacute;t C&aacute;t, du kh&aacute;ch tham quan: trạm thủy điện C&aacute;t C&aacute;t, th&aacute;c nước C&aacute;t C&aacute;t&hellip; Ăn trưa. Di chuyển đến ga c&aacute;p treo Fansipan bắt đầu h&agrave;nh tr&igrave;nh:</p>\r\n\r\n<p><strong>&bull;</strong>&nbsp;<strong>Chinh phục Fansipan</strong>&nbsp;bằng hệ thống c&aacute;p treo 3 d&acirc;y hiện đại nhất thế giới với cabin c&oacute; sức chứa tới 35 kh&aacute;ch. Vượt qua 639 bậc thang chinh phục đỉnh Fansipan tr&ecirc;n độ cao 3.143m &ndash; n&oacute;c nh&agrave; của Đ&ocirc;ng Dương.&nbsp;<em>(Chi ph&iacute; c&aacute;p treo tự t&uacute;c, gi&aacute; tham khảo: 850,000VNĐ/kh&aacute;ch)</em></p>\r\n\r\n<p>Ăn tối. Thư gi&atilde;n với liệu tr&igrave;nh massage ch&acirc;n thảo dược của người Dao Đỏ gi&uacute;p t&aacute;i tạo năng lượng,&nbsp;<strong>mỗi kh&aacute;ch đươc tặng 01 v&eacute; food massage</strong>. Tự do kh&aacute;m ph&aacute; SaPa về đ&ecirc;m. Nghỉ đ&ecirc;m tại SaPa.</p>'),
(50, 181, 'SAPA – HÀ NỘI – TP. HCM', '<p>Ăn s&aacute;ng. Trả ph&ograve;ng kh&aacute;ch sạn. Khởi h&agrave;nh về lại H&agrave; Nội.</p>\r\n\r\n<p>Ăn trưa. Di chuyển ra s&acirc;n bay Nội B&agrave;i đ&aacute;p chuyến bay H&Agrave; NỘI &ndash; TP. HCM.</p>\r\n\r\n<p><em>(C&aacute;c chuyến bay dự kiến từ 16:30 &ndash; 17:30)</em>. Kết th&uacute;c chương tr&igrave;nh tham quan!</p>\r\n\r\n<p><em><strong>C&aacute;c mốc thời gian c&oacute; gi&aacute; trị tham khảo, t&ugrave;y theo điều kiện thực tế m&agrave; lịch tr&igrave;nh c&oacute; thể thay đổi cho ph&ugrave; hợp.</strong></em></p>'),
(51, 183, 'TP. HCM – HÀ NỘI – HÀ GIANG', '<p>Tập trung tại s&acirc;n bay T&acirc;n Sơn Nhất đ&aacute;p chuyến bay TP. HCM &ndash; H&Agrave; NỘI.&nbsp;<em>(C&aacute;c chuyến bay dự kiến trước 07:00).</em>&nbsp;Xe v&agrave; HDV đ&oacute;n kh&aacute;ch tại s&acirc;n bay Nội B&agrave;i. Khởi h&agrave;nh đi&nbsp;<strong>H&agrave; Giang</strong>.</p>\r\n\r\n<p>Ăn trưa tại Ph&uacute; Thọ. Tiếp tục h&agrave;nh tr&igrave;nh đến H&agrave; Giang, đo&agrave;n check-in với&nbsp;<strong>cột mốc Km 0</strong>&nbsp;&ndash; điểm khởi đầu con đường hạnh ph&uacute;c xuy&ecirc;n cao nguy&ecirc;n đ&aacute;.</p>\r\n\r\n<p>Ăn tối. Nghỉ đ&ecirc;m tại&nbsp;<strong>H&agrave; Giang</strong>.</p>'),
(52, 183, 'HÀ GIANG – QUẢN BẠ – ĐỒNG VĂN', '<p>Ăn s&aacute;ng tại kh&aacute;ch sạn. Trả ph&ograve;ng. Di chuyển tr&ecirc;n con đường hạnh ph&uacute;c đến thị trấn Đồng Văn, tr&ecirc;n đường đi du kh&aacute;ch chi&ecirc;m ngưỡng&nbsp;<strong>N&uacute;i Đ&ocirc;i C&ocirc; Ti&ecirc;n Quản Bạ</strong>. Khởi h&agrave;nh tham quan:</p>\r\n\r\n<p><strong>&bull; Phố C&aacute;o (Sủng L&agrave;)</strong>&nbsp;l&agrave; một thung lũng nằm ở v&ugrave;ng n&uacute;i T&acirc;y Bắc Việt Nam. Sở hữu lối kiến tr&uacute;c độc đ&aacute;o của những ng&ocirc;i nh&agrave; tr&igrave;nh tường m&agrave;u v&agrave;ng hoặc m&agrave;u đỏ nhuộm m&agrave;u thời gian. L&agrave;ng văn h&oacute;a&nbsp;<strong>Lũng Cẩm</strong>&nbsp;&ndash; bối cảnh những thước phim nổi tiếng &ldquo;Chuyện của Pao&rdquo;.</p>\r\n\r\n<p>Ăn trưa. Nhận ph&ograve;ng kh&aacute;ch sạn, nghỉ ngơi. Chiều, tham quan:</p>\r\n\r\n<p><strong>&bull; Dinh thự vua M&egrave;o Vương Ch&iacute;nh Đức</strong>&nbsp;l&agrave; một c&ocirc;ng tr&igrave;nh dinh thự lớn được x&acirc;y dựng từ cuối thế kỉ XIX với những n&eacute;t kiến tr&uacute;c độc đ&aacute;o v&agrave; những c&acirc;u chuyện b&iacute; ẩn xung quanh.</p>\r\n\r\n<p><strong>&bull; Cột cờ Lũng C&uacute; &ndash; Cực Bắc</strong>&nbsp;&ndash; Nơi địa đầu Tổ Quốc, hay c&ograve;n được mi&ecirc;u tả l&agrave;:&nbsp;<em>&ldquo;Nơi c&uacute;i mặt s&aacute;t đất, ngẩng mặt đụng trời&rdquo;</em>. Ngắm bản S&eacute;o Lủng v&agrave; c&aacute;c thửa ruộng bậc thang từ tr&ecirc;n cao.</p>\r\n\r\n<p>Ăn tối. Tự do kh&aacute;m ph&aacute; khu phố cổ Đồng Văn về đ&ecirc;m. Nghỉ đ&ecirc;m tại&nbsp;<strong>Đồng Văn</strong>.</p>'),
(53, 183, 'ĐỒNG VĂN – MÃ PÌ LÈNG – CAO BẰNG', '<p>Ăn s&aacute;ng. Trả ph&ograve;ng. Khởi h&agrave;nh đi Cao Bằng. Tr&ecirc;n đường đi, thưởng ngoạn đ&egrave;o&nbsp;<strong>M&atilde; P&igrave; L&egrave;ng</strong>&nbsp;&ndash; Nơi được mệnh danh l&agrave; đệ nhất h&ugrave;ng quan của Việt Nam, hay con đường hạnh ph&uacute;c.</p>\r\n\r\n<p>Ăn trưa. Tiếp tục h&agrave;nh tr&igrave;nh đi Cao Bằng, nếu thời tiết thuận lợi v&agrave; điều kiện cho ph&eacute;p xe di chuyển theo cung đường&nbsp;<strong>đ&egrave;o Mả Pia &ndash; đ&egrave;o 14 tầng</strong>&nbsp;nổi tiếng nhất c&aacute;c đ&egrave;o tại Việt Nam.</p>\r\n\r\n<p><em>(Nếu kịp giờ đo&agrave;n sẽ dừng ch&acirc;n tham quan:&nbsp;<strong>suối L&ecirc; Nin, Hang P&aacute;c B&oacute;</strong>&nbsp;&ndash; Nơi hoạt động c&aacute;ch mạng đầu ti&ecirc;n sau khi chủ tịch Hồ ch&iacute; Minh về nước).</em></p>\r\n\r\n<p>Đến Cao Bằng, nhận ph&ograve;ng kh&aacute;ch sạn.</p>\r\n\r\n<p>Ăn tối. Tự do kh&aacute;m ph&aacute; Cao Bằng về đ&ecirc;m. Nghỉ đ&ecirc;m tại&nbsp;<strong>Cao Bằng</strong>.</p>'),
(54, 183, 'CAO BẰNG – THÁC BẢN GIỐC – LẠNG SƠN', '<p>Ăn s&aacute;ng. Trả ph&ograve;ng. Khởi h&agrave;nh tham quan:</p>\r\n\r\n<p><strong>&bull;</strong>&nbsp;<strong>Th&aacute;c Bản Giốc</strong>&nbsp;&ndash; Th&aacute;c nước đẹp nhất Việt Nam v&agrave; l&agrave; th&aacute;c nước lớn nhất Đ&ocirc;ng Nam &Aacute;. Th&aacute;c c&oacute; độ cao 53m, chia l&agrave;m 3 tầng được v&iacute; von như dải lụa trắng giữa n&uacute;i rừng Đ&ocirc;ng Bắc. Theo d&ograve;ng chảy của s&ocirc;ng Qu&acirc;y Sơn, từ thượng nguồn, mạch nước tu&ocirc;n mạnh mẽ, tr&agrave;n qua c&aacute;c th&aacute;c phụ tạo n&ecirc;n một chiếc r&egrave;m nước tự nhi&ecirc;n tuyệt đẹp. Th&aacute;c Bản Giốc c&oacute; &yacute; nghĩa đặc biệt khi nằm tr&ecirc;n bi&ecirc;n giới giữa Việt Nam v&agrave; Trung Quốc.</p>\r\n\r\n<p><strong>&bull;</strong>&nbsp;Chụp h&igrave;nh lưu niệm với&nbsp;<strong>cột mốc bi&ecirc;n giới 836 (02)</strong>&nbsp;chủ quyền thi&ecirc;ng li&ecirc;ng của Tổ Quốc.</p>\r\n\r\n<p><strong>&bull;</strong>&nbsp;<strong>Động Ngườm Ngao</strong>&nbsp;&ndash; l&agrave; hang động c&oacute; vẻ đẹp kỳ th&uacute; ẩn m&igrave;nh trong ngọn n&uacute;i h&ugrave;ng vĩ ở x&atilde; Đ&agrave;m Thủy, huyện Tr&ugrave;ng Kh&aacute;nh (Cao Bằng) v&agrave; l&agrave; một trong những hang động đẹp nhất Cao Bằng. Được v&iacute; như kiệt t&aacute;c thi&ecirc;n nhi&ecirc;n hiếm c&oacute;, giữ nguy&ecirc;n vẻ đẹp hoang sơ v&agrave; độc đ&aacute;o.</p>\r\n\r\n<p>Ăn trưa. Khởi h&agrave;nh đi Lạng Sơn.</p>\r\n\r\n<p>Đến Lạng Sơn, nhận ph&ograve;ng nghỉ ngơi.</p>\r\n\r\n<p>Ăn tối. Tự do kh&aacute;m ph&aacute; Lạng Sơn về đ&ecirc;m. Nghỉ đ&ecirc;m tại&nbsp;<strong>Lạng Sơn.</strong></p>'),
(55, 183, 'LẠNG SƠN – HÀ NỘI – TP. HCM', '<p>Ăn s&aacute;ng. Trả ph&ograve;ng. Khởi h&agrave;nh tham quan:</p>\r\n\r\n<p><strong>&bull; Khu di t&iacute;ch danh thắng Nhị &ndash; Tam Thanh</strong>&nbsp;được xếp hạng di t&iacute;ch quốc gia năm 1962. L&agrave; một trong những động tự nhi&ecirc;n kỳ th&uacute; tại Lạng Sơn. Những n&eacute;t đẹp ri&ecirc;ng của động Nhị Thanh, động Tam Thanh, được mệnh danh l&agrave; &ldquo;<em>Đệ nhất b&aacute;t cảnh</em>&rdquo; của xứ Lạng.</p>\r\n\r\n<p><strong>&bull;&nbsp;</strong>Mua sắm tại&nbsp;<strong>Chợ Đ&ocirc;ng Kinh</strong>&nbsp;&ndash; ng&ocirc;i chợ sầm uất nhộn nhịp v&agrave; lớn nhất Lạng Sơn.</p>\r\n\r\n<p>Ăn trưa. Khởi h&agrave;nh về lại H&agrave; Nội.</p>\r\n\r\n<p>Di chuyển ra s&acirc;n bay Nội B&agrave;i đ&aacute;p chuyến bay về lại TP. HCM.&nbsp;<em>(C&aacute;c chuyến bay dự kiến sau 18:00)</em></p>\r\n\r\n<p>Kết th&uacute;c chương tr&igrave;nh tham quan.</p>\r\n\r\n<p><em><strong>C&aacute;c mốc thời gian c&oacute; gi&aacute; trị tham khảo, t&ugrave;y theo điều kiện thực tế m&agrave; lịch tr&igrave;nh c&oacute; thể thay đổi cho ph&ugrave; hợp.</strong></em></p>'),
(56, 184, 'ĐÀ NẴNG - PHỐ CỔ HỘI AN', '<p>Đến Đ&agrave; Nẵng, xe v&agrave; HDV đ&oacute;n kh&aacute;ch. Khởi h&agrave;nh tham quan:&nbsp;</p>\r\n\r\n<p><strong>&bull; Cầu T&igrave;nh Y&ecirc;u</strong>&nbsp;v&agrave; check-in với biểu tượng&nbsp;<strong>C&aacute; Ch&eacute;p H&oacute;a Rồng, cầu Rồng Đ&agrave; Nẵng&hellip;</strong></p>\r\n\r\n<p><strong>&bull; Bảo t&agrave;ng Phật Học</strong>&nbsp;&ndash; Bảo t&agrave;ng văn h&oacute;a Phật gi&aacute;o đầu ti&ecirc;n ở Việt Nam.</p>\r\n\r\n<p><strong>&bull; Ch&ugrave;a Quan Thế &Acirc;m</strong>&nbsp;&ndash; tọa lạc tại ch&acirc;n n&uacute;i Kim Sơn, một trong năm ngọn Ngũ H&agrave;nh Sơn.</p>\r\n\r\n<p><strong>&bull; L&agrave;ng nghề đi&ecirc;u khắc đ&aacute;</strong>&nbsp;v&agrave; mua sắm tại cửa h&agrave;ng đặc sản Miền Trung.</p>\r\n\r\n<p>Ăn trưa. Khởi h&agrave;nh tham quan&nbsp;<strong>Phố cổ Hội An</strong>&nbsp;&ndash; du kh&aacute;ch tản bộ kh&aacute;m ph&aacute; c&aacute;c c&ocirc;ng tr&igrave;nh kiến tr&uacute;c nổi tiếng:&nbsp;<em>Ch&ugrave;a Cầu Nhật Bản, c&aacute;c ng&ocirc;i nh&agrave; cổ h&agrave;ng trăm tuổi, Hội Qu&aacute;n Phước Kiến, Xưởng thủ c&ocirc;ng mỹ nghệ</em>. Phố cổ Hội An l&agrave; qu&aacute; khứ v&agrave;ng son của một thương cảng sầm uất thời phong kiến.</p>\r\n\r\n<p>Ăn tối với&nbsp;<strong><em>đặc sản Cao lầu Hội An</em></strong>&nbsp;nổi tiếng. Khởi h&agrave;nh về lại Đ&agrave; Nẵng.</p>\r\n\r\n<p>Tự do kh&aacute;m ph&aacute; Đ&agrave; Nẵng về đ&ecirc;m. Nghỉ đ&ecirc;m tại&nbsp;<strong>Đ&agrave; Nẵng</strong>.</p>'),
(57, 184, 'ĐÀ NẴNG – BÀ NÀ – HUẾ', '<p>Ăn s&aacute;ng buffet tại kh&aacute;ch sạn. Trả ph&ograve;ng. Khởi h&agrave;nh tham quan:</p>\r\n\r\n<p><strong>&bull; Khu du lịch B&agrave; N&agrave; Hills</strong>&nbsp;&ndash; chinh phục B&agrave; N&agrave; bằng hệ thống c&aacute;p treo một d&acirc;y d&agrave;i nhất thế giới 5.801m v&agrave; l&agrave; một trong 10 tuyến c&aacute;p treo ấn tượng nhất thế giới. B&agrave; N&agrave; l&agrave; nơi c&oacute; những khoảnh khoắc giao m&ugrave;a bất ngờ&nbsp;<em>Xu&acirc;n &ndash; Hạ &ndash; Thu &ndash; Đ&ocirc;ng</em>&nbsp;trong một ng&agrave;y.</p>\r\n\r\n<p><strong><em>(chi ph&iacute; c&aacute;p treo B&agrave; N&agrave; tự t&uacute;c)</em></strong></p>\r\n\r\n<p><strong>&bull; Ch&ugrave;a Linh Ứng</strong>&nbsp;với tượng Phật Th&iacute;ch Ca cao 27m, đền thờ&nbsp;<strong>B&agrave; Ch&uacute;a Mẫu Thượng Ng&agrave;n</strong>.</p>\r\n\r\n<p><strong>&bull; Cầu V&agrave;ng</strong>&nbsp;&ndash; c&acirc;y cầu độc đ&aacute;o nằm trong vườn Thi&ecirc;n Thai, được x&acirc;y dựng tr&ecirc;n độ cao 1.400m so với mặt nước biển, được n&acirc;ng đỡ bởi kiến tr&uacute;c h&igrave;nh đ&ocirc;i b&agrave;n tay khổng lồ độc đ&aacute;o.</p>\r\n\r\n<p><strong>&bull; C&ocirc;ng vi&ecirc;n Fantasy Park</strong>&nbsp;với hơn 100 tr&ograve; chơi phi&ecirc;u lưu hấp dẫn, mang đến cho qu&yacute; kh&aacute;ch nhiều cung bậc cảm x&uacute;c bất ngờ v&agrave; th&uacute; vị.</p>\r\n\r\n<p><strong>Ăn trưa buffet tại B&agrave;</strong>&nbsp;<strong>N&agrave;&nbsp;<em>(chi ph&iacute; tự t&uacute;c)</em></strong>.</p>\r\n\r\n<p><em>(Nếu qu&yacute; kh&aacute;ch kh&ocirc;ng tham quan B&agrave; N&agrave; th&igrave; tự t&uacute;c ăn trưa v&agrave; tự t&uacute;c chi ph&iacute; nhập đo&agrave;n tại điểm hẹn).</em></p>\r\n\r\n<p>Khởi h&agrave;nh đi&nbsp;<strong>Huế</strong>&nbsp;đi xuy&ecirc;n&nbsp;<strong>hầm đường bộ đ&egrave;o Hải V&acirc;n</strong>. Tr&ecirc;n đường di chuyển, tham quan:</p>\r\n\r\n<p><strong>&bull; L&agrave;ng ch&agrave;i Lăng C&ocirc;</strong>&nbsp;chụp h&igrave;nh lưu niệm.</p>\r\n\r\n<p><strong>&bull; Ch&ugrave;a Thi&ecirc;n Mụ</strong>&nbsp;cổ k&iacute;nh l&agrave; nơi lưu giữ nhiều cổ vật c&oacute; gi&aacute; trị về mặt lịch sử v&agrave; nghệ thuật.</p>\r\n\r\n<p>Nhận ph&ograve;ng kh&aacute;ch sạn.</p>\r\n\r\n<p>Ăn tối với đặc sản xứ Huế&nbsp;<em>(B&aacute;nh b&egrave;o, lọc, nậm, kho&aacute;i&hellip;).</em></p>\r\n\r\n<p>Xu&ocirc;́ng thuy&ecirc;̀n R&ocirc;̀ng thưởng thức&nbsp;<strong>Ca Hu&ecirc;́ tr&ecirc;n s&ocirc;ng Hương</strong>&nbsp;&ndash; nét văn hóa đ&ocirc;̣c đáo của xứ Hu&ecirc;́.</p>\r\n\r\n<p>Nghỉ đ&ecirc;m tại Huế.</p>'),
(58, 184, 'THÁNH ĐỊA LA VANG - ĐỘNG PHONG NHA', '<p>Ăn s&aacute;ng buffet tại kh&aacute;ch sạn. Rời Huế khởi h&agrave;nh đi&nbsp;<strong>Phong Nha &ndash; Kẻ B&agrave;ng</strong>. Tr&ecirc;n đường tham quan:</p>\r\n\r\n<p><strong>&bull; Th&aacute;nh Địa La Vang</strong>&nbsp;&ndash; Tiểu Vương Cung Th&aacute;nh Đường, nơi lưu dấu những vết t&iacute;ch của lịch sử v&agrave; l&agrave; trung t&acirc;m h&agrave;nh hương C&ocirc;ng Gi&aacute;o lớn nhất của nước ta.</p>\r\n\r\n<p><strong>&bull; Vĩ tuyến 17&nbsp;</strong>với chứng t&iacute;ch&nbsp;<strong>s&ocirc;ng Bến Hải &ndash; cầu Hiền Lương</strong>&nbsp;huy&ecirc;̀n thoại.</p>\r\n\r\n<p><strong>&bull;&nbsp;</strong>Di chuyển ngang qua&nbsp;<strong>c&aacute;nh đồng điện gi&oacute; Quảng B&igrave;nh&nbsp;</strong>l&agrave; dự &aacute;n điện gi&oacute; tr&ecirc;n bờ c&oacute; quy m&ocirc; lớn nhất Việt Nam t&iacute;nh đến thời điểm hiện nay.</p>\r\n\r\n<p>Ăn trưa tại&nbsp;<strong>Phong Nha</strong>. Ngồi thuyền ngược&nbsp;<strong>s&ocirc;ng Son</strong>&nbsp;chinh phục&nbsp;<strong>Động Phong Nha</strong>: C&ocirc; Ti&ecirc;n &amp; Cung Đ&igrave;nh dưới s&acirc;u l&ograve;ng n&uacute;i nơi c&oacute; con s&ocirc;ng ngầm từ L&agrave;o chảy sang, chi&ecirc;m ngưỡng c&aacute;c khối thạch nhũ tuyệt đẹp được kiến tạo bởi thi&ecirc;n nhi&ecirc;n qua h&agrave;ng ngh&igrave;n năm.</p>\r\n\r\n<p>Khởi h&agrave;nh về Huế theo đường Trường Sơn &ndash; Hồ Ch&iacute; Minh.</p>\r\n\r\n<p>Ăn tối. Tự do kh&aacute;m ph&aacute; cố đ&ocirc; Huế về đ&ecirc;m với&nbsp;<strong>cầu Tr&agrave;ng Tiền</strong>&nbsp;rực rỡ soi m&igrave;nh xuống d&ograve;ng s&ocirc;ng Hương thơ mộng. Nghỉ đ&ecirc;m tại&nbsp;<strong>Huế</strong>.</p>'),
(59, 184, 'HUẾ – ĐÀ NẴNG – TP. HCM', '<p>Ăn s&aacute;ng buffet tại kh&aacute;ch sạn. Trả ph&ograve;ng. Khởi h&agrave;nh tham quan:</p>\r\n\r\n<p><strong>&bull; Đại Nội &ndash; Ho&agrave;ng cung triều Nguyễn</strong>, được x&acirc;y dựng từ đầu thế kỷ XIX đến nửa đầu thế kỷ XX, l&agrave; một trong số c&aacute;c di t&iacute;ch thuộc cụm Quần thể di t&iacute;ch Cố đ&ocirc; Huế được c&ocirc;ng nhận l&agrave; Di sản Văn ho&aacute; Thế giới UNESCO từ năm 1993. To&agrave;n cảnh Đại Nội Huế c&ograve;n lưu giữ nhiều dấu ấn đặc sắc của triều đ&igrave;nh phong kiến nh&agrave; Nguyễn h&agrave;ng trăm năm.</p>\r\n\r\n<p><strong>&bull; L&agrave;ng hương Thủy Xu&acirc;n</strong>&nbsp;&ndash; L&agrave;ng nghề l&agrave;m hương truyền thống hơn 700 năm của xứ Huế. T&igrave;m hiểu về quy tr&igrave;nh v&agrave; c&aacute;c c&ocirc;ng đoạn l&agrave;m hương, tự do chụp ảnh lưu niệm.</p>\r\n\r\n<p>Ăn trưa. Di chuyển ra s&acirc;n bay đ&aacute;p chuyến bay về lại TP. HCM.&nbsp;<strong>S&acirc;n bay Huế</strong>&nbsp;<em>(c&aacute;c chuyến bay dự kiến sau 14:00).</em>&nbsp;<strong>S&acirc;n bay Đ&agrave; Nẵng</strong>&nbsp;<em>(c&aacute;c chuyến bay dự kiến sau 17:00)</em>.&nbsp;</p>\r\n\r\n<p>Chia tay đo&agrave;n v&agrave; hẹn gặp lại. Kết th&uacute;c chương tr&igrave;nh tham quan.</p>\r\n\r\n<p><strong><em>C&aacute;c mốc thời gian c&oacute; gi&aacute; trị tham khảo, t&ugrave;y theo điều kiện thực tế m&agrave; lịch tr&igrave;nh c&oacute; thể thay đổi cho ph&ugrave; hợp.</em></strong></p>'),
(60, 185, 'TP. HCM – HÀ NỘI', '<p><strong>TOUR KHỞI H&Agrave;NH MỖI THỨ 7 TRONG TUẦN</strong></p>\r\n\r\n<p>Xe v&agrave; HDV đ&oacute;n kh&aacute;ch tại s&acirc;n bay Nội B&agrave;i, đo&agrave;n di chuyển về trung t&acirc;m H&agrave; Nội.</p>\r\n\r\n<p>Ăn trưa. Nhận ph&ograve;ng kh&aacute;ch sạn. Khởi h&agrave;nh tham quan:</p>\r\n\r\n<p><strong>Hồ T&acirc;y</strong>&nbsp;&ndash; Hồ lớn nhất H&agrave; Nội,&nbsp;<strong>ch&ugrave;a Trấn Quốc</strong>,&nbsp;<strong>Hồ Gươm</strong>,&nbsp;<strong>đền Ngọc Sơn</strong>,&nbsp;<strong>cầu Th&ecirc; H&uacute;c</strong>, chụp h&igrave;nh lưu niệm tại&nbsp;<strong>Nh&agrave; Thờ Lớn</strong>,&nbsp;<strong>Nh&agrave; H&aacute;t Lớn</strong>&nbsp;th&agrave;nh phố.</p>\r\n\r\n<p>Thưởng thức&nbsp;<strong>b&aacute;nh cốm</strong>&nbsp;<strong>H&agrave;ng Than, kem Tr&agrave;ng Tiền</strong>&nbsp;&ndash; N&eacute;t văn h&oacute;a ẩm thực đặc trưng rất ri&ecirc;ng của H&agrave; Nội.</p>\r\n\r\n<p>Ăn tối. Tự do kh&aacute;m ph&aacute; H&agrave; Nội về đ&ecirc;m. Nghỉ đ&ecirc;m tại H&agrave; Nội.</p>'),
(61, 185, 'HÀ NỘI – LÀO CAI – SAPA', '<p>Ăn s&aacute;ng. Trả ph&ograve;ng. Tham quan&nbsp;<strong>Lăng B&aacute;c, thăm Phủ Chủ Tịch, nh&agrave; s&agrave;n, ao c&aacute; B&aacute;c Hồ, Ch&ugrave;a Một Cột, Văn Miếu &ndash; Quốc Tử Gi&aacute;m</strong>&nbsp;&ndash; trường đại học đầu ti&ecirc;n của Việt Nam.</p>\r\n\r\n<p>Ăn trưa. Khởi h&agrave;nh đi&nbsp;<strong>SaPa</strong>&nbsp;&ndash; nơi m&agrave; người Ph&aacute;p xưa gọi l&agrave;&nbsp;<em>&ldquo;Kinh đ&ocirc; m&ugrave;a h&egrave; của xứ Bắc Kỳ&rdquo;</em>&nbsp;theo tuyến đường cao tốc d&agrave;i nhất Việt Nam 250km. Đến L&agrave;o Cai, dừng ch&acirc;n chụp ảnh lưu niệm tại&nbsp;<strong>cột mốc bi&ecirc;n giới 102</strong>&nbsp;&ndash; cửa khẩu Quốc tế L&agrave;o Cai. Đến SaPa, nhận ph&ograve;ng kh&aacute;ch sạn.</p>\r\n\r\n<p>Ăn tối. Thư gi&atilde;n với liệu tr&igrave;nh massage ch&acirc;n, mỗi kh&aacute;ch được&nbsp;<strong>tặng 01 v&eacute; foot massage.</strong></p>\r\n\r\n<p>Tự do kh&aacute;m ph&aacute; phố n&uacute;i về đ&ecirc;m, tự do thưởng thức:&nbsp;<em>Thắng Cố, cơm lam, lợn cắp n&aacute;ch, khoai nướng, bắp nướng&hellip;</em>&nbsp;Nghỉ đ&ecirc;m tại thị trấn SaPa.</p>'),
(62, 185, 'SAPA – BẢN CÁT CÁT – FANSIPAN', '<p>Ăn s&aacute;ng. Tham quan&nbsp;<strong>Bản C&aacute;t C&aacute;t</strong>&nbsp;&ndash; địa b&agrave;n cư tr&uacute; của người H&rsquo;M&ocirc;ng, ngắm cảnh h&ugrave;ng vĩ của n&uacute;i rừng T&acirc;y Bắc, tham quan th&aacute;c thuỷ điện C&aacute;t C&aacute;t do người Ph&aacute;p x&acirc;y dựng.</p>\r\n\r\n<p>Ăn trưa. Di chuyển đến ga c&aacute;p treo Fansipan bắt đầu h&agrave;nh tr&igrave;nh&nbsp;<strong>chinh phục Fansipan</strong>&nbsp;bằng&nbsp;hệ thống c&aacute;p treo 3 d&acirc;y hiện đại nhất thế giới&nbsp;với cabin c&oacute; sức chứa tới 35 kh&aacute;ch. Vượt qua 639 bậc thang chinh phục đỉnh Fansipan&nbsp;tr&ecirc;n độ cao 3.143m &ndash; n&oacute;c nh&agrave; của Đ&ocirc;ng Dương.&nbsp;<strong><em>(chi ph&iacute; c&aacute;p treo tự t&uacute;c)</em></strong></p>\r\n\r\n<p>Ăn tối. Tự do kh&aacute;m ph&aacute; SaPa về đ&ecirc;m. Nghỉ đ&ecirc;m tại thị trấn SaPa.</p>'),
(63, 185, 'SAPA – HÀ NỘI – TP. HCM', '<p>Ăn s&aacute;ng. Trả ph&ograve;ng. Khởi h&agrave;nh về lại H&agrave; Nội.</p>\r\n\r\n<p>Ăn trưa. Di chuyển ra s&acirc;n bay Nội B&agrave;i đ&aacute;p chuyến bay H&agrave; Nội &ndash; TP. HCM.</p>\r\n\r\n<p><em>(C&aacute;c chuyến bay dự kiến từ 16:30 &ndash; 17:30).&nbsp;</em>Kết th&uacute;c chương tr&igrave;nh tham quan!</p>\r\n\r\n<p><strong><em>C&aacute;c mốc thời gian c&oacute; gi&aacute; trị tham khảo, t&ugrave;y theo điều kiện thực tế m&agrave; lịch tr&igrave;nh c&oacute; thể thay đổi cho ph&ugrave; hợp.</em></strong></p>'),
(64, 187, 'TP.HCM – TUY HÒA – MŨI ĐIỆN (Ăn trưa, chiều)', '<p><strong>S&aacute;ng:&nbsp;</strong>Qu&yacute; kh&aacute;ch c&oacute; mặt tại ga quốc nội, s&acirc;n bay T&acirc;n Sơn Nhất trước giờ bay &iacute;t nhất hai tiếng<strong>.</strong>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Đại diện c&ocirc;ng ty&nbsp;<strong>Du Lịch Việt</strong>&nbsp;đ&oacute;n v&agrave; hỗ trợ Qu&yacute; Kh&aacute;ch l&agrave;m thủ tục đ&oacute;n chuyến bay đi&nbsp;<strong>Tuy H&ograve;a.&nbsp;</strong></li>\r\n	<li>Đến s&acirc;n bay&nbsp;<strong>Tuy H&ograve;a</strong>, Hướng dẫn vi&ecirc;n đ&oacute;n đo&agrave;n khởi h&agrave;nh đi&nbsp;<strong>Mũi Điện</strong>&nbsp; điểm cực đ&ocirc;ng &ndash; nơi đ&oacute;n b&igrave;nh minh đầu ti&ecirc;n tr&ecirc;n đất liền của Tổ Quốc.</li>\r\n	<li><strong>Mũi Điện</strong>&nbsp;c&oacute; n&uacute;i, c&oacute; biển, từ xa l&agrave; ngọn đ&egrave;n hải đăng đứng sừng sững quay mặt ra biển, b&ecirc;n dưới ch&acirc;n n&uacute;i l&agrave; B&atilde;i M&ocirc;n được nhiều người y&ecirc;u th&iacute;ch bởi sự y&ecirc;n tĩnh, thanh vắng m&agrave; b&atilde;i biển n&agrave;y mang lại. Tr&ecirc;n đường khời h&agrave;nh ra mũi điện qu&yacute; kh&aacute;ch co dip chi&ecirc;m ngưỡng cung đường ven biển Phước T&acirc;n &ndash; B&atilde;i Ng&agrave; tuyệt đẹp.&nbsp;</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Mũi Điện\" src=\"https://dulichviet.com.vn/images/bandidau/NOI-DIA/Quy-Nhon/du-lich-quy-nhon-kham-pha-mui-dien-du-lich-viet.jpg\" /><br />\r\n<em>Mũi Điện c&oacute; n&uacute;i, c&oacute; biển, từ xa l&agrave; ngọn đ&egrave;n hải đăng đứng sừng sững quay mặt ra biển</em><br />\r\n&nbsp;</p>\r\n\r\n<p><strong>Trưa:</strong>&nbsp;đo&agrave;n d&ugrave;ng cơm trưa tại v&egrave; nổi tr&ecirc;n vịnh Vũng R&ocirc;.</p>\r\n\r\n<ul>\r\n	<li>Sau bữa trưa qu&yacute; kh&aacute;ch c&oacute; thể đi cano ra H&ograve;n Nưa một h&ograve;n đảo hoang sơ nhưng thật quyến rũ tữ do tắm biển v&agrave; ngắm san h&ocirc;<strong>&nbsp;</strong><em>(chi ph&iacute; tự t&uacute;c).</em></li>\r\n</ul>\r\n\r\n<p><strong>Chiều:</strong>&nbsp;D&ugrave;ng cơm chiều. Nghỉ đ&ecirc;m tại&nbsp;<strong>Tuy Ho&agrave;.</strong></p>\r\n\r\n<ul>\r\n	<li>Qu&yacute; kh&aacute;ch tự do mua sắm c&aacute;c đặc sản&nbsp;<strong>Ph&uacute; Y&ecirc;n</strong>&nbsp;như: kh&ocirc; c&aacute; ngừ đại dương, b&ograve; một nắng hai sương, muối kiến v&agrave;ng l&aacute; then leng&hellip;</li>\r\n</ul>'),
(65, 187, 'TUY HÒA - QUY NHƠN (Ăn sáng, trưa, chiều)', '<p><strong>S&aacute;ng:</strong>&nbsp;&nbsp; D&ugrave;ng bữa s&aacute;ng.</p>\r\n\r\n<ul>\r\n	<li>Tham quan&nbsp;<strong>cầu Thị Nại &ndash;&nbsp;</strong>c&acirc;y cầu vượt biển d&agrave;i nhất Việt Nam nối liền th&agrave;nh phố Quy Nhơn với khu kinh tế Nhơn Hội, đo&agrave;n c&oacute; dịp nh&igrave;n ngắm&nbsp;<strong>đồi c&aacute;t&nbsp; Nhơn L&yacute;&nbsp;</strong>&ndash; được cho l&agrave; đồi c&aacute;t lớn nhất Việt Nam, mệnh danh l&agrave; sa mạc giữa long biển xanh.</li>\r\n	<li>Đo&agrave;n khởi h&agrave;nh tới&nbsp;<strong>Kỳ Co</strong>, nơi được thi&ecirc;n nhi&ecirc;n ưu &aacute;i cho dải c&aacute;t v&agrave;ng &ocirc;m lấy biển, bọc th&agrave;nh một h&igrave;nh cung, mềm mại như dải lụa uốn lượn, &ocirc;m trọn lấy l&agrave;n nước trong xanh tận đ&aacute;y. Bạn c&oacute; thể thả m&igrave;nh v&agrave;o l&agrave;n nước m&aacute;t, vui đ&ugrave;a với từng con s&oacute;ng hoặc ng&acirc;m m&igrave;nh trong những hồ nước tự nhi&ecirc;n ngay s&aacute;t b&atilde;i biển do nước biển đ&aacute;nh v&agrave;o những v&aacute;ch đ&aacute; l&otilde;m tạo th&agrave;nh. Du kh&aacute;ch sẽ ho&agrave;n to&agrave;n bị cho&aacute;ng ngợp bởi vẻ đẹp hoang sơ, trong trẻo, kỳ vĩ m&agrave; tạo h&oacute;a ban tặng cho nơi đ&acirc;y.&nbsp;</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Kỳ Co\" src=\"https://dulichviet.com.vn/images/bandidau/NOI-DIA/Quy-Nhon/ky-co-du-lich-viet.jpg\" /><br />\r\n<em>Kỳ Co, nơi được thi&ecirc;n nhi&ecirc;n ưu &aacute;i cho dải c&aacute;t v&agrave;ng &ocirc;m lấy biển</em><br />\r\n&nbsp;</p>\r\n\r\n<p><strong>Trưa:</strong>&nbsp;D&ugrave;ng cơm trưa với&nbsp;<strong>những m&oacute;n hải sản đặc trưng.</strong>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Đo&agrave;n tiếp tục tham quan&nbsp;<strong>Eo gi&oacute;</strong>&nbsp;-&nbsp;<strong><em>điểm check-in tuyệt đẹp tại Quy Nhơn,</em></strong>&nbsp;được mệnh danh l&agrave; nơi ngắm ho&agrave;ng h&ocirc;n đẹp nhất Việt Nam, k&igrave; vĩ bởi những rặng n&uacute;i cao v&agrave; c&oacute; h&igrave;nh th&ugrave; k&igrave; lạ vươn ra biển lớn, &ocirc;m trọn một v&ograve;ng tạo th&agrave;nh eo biển h&uacute;t gi&oacute;. Đứng từ tr&ecirc;n cao nh&igrave;n xuống Eo gi&oacute; như một bức tranh sơn thủy hữu t&igrave;nh vừa gởi cảm vừa hoang sơ đầy quyến rũ.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Eo gió\" src=\"https://dulichviet.com.vn/images/bandidau/NOI-DIA/Quy-Nhon/du-lich-quy-nhon-kham-pha-eo-gio-du-lich-viet.jpg\" /><br />\r\n<em>Tham quan Eo gi&oacute; - điểm check-in tuyệt đẹp tại Quy Nhơn</em><br />\r\n&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Tham quan&nbsp;<strong>Tịnh x&aacute; Ngọc H&ograve;a</strong>&nbsp;&ndash; chi&ecirc;m ngưỡng tượng Phật đ&ocirc;i cao nhất Việt Nam, cao 30 m&eacute;t, xoay về 2 hướng Nam Bắc với 2 m&agrave;u v&agrave;ng v&agrave; bạc.&nbsp;</li>\r\n</ul>\r\n\r\n<p><strong>Chiều:</strong>&nbsp;D&ugrave;ng cơm chiều. Nghỉ đ&ecirc;m tại Quy Nhơn</p>\r\n\r\n<ul>\r\n	<li>Qu&yacute; kh&aacute;ch tự do nghỉ ngơi tại kh&aacute;ch sạn hoặc dạo một v&ograve;ng quanh th&agrave;nh phố Quy Nhơn thưởng thức c&aacute;c m&oacute;n ăn đặc sản: m&oacute;n nem nướng, b&aacute;nh tr&aacute;ng trứng, b&aacute;nh canh, b&aacute;nh x&egrave;o t&ocirc;m nhảy, hải sản,&hellip;</li>\r\n</ul>');
INSERT INTO `tbl_timeline` (`timeLineId`, `tourId`, `title`, `description`) VALUES
(66, 187, 'QUY NHƠN - KỲ CO – EO GIÓ (Ăn sáng, trưa, chiều)', '<p><strong>S&aacute;ng:</strong>&nbsp;&nbsp; D&ugrave;ng bữa s&aacute;ng.</p>\r\n\r\n<ul>\r\n	<li>Tham quan&nbsp;<strong>cầu Thị Nại &ndash;&nbsp;</strong>c&acirc;y cầu vượt biển d&agrave;i nhất Việt Nam nối liền th&agrave;nh phố Quy Nhơn với khu kinh tế Nhơn Hội, đo&agrave;n c&oacute; dịp nh&igrave;n ngắm&nbsp;<strong>đồi c&aacute;t&nbsp; Nhơn L&yacute;&nbsp;</strong>&ndash; được cho l&agrave; đồi c&aacute;t lớn nhất Việt Nam, mệnh danh l&agrave; sa mạc giữa long biển xanh.</li>\r\n	<li>Đo&agrave;n khởi h&agrave;nh tới&nbsp;<strong>Kỳ Co</strong>, nơi được thi&ecirc;n nhi&ecirc;n ưu &aacute;i cho dải c&aacute;t v&agrave;ng &ocirc;m lấy biển, bọc th&agrave;nh một h&igrave;nh cung, mềm mại như dải lụa uốn lượn, &ocirc;m trọn lấy l&agrave;n nước trong xanh tận đ&aacute;y. Bạn c&oacute; thể thả m&igrave;nh v&agrave;o l&agrave;n nước m&aacute;t, vui đ&ugrave;a với từng con s&oacute;ng hoặc ng&acirc;m m&igrave;nh trong những hồ nước tự nhi&ecirc;n ngay s&aacute;t b&atilde;i biển do nước biển đ&aacute;nh v&agrave;o những v&aacute;ch đ&aacute; l&otilde;m tạo th&agrave;nh. Du kh&aacute;ch sẽ ho&agrave;n to&agrave;n bị cho&aacute;ng ngợp bởi vẻ đẹp hoang sơ, trong trẻo, kỳ vĩ m&agrave; tạo h&oacute;a ban tặng cho nơi đ&acirc;y.&nbsp;</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Kỳ Co\" src=\"https://dulichviet.com.vn/images/bandidau/NOI-DIA/Quy-Nhon/ky-co-du-lich-viet.jpg\" /><br />\r\n<em>Kỳ Co, nơi được thi&ecirc;n nhi&ecirc;n ưu &aacute;i cho dải c&aacute;t v&agrave;ng &ocirc;m lấy biển</em><br />\r\n&nbsp;</p>\r\n\r\n<p><strong>Trưa:</strong>&nbsp;D&ugrave;ng cơm trưa với&nbsp;<strong>những m&oacute;n hải sản đặc trưng.</strong>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Đo&agrave;n tiếp tục tham quan&nbsp;<strong>Eo gi&oacute;</strong>&nbsp;-&nbsp;<strong><em>điểm check-in tuyệt đẹp tại Quy Nhơn,</em></strong>&nbsp;được mệnh danh l&agrave; nơi ngắm ho&agrave;ng h&ocirc;n đẹp nhất Việt Nam, k&igrave; vĩ bởi những rặng n&uacute;i cao v&agrave; c&oacute; h&igrave;nh th&ugrave; k&igrave; lạ vươn ra biển lớn, &ocirc;m trọn một v&ograve;ng tạo th&agrave;nh eo biển h&uacute;t gi&oacute;. Đứng từ tr&ecirc;n cao nh&igrave;n xuống Eo gi&oacute; như một bức tranh sơn thủy hữu t&igrave;nh vừa gởi cảm vừa hoang sơ đầy quyến rũ.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Eo gió\" src=\"https://dulichviet.com.vn/images/bandidau/NOI-DIA/Quy-Nhon/du-lich-quy-nhon-kham-pha-eo-gio-du-lich-viet.jpg\" /><br />\r\n<em>Tham quan Eo gi&oacute; - điểm check-in tuyệt đẹp tại Quy Nhơn</em><br />\r\n&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Tham quan&nbsp;<strong>Tịnh x&aacute; Ngọc H&ograve;a</strong>&nbsp;&ndash; chi&ecirc;m ngưỡng tượng Phật đ&ocirc;i cao nhất Việt Nam, cao 30 m&eacute;t, xoay về 2 hướng Nam Bắc với 2 m&agrave;u v&agrave;ng v&agrave; bạc.&nbsp;</li>\r\n</ul>\r\n\r\n<p><strong>Chiều:</strong>&nbsp;D&ugrave;ng cơm chiều. Nghỉ đ&ecirc;m tại Quy Nhơn</p>\r\n\r\n<ul>\r\n	<li>Qu&yacute; kh&aacute;ch tự do nghỉ ngơi tại kh&aacute;ch sạn hoặc dạo một v&ograve;ng quanh th&agrave;nh phố Quy Nhơn thưởng thức c&aacute;c m&oacute;n ăn đặc sản: m&oacute;n nem nướng, b&aacute;nh tr&aacute;ng trứng, b&aacute;nh canh, b&aacute;nh x&egrave;o t&ocirc;m nhảy, hải sản,&hellip;</li>\r\n</ul>'),
(67, 187, 'QUY NHƠN – TP.HCM (Ăn sáng, trưa)', '<p><strong>S&aacute;ng:</strong>&nbsp;D&ugrave;ng bữa s&aacute;ng.&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Xe đưa qu&yacute; kh&aacute;ch đến&nbsp;<strong>KDL Ghềnh R&aacute;ng &ndash; Ti&ecirc;n Sa</strong>&nbsp;&ndash; một thi&ecirc;n đường của vẻ đẹp tự nhi&ecirc;n v&agrave; nền văn h&oacute;a đậm đ&agrave; tại miền biển xinh đẹp Quy Nhơn. Tại đ&acirc;y, qu&yacute; kh&aacute;ch c&oacute; dịp viếng thăm mộ của thi sĩ&nbsp;<strong>H&agrave;n Mặc Tử</strong>&nbsp;&ndash; một t&agrave;i hoa văn chương bạc mệnh, để hiểu th&ecirc;m về cuộc đời v&agrave; t&aacute;c phẩm nghệ thuật của &ocirc;ng. Tiếp theo, qu&yacute; kh&aacute;ch sẽ được ngắm nh&igrave;n<em>&nbsp;nghệ thuật b&uacute;t lửa của Dzũ Kha</em>, một di sản văn h&oacute;a độc đ&aacute;o của người d&acirc;n địa phương. Kh&aacute;m ph&aacute;&nbsp;<strong>B&atilde;i Trứng</strong>, hay c&ograve;n được biết đến với c&aacute;i t&ecirc;n&nbsp;<strong>b&atilde;i tắm Ho&agrave;ng Hậu</strong>, nơi những vi&ecirc;n đ&aacute; nhẵn như trứng được xếp đầy b&atilde;i c&aacute;t trắng mịn, tạo n&ecirc;n một khung cảnh tuyệt đẹp kh&ocirc;ng giống bất kỳ nơi n&agrave;o kh&aacute;c.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"KDL Ghềnh Ráng – Tiên Sa\" src=\"https://dulichviet.com.vn/images/bandidau/NOI-DIA/Quy-Nhon/ghenh-rang-tien-sa-du-lich-viet.jpg\" /><br />\r\n<em>KDL Ghềnh R&aacute;ng &ndash; Ti&ecirc;n Sa</em></p>\r\n\r\n<p><strong>Trưa:</strong>&nbsp;&nbsp;D&ugrave;ng cơm trưa.</p>\r\n\r\n<ul>\r\n	<li>Qu&yacute; kh&aacute;ch tự do mua sắm đặc sản địa phương về l&agrave;m qu&agrave; cho gia đ&igrave;nh</li>\r\n	<li>Hướng dẫn vi&ecirc;n tiễn đo&agrave;n ra s&acirc;n bay&nbsp;<strong>Ph&ugrave; C&aacute;t&nbsp;</strong>đ&oacute;n chuyến bay về th&agrave;nh phố&nbsp;<strong>Hồ Ch&iacute; Minh</strong></li>\r\n	<li>Kết th&uacute;c chương tr&igrave;nh tham quan, chia tay v&agrave; hẹn gặp lại.</li>\r\n	<li><em>Giờ bay c&oacute; thể thay đổi theo quy định của h&agrave;ng kh&ocirc;ng. Du Lịch Việt sẽ thay đổi chương tr&igrave;nh cho ph&ugrave; hợp với giờ bay mới nhưng sẽ kh&ocirc;ng chịu tr&aacute;ch nhiệm về c&aacute;c khoản chi ph&iacute; ph&aacute;t sinh.</em></li>\r\n</ul>'),
(68, 188, 'TP.HCM - CÔN ĐẢO OAI HÙNG (Ăn trưa, chiều)', '<p>Qu&yacute; kh&aacute;ch tập trung tại s&acirc;n bay T&acirc;n Sơn Nhất (ga trong nước), Hướng dẫn vi&ecirc;n l&agrave;m thủ tục cho đo&agrave;n đ&aacute;p chuyến bay đến C&ocirc;n Đảo.&nbsp;<br />\r\n<br />\r\nĐến nơi, xe v&agrave; Hướng dẫn vi&ecirc;n đưa đo&agrave;n<strong>&nbsp;theo con đường quanh co trải d&agrave;i h&agrave;ng c&acirc;y xanh</strong>&nbsp;nối liền từ s&acirc;n bay về trung t&acirc;m thị trấn, chi&ecirc;m ngưỡng cảnh quan thi&ecirc;n nhi&ecirc;n của n&uacute;i cao h&ugrave;ng vĩ, xa xa l&agrave; những h&ograve;n đảo ẩn hiện trong sắc xanh của biển cả &hellip; &nbsp;</p>\r\n\r\n<p>Qu&yacute; kh&aacute;ch tham quan c&aacute;c<strong>&nbsp;Di t&iacute;ch Văn h&oacute;a - Lịch sử h&agrave;o h&ugrave;ng:</strong><br />\r\n<strong>- Bảo t&agrave;ng C&ocirc;n Đảo:&nbsp;</strong>nơi trưng b&agrave;y những kỷ vật về năm th&aacute;ng lịch sử h&agrave;o h&ugrave;ng của d&acirc;n tộc Việt Nam.<br />\r\n<strong>- Dinh Ch&uacute;a Đảo:</strong>&nbsp;nơi sống v&agrave; l&agrave;m việc c&aacute;c đời ch&uacute;a đảo từ năm 1862 - 1975.<br />\r\n<strong>- Trại Ph&uacute; Hải:</strong>&nbsp;trại giam lớn v&agrave; cổ nhất của hệ thống nh&agrave; t&ugrave; C&ocirc;n Đảo.<br />\r\n<strong>- Chuồng cọp kiểu Ph&aacute;p (trại Ph&uacute; Tường):&nbsp;</strong>kh&aacute;m ph&aacute; hệ thống chuồng Cọp ki&ecirc;n cố được x&acirc;y dựng ẩn giữa c&aacute;c t&ograve;a nh&agrave; như m&ecirc; cung.&nbsp;<br />\r\n<strong>- Chuồng cọp kiểu Mỹ (trại Ph&uacute; B&igrave;nh):</strong>&nbsp;với c&aacute;c d&atilde;y ph&ograve;ng giam nhỏ hẹp v&agrave; ẩm thấp được x&acirc;y dựng v&agrave;o năm 1971. Nơi đ&acirc;y chủ yếu tra tấn t&ugrave; nh&acirc;n về tinh thần v&agrave; cũng l&agrave; nơi nhận được tin S&agrave;i G&ograve;n giải ph&oacute;ng đầu ti&ecirc;n.&nbsp;</p>\r\n\r\n<p>Buổi chiều, Qu&yacute; kh&aacute;ch tham quan:<br />\r\n<strong>- An Sơn Miếu: n</strong>ghe kể về giai thoại thứ phi Phi Yến với c&acirc;u h&aacute;t ru nổi tiếng &ldquo;Gi&oacute; đưa c&acirc;y cải về trời, rau răm ở lại chịu đời đắng cay&rdquo;.&nbsp;<br />\r\n-&nbsp;Ngắm nh&igrave;n vẻ đẹp của&nbsp;<strong>hồ An Hải&nbsp;</strong>tham quan<strong>&nbsp;V&acirc;n Sơn Tự&nbsp;</strong>- Ng&ocirc;i ch&ugrave;a Phật gi&aacute;o duy nhất ở C&ocirc;n Đảo như rực rỡ trong bức tranh thủy mặc của m&aacute;i ng&oacute;i đỏ tươi giữa nền xanh của rừng c&acirc;y cổ thụ</p>\r\n\r\n<p><br />\r\nBuổi tối, Qu&yacute; kh&aacute;ch&nbsp;<strong>viếng mộ c&ocirc; S&aacute;u nổi tiếng linh thi&ecirc;ng về đ&ecirc;m.</strong><br />\r\n<br />\r\nQu&yacute; kh&aacute;ch tự do tản bộ kh&aacute;m ph&aacute; C&ocirc;n Đảo y&ecirc;n b&igrave;nh về đ&ecirc;m;&nbsp;<strong>thưởng thức c&agrave; ph&ecirc; C&ocirc;n Sơn ngắm cầu t&agrave;u 914 hoặc kem dừa đất C&ocirc;n Đảo thơm ngon, cuốn kh&aacute;ch.</strong><br />\r\n<br />\r\n<strong>Nghỉ đ&ecirc;m tại C&ocirc;n Đảo.</strong></p>'),
(69, 188, 'KHÁM PHÁ CÔN ĐẢO “THIÊN ĐƯỜNG CỦA BIỂN” (Ăn sáng, trưa, chiều)', '<p>Sau khi d&ugrave;ng bữa s&aacute;ng tại kh&aacute;ch sạn. Xe đưa Qu&yacute; kh&aacute;ch tham quan:<br />\r\n<br />\r\n<strong>- Miếu Ho&agrave;ng Tử Cải:</strong>&nbsp;ng&ocirc;i miếu con trai của Vua Gia Long v&agrave; Thứ Phi Ho&agrave;ng Phi Yến, gắn liền với cuộc b&ocirc;n tẩu của Vua Gia Long trước sự truy đuổi của qu&acirc;n T&acirc;y Sơn.<br />\r\n<br />\r\n- Thoả th&iacute;ch tắm biển tại&nbsp;<strong>b&atilde;i biển Đầm Trầu:</strong>&nbsp;với b&atilde;i c&aacute;t v&agrave;ng mịn được bao quanh bởi những khu rừng nhiệt đới, đỉnh đ&egrave;o h&igrave;nh chữ C, một b&ecirc;n l&agrave; v&aacute;ch n&uacute;i v&agrave; ở giữa l&agrave; những h&agrave;ng c&acirc;y trải d&agrave;i soi b&oacute;ng;&nbsp;<strong>trải nghiệm săn khoảnh khắc m&aacute;y bay hạ c&aacute;nh</strong></p>\r\n\r\n<p>Buổi chiều, xe đưa Qu&yacute; kh&aacute;ch kh&aacute;m ph&aacute; một trong những<strong>&nbsp;cung đường biển đẹp nhất C&ocirc;n Đảo</strong>&nbsp;chi&ecirc;m ngưỡng&nbsp;<strong>Mũi C&aacute; Mập - Đỉnh T&igrave;nh Y&ecirc;u</strong>; tham quan<strong>&nbsp;B&atilde;i Nh&aacute;t&nbsp;</strong>- Khi nước xuống sẽ lộ thi&ecirc;n một b&atilde;i tắm với c&aacute;t trắng mịn;&nbsp;<strong>ghi lại to&agrave;n cảnh n&uacute;i non n&ecirc;n thơ đang dần chuyển m&igrave;nh khi ho&agrave;ng h&ocirc;n dần bu&ocirc;ng xuống đại dương bao la.</strong><br />\r\n<br />\r\n<strong>Nghỉ đ&ecirc;m tại C&ocirc;n Đảo&nbsp;</strong></p>'),
(70, 188, 'CÔN ĐẢO - TP.HCM (Ăn sáng)', '<p>Qu&yacute; kh&aacute;ch d&ugrave;ng bữa s&aacute;ng tại kh&aacute;ch sạn, tự do nghỉ ngơi thư gi&atilde;n v&agrave; tắm biển.<br />\r\n<br />\r\nQu&yacute; kh&aacute;ch khởi h&agrave;nh ra ra s&acirc;n bay C&ocirc;n Đảo trở về TP. Hồ Ch&iacute; Minh, tr&ecirc;n đường dừng ch&acirc;n<strong>&nbsp;mua sắm đặc sản nổi tiếng C&ocirc;n Đảo</strong>&nbsp;như hạt b&agrave;ng, c&aacute;c loại kh&ocirc; v&agrave; hải sản tươi ngon tại cửa h&agrave;ng Hương Biển.<br />\r\n<br />\r\nChia tay Qu&yacute; kh&aacute;ch v&agrave; kết th&uacute;c chuyến du lịch tại s&acirc;n bay T&acirc;n Sơn Nhất.</p>\r\n\r\n<p><strong>*Qu&yacute; kh&aacute;ch lưu &yacute;:&nbsp;</strong><br />\r\n- Giờ bay phụ thuộc v&agrave;o h&atilde;ng h&agrave;ng kh&ocirc;ng, số bữa ăn phụ thuộc v&agrave;o giờ bay. C&aacute;c bữa ăn c&oacute; thể ho&aacute;n đổi để ph&ugrave; hợp với chương tr&igrave;nh.<br />\r\n- H&agrave;nh tr&igrave;nh c&oacute; thể thay đổi thứ tự điểm đến t&ugrave;y v&agrave;o điều kiện thực tế<br />\r\n- Đ&acirc;y l&agrave; chương tr&igrave;nh du lịch trọn g&oacute;i, Vietravel kh&ocirc;ng c&oacute; tr&aacute;ch nhiệm ho&agrave;n trả chi ph&iacute; ch&ecirc;nh lệch cho c&aacute;c kh&aacute;ch h&agrave;ng thuộc diện miễn giảm hoặc đối tượng ưu ti&ecirc;n như trẻ nhỏ, người lớn tuổi, người c&oacute; c&ocirc;ng với c&aacute;ch mạng&hellip; Ch&iacute;nh s&aacute;ch miễn giảm, ưu ti&ecirc;n (nếu c&oacute;) chỉ d&agrave;nh cho kh&aacute;ch lẻ của c&aacute;c điểm tham quan.<br />\r\n&nbsp;</p>'),
(71, 189, 'HÀ NỘI - SÀI GÒN - MŨI NÉ (ĂN TRƯA, TỐI)', '<p><strong>S&aacute;ng:</strong>&nbsp;Xe &ocirc; t&ocirc; v&agrave; Hướng dẫn vi&ecirc;n (HDV) đ&oacute;n qu&yacute; kh&aacute;ch tại&nbsp;<strong>Rạp xiếc TW - Đường Trần Nh&acirc;n T&ocirc;ng - Q. Hai B&agrave; Trưng - Tp H&agrave; Nội</strong>&nbsp;khởi h&agrave;nh đi s&acirc;n bay Nội B&agrave;i, đ&aacute;p chuyến bay của h&atilde;ng h&agrave;ng kh&ocirc;ng Bamboo đi S&agrave;i G&ograve;n.&nbsp;<br />\r\n<strong>08h30:&nbsp;</strong>Đến s&acirc;n bay T&acirc;n Sơn Nhất, xe v&agrave; HDV địa phương đ&oacute;n đo&agrave;n khởi h&agrave;nh đi Mũi N&eacute;.<br />\r\n<strong>12h00:&nbsp;</strong>Đo&agrave;n d&ugrave;ng bữa trưa tại nh&agrave; h&agrave;ng.<br />\r\n<strong>Chiều:&nbsp;</strong>HDV đưa đo&agrave;n gh&eacute; thăm về di t&iacute;ch&nbsp;<strong>lầu &Ocirc;ng Ho&agrave;ng</strong>&nbsp;v&agrave; tham quan<strong>&nbsp;th&aacute;p Ch&agrave;m Poshanu.</strong><br />\r\n<strong>Tối:&nbsp;</strong>Đo&agrave;n d&ugrave;ng bữa tối tại nh&agrave; h&agrave;ng, thưởng thức hải sản Mũi N&eacute;. Nghỉ đ&ecirc;m tại kh&aacute;ch sạn.</p>'),
(72, 189, 'KHÁM PHÁ MŨI NÉ (ĂN SÁNG, TRƯA, TỐI TỰ TÚC)', '<p><strong>S&aacute;ng:</strong>&nbsp;Đo&agrave;n d&ugrave;ng bữa s&aacute;ng tại kh&aacute;ch sạn.<br />\r\n<strong>08h00:</strong>&nbsp;Xe đ&oacute;n đưa qu&yacute; kh&aacute;ch tham quan :</p>\r\n\r\n<ul>\r\n	<li><strong>Đồi C&aacute;t Hồng.&nbsp;</strong>Đến nơi, qu&yacute; kh&aacute;ch sẽ được kh&aacute;m ph&aacute; vẻ đẹp trinh nguy&ecirc;n của đồi c&aacute;t v&agrave;o b&igrave;nh minh của một ng&agrave;y mới, qu&yacute; kh&aacute;ch c&oacute; thể thu&ecirc; m&aacute;ng trượt (chi ph&iacute; tự t&uacute;c) hoặc d&ugrave;ng m&oacute;n b&aacute;nh bột lọc (chi ph&iacute; tự t&uacute;c) tại khu vực đồi c&aacute;t suối Hồng. &nbsp; &nbsp;</li>\r\n</ul>\r\n\r\n<p>Tiếp tục lộ tr&igrave;nh, qu&yacute; kh&aacute;ch khởi h&agrave;nh tham quan :</p>\r\n\r\n<ul>\r\n	<li><strong>Suối Ti&ecirc;n - &nbsp;</strong>L&agrave; một khe nước nhỏ đổ ra biển, thuộc phường H&agrave;m Tiến, th&agrave;nh phố Phan Thiết. Khu vực n&agrave;y được du kh&aacute;ch đặt cho l&agrave; &quot;Bồng Lai Ti&ecirc;n Cảnh&quot;. Trước đ&acirc;y, khe nước nhỏ n&agrave;y c&ograve;n c&oacute; t&ecirc;n gọi l&agrave; Suối Tre. Người Phan Thiết cũng &iacute;t ai biết Suối Tre v&igrave; n&oacute; nằm khuất sau những đồi c&aacute;t ch&aacute;y nắng.</li>\r\n</ul>\r\n\r\n<p><strong>Trưa:</strong>&nbsp;Qu&yacute; kh&aacute;ch d&ugrave;ng bữa tại nh&agrave; h&agrave;ng<br />\r\n<strong>Chiều:&nbsp;</strong>Xe đưa Qu&yacute; kh&aacute;ch tham quan:</p>\r\n\r\n<ul>\r\n	<li><strong>L&acirc;u đ&agrave;i Vang RD trong khu nghỉ dưỡng Sealink:</strong>&nbsp;Tại đ&acirc;y, du kh&aacute;ch c&oacute; cảm gi&aacute;c trở về thời trung cổ của ch&acirc;u &Acirc;u với khu quảng trường rộng lớn, m&aacute;i v&ograve;m cổ k&iacute;nh, trang nh&atilde;, sang trọng, v&agrave; những th&aacute;p canh cao v&uacute;t trải tầm nh&igrave;n ra kh&ocirc;ng gian tươi xanh của Sea Links City và toàn vịnh Mũi Né. Ngo&agrave;i ra, Qu&yacute; kh&aacute;ch được nghe giới thiệu về quy tr&igrave;nh sản xuất, đ&oacute;ng chai v&agrave; thưởng thức một trong những loại rượu vang hảo hạng nổi tiếng thế giới được xuất xứ từ Thung lũng Napa (Mỹ)</li>\r\n	<li>Tham quan<strong>&nbsp;cơ sở sản xuất nước mắm v&agrave; mua sắm đặc sản địa phương về l&agrave;m qu&agrave;.</strong></li>\r\n	<li><strong>Trung t&acirc;m b&ugrave;n kho&aacute;ng n&oacute;ng Mũi N&eacute;.&nbsp;</strong>Tại đ&acirc;y, qu&yacute; kh&aacute;ch sẽ được thưởng thức c&aacute;c dịch vụ tắm b&ugrave;n kho&aacute;ng, tắm kho&aacute;ng n&oacute;ng, hồ bơi kho&aacute;ng ấm với đội nghũ nh&acirc;n vi&ecirc;n th&acirc;n thiện chuy&ecirc;n nghiệp lu&ocirc;n sẵn s&agrave;ng phục vụ tốt nhất cho du kh&aacute;ch (chi ph&iacute; tự t&uacute;c).</li>\r\n</ul>\r\n\r\n<p><strong>Tối:&nbsp;</strong>Qu&yacute; kh&aacute;ch tự do ăn tối sau đ&oacute; nghỉ ngơi, tự do dạo biển Mũi N&eacute; hoặc tham gia c&aacute;c hoạt động ngo&agrave;i chương tr&igrave;nh:&nbsp;<strong>Thưởng thức Fishermen Show - Nhạc kịch nước Huyền Thoại L&agrave;ng Ch&agrave;i: Show giải tr&iacute; đặc sắc v&agrave; đậm n&eacute;t văn h&oacute;a l&agrave;ng ch&agrave;i của Phan Thiết - B&igrave;nh Thuận</strong>&nbsp;(chi ph&iacute; tự t&uacute;c).</p>'),
(73, 189, 'MŨI NÉ - VŨNG TÀU (ĂN SÁNG, TRƯA, TỐI)', '<p><strong>S&aacute;ng:&nbsp;</strong>Qu&yacute; kh&aacute;ch d&ugrave;ng bữa s&aacute;ng tại kh&aacute;ch sạn. Đo&agrave;n tự do đi chợ mua hải sản, mua sắm h&agrave;ng h&oacute;a, qu&agrave; lưu niệm về qu&agrave; l&agrave;m.<br />\r\n<strong>08h00:</strong>&nbsp;Đo&agrave;n tập trung l&ecirc;n xe về với Vũng T&agrave;u.<br />\r\n<strong>12h00:</strong>&nbsp;Đo&agrave;n d&ugrave;ng bữa trưa tại nh&agrave; h&agrave;ng.<br />\r\n<strong>13h30:</strong>&nbsp;Đo&agrave;n khởi h&agrave;nh về trung t&acirc;m th&agrave;nh phố, nhận ph&ograve;ng kh&aacute;ch sạn.<br />\r\n<strong>Chiều: Qu&yacute; kh&aacute;ch đi tham quan&nbsp;</strong>Tượng Ch&uacute;a Jesu lớn nhất thế giới nằm tr&ecirc;n đỉnh Tao Ph&ugrave;ng, tham quan l&ograve;ng tượng, l&ecirc;n c&aacute;nh tay ch&uacute;a ngắm to&agrave;n cảnh Vũng T&agrave;u.&nbsp;<br />\r\n<strong>19h00:</strong>&nbsp;Ăn tối nh&agrave; h&agrave;ng v&agrave; tự do kh&aacute;m ph&aacute; Vũng T&agrave;u về đ&ecirc;m</p>'),
(74, 189, 'VŨNG TÀU - SÀI GÒN - HÀ NỘI (ĂN SÁNG, TRƯA)', '<p><strong>S&aacute;ng:&nbsp;</strong>Qu&yacute; kh&aacute;ch d&ugrave;ng bữa s&aacute;ng. Qu&yacute; kh&aacute;ch c&oacute; thể đi chợ mua đặc sản về l&agrave;m qu&agrave; cho người th&acirc;n v&agrave; gia đ&igrave;nh.<br />\r\n<strong>08h00:&nbsp;</strong>Qu&yacute; kh&aacute;ch khởi h&agrave;nh đi tham quan:</p>\r\n\r\n<ul>\r\n	<li><strong>Đ&igrave;nh Thần Thắng Tam v&agrave; Bạch Dinh - Biệt thự trắng tinh tế cho kiến tr&uacute;c Ph&aacute;p Bạch Dinh</strong>&nbsp;được x&acirc;y dựng từ năm 1898 và hoàn thành vào năm 1916, có nghĩa là Villa Balanche. Dinh thự dùng làm nơi nghỉ ngơi của Paul Doumer (toàn quy&ecirc;̀n Pháp). &nbsp;&nbsp;</li>\r\n</ul>\r\n\r\n<p><strong>12h00:</strong>&nbsp;Đo&agrave;n ăn trưa tại nh&agrave; h&agrave;ng.<br />\r\n<strong>14h00:&nbsp;</strong>Đo&agrave;n l&ecirc;n xe khởi h&agrave;nh về lại S&agrave;i G&ograve;n. HDV l&agrave;m thủ tục cho qu&yacute; kh&aacute;ch bay về lại H&agrave; Nội.<br />\r\n<strong>20h30:&nbsp;</strong>Về đến s&acirc;n bay Nội B&agrave;i, xe v&agrave; HDV đ&oacute;n qu&yacute; kh&aacute;ch về lại trung t&acirc;m th&agrave;nh phố, trả kh&aacute;ch tại điểm đ&oacute;n. Chia tay v&agrave; hẹn gặp lại qu&yacute; kh&aacute;ch.</p>\r\n\r\n<p><strong>Ghi ch&uacute;:&nbsp;</strong>Lịch tr&igrave;nh thăm quan c&oacute; thể thay đổi linh hoạt theo thực tế nhưng vẫn đảm bảo đầy đủ c&aacute;c điểm theo chương tr&igrave;nh.</p>'),
(79, 192, 'TP.HCM - CHÂU ĐỐC - MIẾU BÀ CHÚA XỨ (Ăn sáng, trưa, chiều)', '<p>Qu&yacute; kh&aacute;ch tập trung tại Vietravel (190 Pasteur, Phường V&otilde; Thị S&aacute;u, Quận 3, Tp.HCM), khởi h&agrave;nh đi<strong>&nbsp;Ch&acirc;u Đốc</strong>&nbsp;theo<strong>&nbsp;tuyến cao tốc TP.HCM - Trung Lương v&agrave; Trung Lương - Mỹ Thuận</strong>&nbsp;ngắm nh&igrave;n những c&aacute;nh đồng l&uacute;a v&agrave; m&agrave;u xanh vườn tược hai b&ecirc;n đường; Chinh phục&nbsp;<strong>cầu V&agrave;m Cống:&nbsp;</strong>cầu d&acirc;y văng lớn thứ hai nối liền đ&ocirc;i bờ S&ocirc;ng Hậu, được kh&aacute;nh th&agrave;nh trong sự h&aacute;o hức của h&agrave;ng vạn người d&acirc;n sau bao năm chờ mong.</p>\r\n\r\n<p>Đến&nbsp;<strong>Ch&acirc;u Đốc,</strong>&nbsp;Qu&yacute; kh&aacute;ch trải nghiệm đi t&agrave;u tr&ecirc;n s&ocirc;ng Hậu kh&aacute;m ph&aacute; n&eacute;t đẹp bản địa của v&ugrave;ng đất An Giang<br />\r\n<br />\r\n<strong>- L&agrave;ng b&egrave; Ch&acirc;u Đốc:&nbsp;</strong>l&agrave;ng nghề nu&ocirc;i c&aacute; nổi tiếng An Giang rực rỡ sắc m&agrave;u tạo ra một trong những cung đường thuỷ độc đ&aacute;o nhất miền T&acirc;y<br />\r\n<br />\r\n<strong>- Tham quan Nh&agrave; dệt Thổ Cẩm truyền thống hơn 120 năm của nghệ nh&acirc;n Mohamed;</strong>&nbsp;Qu&yacute; kh&aacute;ch c&oacute; thể thu&ecirc; trang phục Chăm v&agrave; check-in<strong>&nbsp;ph&ograve;ng cưới độc đ&aacute;o của người Chăm Islam</strong>&nbsp;(chi ph&iacute; tự t&uacute;c)</p>\r\n\r\n<p>Buổi chiều, xe đưa Qu&yacute; kh&aacute;ch tham quan&nbsp;<strong>Miếu B&agrave; Ch&uacute;a Xứ, T&acirc;y An Cổ Tự, Lăng Thoại Ngọc Hầu.</strong></p>\r\n\r\n<p><br />\r\n<strong>Nghỉ đ&ecirc;m tại Ch&acirc;u Đốc.</strong></p>'),
(80, 192, 'CHÂU ĐỐC - RỪNG TRÀM TRÀ SƯ - HÀ TIÊN - MŨI NAI (Ăn sáng, trưa, chiều)', '<p>Đo&agrave;n d&ugrave;ng điểm t&acirc;m v&agrave; trả ph&ograve;ng, khởi h&agrave;nh đi tham quan:</p>\r\n\r\n<p><strong>- Rừng Tr&agrave;m Tr&agrave; Sư:</strong>&nbsp;<strong>&lsquo;Bảo t&agrave;ng Tr&agrave;m nhiệt đới&rsquo;&nbsp;</strong>c&oacute; tổng diện t&iacute;ch gần 1.500 h&eacute;c-ta c&ugrave;ng hệ sinh th&aacute;i động-thực vật đa dạng. Du kh&aacute;ch di chuyển bằng xuồng m&aacute;y hay những chiếc ghe để len lỏi giữa c&aacute;c lối đi nhỏ xuy&ecirc;n qua khu rừng tr&agrave;m ngắm nh&igrave;n những mảng b&egrave;o xanh ng&aacute;t trải d&agrave;i; check-in nh&agrave; trống - m&aacute;i, bến t&agrave;u tổ chim ngộ nghĩnh, &lsquo;C&acirc;y Cầu Tre Vạn Bước&rsquo; d&agrave;i nhất Việt Nam; đặc biệt, bối cảnh phim trường<strong>&nbsp;&#39;Đất rừng phương Nam&#39;</strong>.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Khởi h&agrave;nh đến H&agrave; Ti&ecirc;n nhận ph&ograve;ng kh&aacute;ch sạn nghỉ ngơi. Buổi chiều đo&agrave;n tham quan:</p>\r\n\r\n<p><strong>- Lăng Mạc Cửu -</strong>&nbsp;nơi thờ d&ograve;ng họ Mạc m&agrave; khởi đầu l&agrave; &ocirc;ng Mạc Cửu - người đ&atilde; c&oacute; c&ocirc;ng khai ph&aacute; mảnh đất H&agrave; Ti&ecirc;n.</p>\r\n\r\n<p><strong>- Ch&ugrave;a Ph&ugrave; Dung -</strong>&nbsp;tọa lạc dưới ch&acirc;n n&uacute;i B&igrave;nh San.&nbsp;</p>\r\n\r\n<p><strong>-&nbsp;</strong>Tắm biển&nbsp;<strong>Mũi Nai:</strong>&nbsp;một trong mười cảnh đẹp đ&atilde; đi v&agrave;o thi ca; du kh&aacute;ch &quot;săn&quot; &aacute;nh ho&agrave;ng h&ocirc;n tr&ecirc;n v&ugrave;ng ch&acirc;u thổ Cửu Long.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Nghỉ đ&ecirc;m tại H&agrave; Ti&ecirc;n</strong></p>'),
(81, 192, 'HÀ TIÊN - RẠCH GIÁ - CẦN THƠ (Ăn sáng, trưa, chiều)', '<p>Đo&agrave;n ăn s&aacute;ng, trả ph&ograve;ng v&agrave; khởi h&agrave;nh viếng thăm:<br />\r\n<br />\r\n<strong>- Ch&ugrave;a Tam Bảo:&nbsp;</strong>được dựng v&agrave;o khoảng đầu thế kỷ XVIII, ch&ugrave;a được triều đ&igrave;nh nh&agrave; Nguyễn ban biển sắc phong n&ecirc;n c&ograve;n được gọi l&agrave; Sắc Tứ Tam Bảo Tự. Tại Điện Phật vẫn c&ograve;n được b&agrave;i tr&iacute; Pho tượng đức Phật A Di Đ&agrave; bằng đồng cao 1,40m, do ch&iacute;nh &ocirc;ng Mạc Cửu thờ ở ch&ugrave;a.</p>\r\n\r\n<p>Khởi h&agrave;nh đi<strong>&nbsp;Rạch Gi&aacute;,</strong>&nbsp;đo&agrave;n tham quan:</p>\r\n\r\n<p>-&nbsp;<strong>Đền thờ Nguyễn Trung Trực&nbsp;</strong>- đ&acirc;y l&agrave; ng&ocirc;i đ&igrave;nh thờ &ocirc;ng sớm nhất v&agrave; lớn nhất trong ch&iacute;n đền thờ ở Ki&ecirc;n Giang.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p>Khởi h&agrave;nh đi&nbsp;<strong>Cần Thơ&nbsp;</strong>đo&agrave;n về kh&aacute;ch sạn nhận ph&ograve;ng, nghỉ ngơi. Sau khi ăn tối, Qu&yacute; kh&aacute;ch c&oacute; thể tự do&nbsp;<strong>dạo phố đ&ecirc;m kh&aacute;m ph&aacute; &ldquo;T&acirc;y Đ&ocirc;&rdquo;</strong>&nbsp;lung linh sắc m&agrave;u: ngắm cảnh&nbsp;<strong>Cầu Cần Thơ</strong>&nbsp;về đ&ecirc;m; Check-in&nbsp;<strong>Cầu T&igrave;nh Y&ecirc;u&nbsp;</strong>- thiết kế uốn lượn h&igrave;nh chữ S c&ugrave;ng hai đ&agrave;i sen v&agrave; hệ thống đ&egrave;n led rực rỡ sắc m&agrave;u; Chụp ảnh tại&nbsp;<strong>Ch&ugrave;a &Ocirc;ng</strong>&nbsp;cổ k&iacute;nh ...<br />\r\n&nbsp;</p>\r\n\r\n<p><strong>Nghỉ đ&ecirc;m tại Cần Thơ.</strong></p>'),
(82, 192, 'CẦN THƠ - CHỢ NỔI CÁI RĂNG - TP.HỒ CHÍ MINH (Ăn sáng, trưa)', '<p>Đo&agrave;n trả ph&ograve;ng v&agrave; khởi h&agrave;nh ra<strong>&nbsp;Bến Ninh Kiều,</strong>&nbsp;xuống thuyền du ngoạn tr&ecirc;n<strong>&nbsp;s&ocirc;ng Cần Thơ,</strong>&nbsp;nghe giới thiệu về&nbsp;<strong>Chợ thủy sản nước ngọt Cần Thơ</strong>; trải nghiệm&nbsp;<strong>Chợ nổi C&aacute;i Răng</strong>&nbsp;- Một h&igrave;nh thức họp chợ đặc trưng tại c&aacute;c ng&atilde; s&ocirc;ng của v&ugrave;ng đồng bằng s&ocirc;ng Cửu Long đ&atilde; được c&ocirc;ng nhận Di Sản Văn H&oacute;a Phi Vật Thể Quốc Gia.<br />\r\n<br />\r\nTiếp tục, Qu&yacute; kh&aacute;ch tham quan:<br />\r\n<br />\r\n<strong>- L&agrave;ng du lịch Mỹ Kh&aacute;nh:</strong>&nbsp;điểm du lịch sinh th&aacute;i hấp dẫn với c&aacute;c điểm tham quan Nh&agrave; cổ Nam bộ hơn 115 năm tuổi; Vườn hoa Mỹ Kh&aacute;nh; Khu nh&agrave; văn ho&aacute; 4 d&acirc;n tộc Kinh - Hoa - Chăm - Khmer; L&agrave;ng nghề truyền thống &hellip;<br />\r\n&bull; Trải nghiệm tr&ograve; chơi&nbsp;<strong>Thảm trượt Cầu vồng: chinh phục cầu trượt phao kh&ocirc; đầu ti&ecirc;n của miền T&acirc;y&nbsp;</strong>với độ cao 20m, đường trượt d&agrave;i 160m.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>- Thiền Viện Phương Nam</strong>&nbsp;- một trong những ng&ocirc;i thiền viện rộng nhất Đồng bằng s&ocirc;ng Cửu Long, tham quan lầu trống, lầu chu&ocirc;ng, lễ Phật tại ch&aacute;nh điện.<br />\r\n&nbsp;</p>\r\n\r\n<p>Sau khi dừng ch&acirc;n tham quan v&agrave;&nbsp;<strong>mua sắm tại si&ecirc;u thị đặc sản v&agrave; qu&agrave; lưu niệm Hương Đảo,&nbsp;</strong>đo&agrave;n khởi h&agrave;nh về TP.HCM theo tuyến cao tốc&nbsp;<strong>Mỹ Thuận - Cần Thơ</strong>; Chi&ecirc;m ngưỡng&nbsp;<strong>cầu Mỹ Thuận 2</strong>&nbsp;- Dự &aacute;n trọng điểm quốc gia c&oacute; chiều d&agrave;i 1,9km nối liền tỉnh Vĩnh Long v&agrave; Tiền Giang.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>'),
(83, 193, 'TP. HCM - THÁC DAMBRI - ĐÀ LẠT', '<p>Qu&yacute; kh&aacute;ch tập trung tại Vietravel (190 Pasteur, P. V&otilde; Thị S&aacute;u, Q 3, TP.HCM), xe đưa đo&agrave;n khởi h&agrave;nh đi&nbsp;<strong>Đ&agrave; Lạt</strong>. Tr&ecirc;n đường đo&agrave;n dừng ch&acirc;n tham quan v&agrave; ăn trưa tại Bảo Lộc:</p>\r\n\r\n<p><strong>&bull;&nbsp;Th&aacute;c DamBri:</strong>&nbsp;được coi l&agrave; ngọn th&aacute;c lớn nhất v&ugrave;ng đất L&acirc;m Đồng với chiều cao 60m tạo th&agrave;nh 2 d&ograve;ng chảy cao thấp rất h&ugrave;ng vĩ. Khu du lịch th&aacute;c Đamb&#39;ri l&agrave; một quần thể du lịch bao gồm khu vui chơi, hồ, th&aacute;c v&agrave; rừng nguy&ecirc;n sinh rộng h&agrave;ng ngh&igrave;n hecta. Nơi đ&acirc;y c&oacute; &ldquo;ống trượt d&agrave;i nhất Đ&ocirc;ng Nam &Aacute;&rdquo; với độ d&agrave;i 1.650m.</p>\r\n\r\n<p>Tiếp tục h&agrave;nh tr&igrave;nh đến với th&agrave;nh phố sương m&ugrave;, Qu&yacute; kh&aacute;ch d&ugrave;ng bữa tối v&agrave; nhận ph&ograve;ng nghỉ</p>\r\n\r\n<p><strong>Nghỉ đ&ecirc;m tại Đ&agrave; Lạt</strong></p>'),
(84, 193, 'ĐÀ LẠT - ĐỒI CHÈ CẦU ĐẤT -  HẦM VANG ĐÀ LẠT - MONGO LAND', '<p>Đ&agrave; Lạt ch&agrave;o đ&oacute;n qu&yacute; kh&aacute;ch với kh&ocirc;ng kh&iacute; se lạnh thoang thoảng m&ugrave;i sương sớm mai, sau khi d&ugrave;ng bữa s&aacute;ng, xe đưa đo&agrave;n tham quan:<br />\r\n<br />\r\n<strong>&bull; Chi&ecirc;m ngưỡng đồi ch&egrave; Cầu Đất</strong>&nbsp;&ndash;<strong>&nbsp;check-in tua bin điện gi&oacute;:</strong>&nbsp;tr&ecirc;n đầu l&agrave; bầu trời xanh ng&aacute;t với l&agrave;n m&acirc;y trắng bay bay, b&ecirc;n dưới l&agrave; thảm tr&agrave; xanh tươi tắn, ở giữa l&agrave; những c&aacute;nh đồng quạt gi&oacute; trắng quay đều tuyệt đẹp kh&ocirc;ng chỉ đưa lữ kh&aacute;ch h&ograve;a m&igrave;nh v&agrave;o thi&ecirc;n nhi&ecirc;n kho&aacute;ng đạt m&agrave; c&ograve;n ngẫu nhi&ecirc;n kết hợp những gam m&agrave;u h&agrave;i h&ograve;a, mang đến những bức ảnh v&ocirc; c&ugrave;ng n&ecirc;n thơ. Ngồi nh&acirc;m nhi ly c&agrave; ph&ecirc; thơm lừng tại Panorama Cafe Cầu Đất tận hưởng những điều b&igrave;nh y&ecirc;n nhất của cuộc sống. (Chi ph&iacute; nước uống tự t&uacute;c)<br />\r\n<br />\r\n<strong>&bull; T&igrave;m hiểu v&agrave; thưởng thức rượu vang tại Hầm Vang Đ&agrave; Lạt, Qu&yacute; kh&aacute;ch sẽ được:&nbsp;</strong><br />\r\n- Kh&aacute;m ph&aacute; hầm vang duy nhất v&agrave; lớn nhất đạt ti&ecirc;u chuẩn ch&acirc;u &Acirc;u tại Việt Nam, đắm m&igrave;nh trong một kh&ocirc;ng gian độc đ&aacute;o của kiến tr&uacute;c v&agrave; m&ugrave;i hương, ngắm nh&igrave;n những m&aacute;i v&ograve;m theo lối kiến tr&uacute;c Trung cổ&nbsp;<br />\r\n- Được lắng nghe những c&acirc;u chuyện đầy hấp dẫn về người l&agrave;m vang qua từng thời kỳ<br />\r\n- Trải nghiệm 01 ly rượu vang với hương vị thanh khiết được ng&acirc;m ủ trong những th&ugrave;ng gỗ sồi sẽ mang lại cho kh&aacute;ch tham quan đầy đủ trải nghiệm về một hầm vang Ch&acirc;u &Acirc;u đặc trưng nhất<br />\r\n<br />\r\nBuổi chiều, đo&agrave;n tiếp tục tham quan:&nbsp;<br />\r\n<br />\r\n<strong>&bull; Mongo Land:&nbsp;</strong>mang vẻ đẹp ho&agrave;n to&agrave;n mới lạ - một &quot;tiểu M&ocirc;ng Cổ thu nhỏ&quot; trong l&ograve;ng Đ&agrave; Lạt được thiết kế như một n&ocirc;ng trại với những chiếc lều đủ m&agrave;u sắc. Tất cả đều được b&agrave;i tr&iacute;&nbsp;độc đ&aacute;o, bắt mắt theo phong c&aacute;ch M&ocirc;ng Cổ đặc trưng. Đến đ&acirc;y, ngo&agrave;i việc tận hưởng bầu kh&ocirc;ng kh&iacute; trong l&agrave;nh v&agrave; chi&ecirc;m ngưỡng vẻ đẹp của thi&ecirc;n nhi&ecirc;n h&ugrave;ng vĩ, Qu&yacute; kh&aacute;ch sẽ được trải nghi&ecirc;m:&nbsp;<br />\r\n- Thỏa th&iacute;ch chụp ảnh tại&nbsp;<strong>lều m&ocirc;ng cổ, x&iacute;ch đu, n&ocirc;ng trại &acirc;u, thảm cỏ, sa mạc xương rồng,&hellip;</strong><br />\r\n- Trải nghiệm cảm gi&aacute;c &ldquo;si&ecirc;u đ&atilde;&rdquo; tại<strong>&nbsp;đường trượt phao kh&ocirc; đẹp v&agrave; d&agrave;i nhất Đ&agrave; Lạt</strong><br />\r\n- H&ograve;a m&igrave;nh v&agrave;o thế giới của động vật:&nbsp;<strong>hươu sao, d&ecirc; mini, cừu, thỏ Việt, thỏ Holland Lop H&agrave; Lan, thỏ Sư Tử, chim Yến, Chinchilla, s&oacute;c Chipmunk,&hellip;&nbsp;</strong>v&agrave; tự tay cho th&uacute; ăn &nbsp;(thức ăn sẽ được Mongo Land chuẩn bị sẵn cho qu&yacute; kh&aacute;ch).&nbsp;<br />\r\n- Tham gia c&aacute;c hoạt động vui chơi như: trượt cỏ, bắn cung<br />\r\n- Trở th&agrave;nh những c&ocirc; n&agrave;ng, ch&agrave;ng trai du mục thực thụ khi diện tr&ecirc;n m&igrave;nh trang phục người M&ocirc;ng Cổ. (Chi ph&iacute; thu&ecirc; trang phục tự t&uacute;c).<br />\r\n<br />\r\nĐo&agrave;n d&ugrave;ng cơm chiều tại nh&agrave; h&agrave;ng&nbsp;<strong>Cơm ni&ecirc;u Như Ngọc&nbsp;</strong>nổi tiếng của th&agrave;nh phố sương m&ugrave; với c&aacute;c m&oacute;n ăn mang đậm bản sắc thuần Việt nhưng lại ghi dấu ấn với bạn b&egrave; quốc tế khi đ&atilde; đạt nhưng giải thưởng về ẩm thực, cũng l&agrave; những m&oacute;n ăn l&agrave;m n&ecirc;n t&ecirc;n tuổi của nh&agrave; h&agrave;ng như:&nbsp;<strong>ba chỉ ch&aacute;y cạnh, salad organic trộn, g&agrave; nướng da gi&ograve;n v&agrave; x&ocirc;i chi&ecirc;n, lẩu b&ograve;,...&nbsp;</strong>v&agrave; đặc biệt Qu&yacute; kh&aacute;ch sẽ được thưởng thức những m&oacute;n tr&aacute;ng miệng đặc sắc theo m&ugrave;a.</p>\r\n\r\n<p><br />\r\n<strong>Nghỉ đ&ecirc;m tại Đ&agrave; Lạt.</strong></p>'),
(85, 193, 'ĐÀ LẠT –  THĂM VƯỜN DÂU TÂY – QUẢNG TRƯỜNG LÂM VIÊN', '<p>Sau khi d&ugrave;ng bữa s&aacute;ng tại kh&aacute;ch sạn, xe đưa đo&agrave;n tham quan:<br />\r\n<br />\r\n<strong>&bull; Vườn d&acirc;u Hiệp Lực:</strong>&nbsp;l&agrave; một trong những vườn d&acirc;u tại Đ&agrave; Lạt đạt chất lượng cao với c&aacute;c giống d&acirc;u đa dạng như New Zealand v&agrave; Ph&aacute;p. Đến nơi đ&acirc;y, ngo&agrave;i việc được t&igrave;m hiểu phương ph&aacute;p trồng d&acirc;u Qu&yacute; kh&aacute;ch c&ograve;n được thoả sức tạo d&aacute;ng check-in b&ecirc;n những gi&agrave;n d&acirc;u đỏ mộng, trải nghiệm h&aacute;i d&acirc;u v&agrave; mua về l&agrave;m qu&agrave; cho người th&acirc;n, bạn b&egrave; (chi ph&iacute; tự t&uacute;c)<br />\r\n<br />\r\n<strong>&bull; Quảng trường L&acirc;m Vi&ecirc;n:</strong>&nbsp;Điểm nhấn của TP. Đ&agrave; Lạt b&ecirc;n bờ hồ Xu&acirc;n Hương với c&ocirc;ng tr&igrave;nh nghệ thuật b&ocirc;ng hoa d&atilde; quỳ v&agrave; nụ hoa Atiso khổng lồ được thiết kế bằng k&iacute;nh m&agrave;u, lấp l&aacute;nh phản chiếu đẹp mắt bởi &aacute;nh s&aacute;ng.<br />\r\n<br />\r\n&bull; Mua sắm đặc sản Đ&agrave; Lạt tại&nbsp;<strong>cơ sở mứt Thanh Nhu:</strong>&nbsp;c&aacute;c loại tr&aacute;i c&acirc;y sấy gi&ograve;n, sấy dẻo, nước cốt d&acirc;u tằm &ndash; d&acirc;u tươi, tr&agrave; atiso, tr&agrave; hoa, ...<br />\r\n<br />\r\nSau khi d&ugrave;ng bữa trưa, xe đưa đo&agrave;n về lại kh&aacute;ch sạn tự do nghỉ ngơi. Buổi chiều Qu&yacute; kh&aacute;ch c&oacute; thể lựa chọn tham gia c&aacute;c trải nghiệm độc đ&aacute;o như:<br />\r\n<br />\r\n<strong>&bull; V&agrave;o 16h30 đ&oacute;n ho&agrave;ng h&ocirc;n v&agrave; thưởng thức bữa tiệc &acirc;m nhạc c&ugrave;ng Lululola Show</strong>&nbsp;với kh&ocirc;ng gian ngo&agrave;i trời v&agrave; c&oacute; tầm nh&igrave;n xuống th&agrave;nh phố Đ&agrave; Lạt thơ mộng. Hoặc v&agrave;o 20h00 thưởng thức những bản nhạc trữ t&igrave;nh xưa, nhạc Trịnh, nhạc Bolero tại &lsquo;&lsquo;&Acirc;m sắc Đ&agrave; Lạt &ndash; Cung Đ&agrave;n Xưa&rsquo;&rsquo;, Qu&yacute; kh&aacute;ch c&oacute; dịp trải nghiệm một phong c&aacute;ch sống &ldquo;chậm&rdquo; v&agrave; tận hưởng từng ph&uacute;t gi&acirc;y, lưu giữ những khoảnh khắc tuyệt vời ở tận s&acirc;u trong t&acirc;m hồn mỗi người. (Chi ph&iacute; tự t&uacute;c)<br />\r\n<br />\r\n&bull; Trải nghiệm tắm kho&aacute;ng n&oacute;ng &amp; x&ocirc;ng hơi tại Dlat&rsquo;s gi&uacute;p bạn thư gi&atilde;n v&agrave; nghỉ ngơi, giải tỏa căng thẳng (Chi ph&iacute; tự t&uacute;c)</p>\r\n\r\n<p><strong>***Lưu &yacute;: Chi ph&iacute; v&eacute; v&agrave; đi lại tự t&uacute;c***</strong></p>\r\n\r\n<p>Buổi tối Qu&yacute; kh&aacute;ch tự do tản bộ Chợ Đ&agrave; Lạt v&agrave; thưởng thức ẩm thực địa phương: lẩu b&ograve;, lẩu g&agrave; l&aacute; &eacute;, b&aacute;nh ướt l&ograve;ng g&agrave;, b&aacute;nh căn,....&nbsp;</p>\r\n\r\n<p><br />\r\n<strong>Nghỉ đ&ecirc;m tại Đ&agrave; Lạt.</strong></p>'),
(86, 193, 'ĐÀ LẠT – SAMTEN HILLS – CHIÊM NGƯỠNG ĐẠI BẢO THÁP KINH LUÂN - TP. HCM', '<p>Qu&yacute; kh&aacute;ch d&ugrave;ng bữa s&aacute;ng, l&agrave;m thủ tục trả ph&ograve;ng. Sau đ&oacute; xe v&agrave; HDV đưa kh&aacute;ch đi tham quan:&nbsp;<br />\r\n<br />\r\n<strong>&bull; Samten Hills Dalat:&nbsp;</strong>l&agrave; một khu du lịch trải nghiệm về văn h&oacute;a t&acirc;m linh vừa đi v&agrave;o hoạt động từ cuối th&aacute;ng 2/2023. Lấy cảm hứng từ kiến tr&uacute;c độc đ&aacute;o của văn h&oacute;a T&acirc;y Tạng, kết hợp với phong c&aacute;ch thiết kế hiện đại, tất cả đ&atilde; tạo n&ecirc;n một kh&ocirc;ng gian Văn h&oacute;a T&acirc;m linh Phật gi&aacute;o Kim Cương Thừa &ndash; Drigung Kagyu Samten Ling Việt Nam. Tại đ&acirc;y, đo&agrave;n sẽ được kh&aacute;m ph&aacute; v&agrave; trải nghiệm:<br />\r\n- Thưởng thức c&aacute;c t&aacute;c phẩm nghệ thuật tại<strong>&nbsp;Nh&agrave; trưng b&agrave;y Di Sản Phật Gi&aacute;o Kim Cương Thừa</strong>&nbsp;được chế t&aacute;c bởi đ&ocirc;i b&agrave;n tay t&agrave;i hoa v&agrave; sự hợp nhất th&acirc;n &ndash; t&acirc;m của c&aacute;c nghệ nh&acirc;n đến từ Ấn Độ, Nepal &ndash; qu&ecirc; hương của Đức Phật.&nbsp;<br />\r\n- Chi&ecirc;m ngưỡng bức tượng<strong>&nbsp;Đức Phật V&ocirc; Lượng Thọ v&agrave; Đức Phật Di Lạc</strong>&nbsp;bằng đồng ngo&agrave;i trời, cao 10.5m v&agrave; độc đ&aacute;o hiếm c&oacute; tại Việt Nam<br />\r\n- Với điểm nhấn nổi bật l&agrave;<strong>&nbsp;Đại bảo th&aacute;p Kinh Lu&acirc;n</strong>&nbsp;bằng đồng nguy&ecirc;n chất d&aacute;t v&agrave;ng 24K lớn nhất thế giới đ&atilde; được x&aacute;c lập kỷ lục Guinness.&nbsp;<br />\r\n<br />\r\nKhởi h&agrave;nh về TP. Hồ Ch&iacute; Minh, đo&agrave;n dừng tại Bảo Lộc d&ugrave;ng cơm trưa, sau đ&oacute; khởi h&agrave;nh về điểm đ&oacute;n ban đầu. Chia tay đo&agrave;n v&agrave; kết th&uacute;c chương tr&igrave;nh du lịch.&nbsp;</p>'),
(87, 194, 'Rạch Giá - Hòn Sơn - Câu cá & lặn ngắm san hô', '<p>Hướng dẫn vi&ecirc;n Vietravel đ&oacute;n kh&aacute;ch tại&nbsp;<strong>bến t&agrave;u Rạch Gi&aacute;</strong>, sau đ&oacute; đo&agrave;n l&agrave;m thủ tục xuống t&agrave;u đi&nbsp;<strong>H&ograve;n Sơn</strong>&nbsp;thời gian dự kiến&nbsp;<strong>07h30 - 09h00.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>09h00</strong>: T&agrave;u cập bến, Qu&yacute; kh&aacute;ch di chuyển đến nh&agrave; nghỉ gửi đồ. Đo&agrave;n tiếp tục di chuyển bằng xe m&aacute;y đến<strong>&nbsp;b&atilde;i tắm C&acirc;y Dừa Nằm</strong>&nbsp;- một trong những b&atilde;i tắm đẹp nhất&nbsp;<strong>H&ograve;n Sơn</strong>. Đến đ&acirc;y l&agrave;n nước trong vắt c&ugrave;ng b&atilde;i c&aacute;t mịn sạch sẽ đ&oacute;n ch&agrave;o du kh&aacute;ch, ho&agrave; c&ugrave;ng tiếng s&oacute;ng vỗ r&igrave; r&agrave;o, những h&agrave;ng dừa nghi&ecirc;ng như đang nằm tựa lưng tr&ecirc;n c&aacute;t mở ra một khung cảnh tuyệt vời như một bức tranh.</p>\r\n\r\n<p><strong>Trưa:</strong>&nbsp;Qu&yacute; kh&aacute;ch d&ugrave;ng bữa trưa, sau đ&oacute; nhận ph&ograve;ng nghỉ ngơi.</p>\r\n\r\n<p><strong>14h30</strong>: Đo&agrave;n l&ecirc;n t&agrave;u tham quan&nbsp;<strong>chương tr&igrave;nh c&acirc;u c&aacute; lặn ngắm san h&ocirc;</strong>&nbsp;tại H&ograve;n Sơn, Qu&yacute; kh&aacute;ch sẽ được<strong>&nbsp;thưởng thức ch&aacute;o mực</strong>&nbsp;trong h&agrave;nh tr&igrave;nh c&acirc;u c&aacute;.</p>\r\n\r\n<p><strong>18h00</strong>: Đo&agrave;n thưởng thức bữa tối&nbsp;<strong>BBQ</strong>&nbsp;ấm &aacute;p với nhiều m&oacute;n hải sản phong ph&uacute;. Tham gia chương tr&igrave;nh h&aacute;t với nhau.</p>\r\n\r\n<p><strong>Nghỉ đ&ecirc;m tại H&ograve;n Sơn</strong></p>'),
(88, 194, 'Hòn Sơn - Rạch Giá', '<p><strong>06h30</strong>: D&ugrave;ng bữa s&aacute;ng c&ugrave;ng c&aacute;c m&oacute;n ăn được chế biến với nước d&ugrave;ng đậm đ&agrave; vị hải sản, sau đ&oacute; đo&agrave;n đi theo cung đường l&ecirc;n n&uacute;i, ngắm nh&igrave;n to&agrave;n cảnh<strong>&nbsp;đảo H&ograve;n Sơn</strong>. Tr&ecirc;n đường đi đo&agrave;n tham quan, chụp ảnh&nbsp;<strong>suối Đ&aacute; bậc thang</strong>. Tiếp tục dừng ch&acirc;n tại&nbsp;<strong>đỉnh Y&ecirc;n Ngựa</strong>&nbsp;để Qu&yacute; kh&aacute;ch chụp h&igrave;nh v&agrave; ngắm nh&igrave;n to&agrave;n bộ H&ograve;n Sơn từ tr&ecirc;n cao.</p>\r\n\r\n<ul>\r\n	<li>Tiếp tục di chuyển đến tham quan&nbsp;<strong>dinh &Ocirc;ng Nam&nbsp;</strong>linh thi&ecirc;ng, đ&acirc;y l&agrave; nơi diễn ra&nbsp;<strong>Lễ hội Nghinh &Ocirc;ng H&ograve;n Sơn&nbsp;</strong>v&agrave;o ng&agrave;y 15 v&agrave; 16/10 &Acirc;m Lịch h&agrave;ng năm.</li>\r\n	<li>\r\n	<p>Check-in chụp h&igrave;nh với c&aacute;c b&atilde;i biển đẹp v&agrave; rất hoang sơ như&nbsp;<strong>b&atilde;i Đ&aacute; B&agrave;ng, b&atilde;i Đ&aacute; Ch&agrave;i</strong>&nbsp;- sở hữu những h&igrave;nh th&ugrave; đặc sắc v&agrave; kỳ th&uacute; kh&ocirc;ng nơi n&agrave;o c&oacute; được.&nbsp;</p>\r\n	</li>\r\n</ul>\r\n\r\n<p><strong>10h15</strong>: Đo&agrave;n d&ugrave;ng bữa trưa với những m&oacute;n hải sản thơm ngon, hấp dẫn.</p>\r\n\r\n<p><strong>11h00:</strong>&nbsp;Qu&yacute; kh&aacute;ch chuẩn bị h&agrave;nh l&yacute; v&agrave; đ&oacute;n&nbsp;<strong>chuyến t&agrave;u cao tốc (dự kiến 12h45 - 14h15)</strong>&nbsp;khởi h&agrave;nh về&nbsp;<strong>cảng Rạch Gi&aacute;</strong>, kết th&uacute;c chương tr&igrave;nh du lịch. Chia tay v&agrave; hẹn gặp lại Q&uacute;y kh&aacute;ch.</p>'),
(89, 195, 'TP. Hồ Chí Minh - Quy Nhơn - Chùa Thiên Hưng', '<p>Qu&yacute; kh&aacute;ch tập trung tại cột số 5 ga đi Trong Nước S&acirc;n bay T&acirc;n Sơn Nhất, hướng dẫn vi&ecirc;n hỗ trợ l&agrave;m thủ tục cho đo&agrave;n đ&aacute;p chuyến bay đi s&acirc;n bay Ph&ugrave; C&aacute;t (Quy Nhơn). Đến nơi, Xe v&agrave; hướng dẫn vi&ecirc;n Vietravel đ&oacute;n đo&agrave;n đi tham quan:&nbsp;</p>\r\n\r\n<ul>\r\n	<li><strong>Ch&ugrave;a Thi&ecirc;n Hưng:</strong>&nbsp;vẻ đẹp của &ldquo;Phượng Ho&agrave;ng cổ trấn&rdquo; phi&ecirc;n bản Việt, l&agrave; một trong những nơi kh&ocirc;ng thể bỏ qua khi đến Quy Nhơn &ndash; B&igrave;nh Định.</li>\r\n</ul>\r\n\r\n<p>Đo&agrave;n ăn tối v&agrave; tự do kh&aacute;m ph&aacute; th&agrave;nh phố biển về đ&ecirc;m với c&aacute;c qu&aacute;n c&agrave; ph&ecirc; nổi tiếng check in đẹp như: Surf Bar, S-Blue Caf&eacute;, Mộc Tr&agrave; Caf&eacute;&hellip;hoặc thưởng thức hải sản tại phố ẩm thực Xu&acirc;n Diệu, khu hồ sinh th&aacute;i nổi tiếng với m&oacute;n: bọ biển, cua Huỳnh Đế v&agrave; c&aacute;c loại ốc đặc trưng của v&ugrave;ng biển Quy Nhơn,..<em>&nbsp;(chi ph&iacute; tự t&uacute;c)</em>.&nbsp;</p>\r\n\r\n<p><strong>Nghỉ đ&ecirc;m tại Quy Nhơn</strong></p>'),
(90, 195, 'Quy Nhơn - Phú Yên', '<p>D&ugrave;ng bữa s&aacute;ng tại kh&aacute;ch sạn, xe đưa đo&agrave;n khởi h&agrave;nh đi Ph&uacute; Y&ecirc;n tham quan:</p>\r\n\r\n<ul>\r\n	<li><strong>Quảng Đức Xưa&nbsp;</strong>-<strong>&nbsp;</strong>được chi&ecirc;m ngưỡng những căn nh&agrave; gỗ cổ xưa được chạm khắc thủ c&ocirc;ng tinh xảo với những di vật, cổ vật b&agrave;i tr&iacute; h&agrave;i h&ograve;a, đặc biệt l&agrave; bộ sưu tập gốm Quảng Đức. Đến với Quảng Đức Xưa c&ograve;n l&agrave; đến với kh&ocirc;ng gian l&agrave;ng nghề truyền thống v&agrave; những m&oacute;n ăn d&acirc;n gi&atilde;. Du kh&aacute;ch c&oacute; thể thử dệt chiếu, nh&acirc;m nhi một t&aacute;ch tr&agrave; ngon hay chọn cho m&igrave;nh một chiếc b&aacute;nh đậm vị qu&ecirc;.</li>\r\n	<li><strong>Nh&agrave; Thờ Mằng Lăng&nbsp;</strong>- nơi lưu giữ cuốn kinh th&aacute;nh bằng chữ Quốc Ngữ đầu ti&ecirc;n của Việt Nam.</li>\r\n	<li><strong>G&agrave;nh Đ&aacute; Đĩa&nbsp;</strong>-&nbsp;thắng cảnh độc nhất v&ocirc; nhị của tỉnh Ph&uacute; Y&ecirc;n v&agrave; của Quốc Gia.</li>\r\n	<li>Tr&ecirc;n đường đi Qu&yacute; kh&aacute;ch c&ograve;n được chi&ecirc;m ngưỡng cảnh&nbsp;<strong>Đầm &Ocirc; Loan.</strong></li>\r\n	<li><strong>Th&aacute;p Nghinh Phong&nbsp;</strong>-<strong>&nbsp;</strong>một thiết kế đặc biệt ấn tượng lấy cảm hứng từ ghềnh đ&aacute; đĩa mang t&iacute;nh biểu tượng của địa phương v&agrave; truyền thuyết &ldquo;Trăm trứng trăm con&rdquo; của Lạc Long qu&acirc;n v&agrave; &Acirc;u Cơ, l&agrave; điểm đến hấp dẫn, thu h&uacute;t sự ch&uacute; &yacute; của nhiều bạn trẻ hẹn nhau sẽ tới nơi n&agrave;y để check-in.</li>\r\n</ul>\r\n\r\n<p>Buổi chiều xe đưa đo&agrave;n trở về TP. Quy Nhơn d&ugrave;ng bữa tối tại nh&agrave; h&agrave;ng địa phương. Buổi tối, Qu&yacute; kh&aacute;ch tự do kh&aacute;m ph&aacute; th&agrave;nh phố về đ&ecirc;m hoặc nghỉ ngơi tại kh&aacute;ch sạn.&nbsp;</p>\r\n\r\n<p><strong>Nghỉ đ&ecirc;m tại Quy Nhơn</strong></p>'),
(91, 195, 'Quy Nhơn - KDL Hầm Hô', '<p>D&ugrave;ng bữa s&aacute;ng tại kh&aacute;ch sạn, xe đưa đo&agrave;n đi tham quan:</p>\r\n\r\n<ul>\r\n	<li><strong>Khu du lịch Hầm H&ocirc;&nbsp;</strong>- l&acirc;m vi&ecirc;n hoang d&atilde; v&agrave; n&ecirc;n thơ, đo&agrave;n kh&aacute;m ph&aacute; cảnh non nước hữu t&igrave;nh. B&ecirc;n cạnh đ&oacute;, Qu&yacute; kh&aacute;ch c&oacute; thể tự t&uacute;c lựa chọn th&ecirc;m dịch vụ kh&aacute;c như: xe ngựa, c&acirc;u c&aacute; thư gi&atilde;n, ch&egrave;o thuyền tr&ecirc;n s&ocirc;ng K&uacute;t, nghỉ v&otilde;ng dưới t&aacute;n c&acirc;y rừng đại ng&agrave;n&hellip;</li>\r\n</ul>\r\n\r\n<p>Sau khi ăn trưa, xe đưa đo&agrave;n đi tham quan:&nbsp;</p>\r\n\r\n<ul>\r\n	<li><strong>Bảo T&agrave;ng Quang Trung:</strong>&nbsp;viếng Điện thờ T&acirc;y Sơn Tam Kiệt, chi&ecirc;m ngưỡng c&acirc;y me 300 tuổi, Giếng nước xưa&hellip;.</li>\r\n	<li><strong>Mua sắm c&aacute;c đặc sản Quy Nhơn&nbsp;</strong>tại cửa h&agrave;ng&nbsp;<strong>Hương Biển</strong>&nbsp;như Rượu B&agrave;u Đ&aacute;, Nem Chợ Huyện, B&aacute;nh Hồng, Tr&eacute; B&igrave;nh Định, B&aacute;nh &Iacute;t L&aacute; Gai&hellip;</li>\r\n</ul>\r\n\r\n<p>Qu&yacute; kh&aacute;ch d&ugrave;ng bữa tối tại nh&agrave; h&agrave;ng địa phương v&agrave; tự do nghỉ ngơi, dạo phố biển về đ&ecirc;m.&nbsp;</p>\r\n\r\n<p><strong>Nghỉ đ&ecirc;m tại Quy Nhơn</strong></p>'),
(92, 195, 'Quy Nhơn - Eo Gió - TP. Hồ Chí Minh', '<p>D&ugrave;ng bữa s&aacute;ng tại kh&aacute;ch sạn, trả ph&ograve;ng. Xe đưa đo&agrave;n đi tham quan:</p>\r\n\r\n<ul>\r\n	<li><strong>Eo Gi&oacute;&nbsp;</strong>- nơi vẫn c&ograve;n giữ được cho m&igrave;nh vẻ đẹp tự nhi&ecirc;n đầy hoang sơ. Đứng từ tr&ecirc;n cao nh&igrave;n xuống, Eo Gi&oacute; như một bức tranh sơn thủy hữu t&igrave;nh với trời, m&acirc;y, nước&hellip; biển n&uacute;i &ocirc;m nhau vừa gợi cảm, vừa hoang sơ đầy quyến rũ.</li>\r\n	<li>Tr&ecirc;n đường đi Qu&yacute; kh&aacute;ch sẽ được chi&ecirc;m ngưỡng c&acirc;y cầu vượt biển d&agrave;i nhất Việt Nam&nbsp;<strong>Cầu Thị Nại</strong>&nbsp;nằm trong hệ thống cầu đường Nhơn Hội d&agrave;i gần 7 km nối th&agrave;nh phố Quy Nhơn với b&aacute;n đảo Phương Mai.</li>\r\n	<li>Dừng ch&acirc;n chụp h&igrave;nh tại ch&acirc;n n&uacute;i&nbsp;<strong>Ch&ugrave;a Linh Phong (Ch&ugrave;a &Ocirc;ng N&uacute;i)&nbsp;</strong>check in tượng phật ngồi 69m cao nhất Đ&ocirc;ng Nam &Aacute; hiện nay.</li>\r\n</ul>\r\n\r\n<p>Sau bữa trưa, xe đưa đo&agrave;n ra s&acirc;n bay Ph&ugrave; C&aacute;t đ&aacute;p chuyến bay về Tp.Hồ Ch&iacute; Minh. Chia tay Qu&yacute; kh&aacute;ch v&agrave; kết th&uacute;c chương tr&igrave;nh tại s&acirc;n bay.</p>\r\n\r\n<p><strong>KẾT TH&Uacute;C CHƯƠNG TR&Igrave;NH, TẠM BIỆT QU&Yacute; KH&Aacute;CH!</strong></p>\r\n\r\n<p><strong><em>Lưu &yacute;:</em></strong></p>\r\n\r\n<ul>\r\n	<li>H&agrave;nh tr&igrave;nh c&oacute; thể thay đổi thứ tự điểm đến t&ugrave;y v&agrave;o điều kiện thực tế.</li>\r\n	<li>Lịch tr&igrave;nh tham quan (tắm biển, ngắm hoa, trải nghiệm,...) rất dễ bị ảnh hưởng bởi thời tiết. Đ&acirc;y l&agrave; trường hợp bất khả kh&aacute;ng mong Qu&yacute; kh&aacute;ch hiểu v&agrave; th&ocirc;ng cảm.</li>\r\n	<li>Kh&aacute;ch sạn c&oacute; thể ở xa trung t&acirc;m th&agrave;nh phố v&agrave;o c&aacute;c m&ugrave;a Cao điểm.&nbsp;</li>\r\n</ul>'),
(93, 197, 'TP. Hồ Chí Minh - CSO Gallery - Rừng dừa Bảy Mẫu “Trải nghiệm trồng cây tái tạo rừng dừa nước” - Chillax Eatery & Hangout - Phố cổ Hội An', '<p>Qu&yacute; kh&aacute;ch tập trung tại<strong>&nbsp;ga đi trong&nbsp;nước - s&acirc;n bay T&acirc;n Sơn Nhất</strong>, Hướng dẫn vi&ecirc;n Vietravel hỗ trợ Qu&yacute; kh&aacute;ch l&agrave;m thủ tục đ&aacute;p chuyến bay đi&nbsp;<strong>Đ&agrave; Nẵng</strong>.&nbsp;</p>\r\n\r\n<p>Xe v&agrave; Hướng dẫn vi&ecirc;n đ&oacute;n đo&agrave;n tham quan&nbsp;<strong>Rừng dừa Bảy Mẫu&nbsp;</strong>tr&ecirc;n những chiếc thuyền th&uacute;ng len lỏi qua c&aacute;c lạch dừa nước bạt ng&agrave;n, Qu&yacute; kh&aacute;ch trải nghiệm</p>\r\n\r\n<ul>\r\n	<li>Thưởng thức m&agrave;n<strong>&nbsp;lắc th&uacute;ng ch&eacute;m nước đi&ecirc;u luyện v</strong>&agrave; những điệu h&ograve; của cư d&acirc;n bản địa.</li>\r\n	<li>Được ngư d&acirc;n&nbsp;<strong>hướng dẫn c&aacute;ch ch&egrave;o thuyền th&uacute;ng</strong>&nbsp;v&agrave; tự tay&nbsp;<strong>quăng ch&agrave;i, thả lưới tr&ecirc;n s&ocirc;ng.</strong></li>\r\n	<li>Theo nh&aacute;nh&nbsp;<strong>s&ocirc;ng Thu Bồn&nbsp;</strong>hiền h&ograve;a, Qu&yacute; kh&aacute;ch<strong>&nbsp;tham gia hoạt động trồng dừa nước&nbsp;</strong>- g&oacute;p phần t&aacute;i tạo rừng dừa nước v&agrave; duy tr&igrave; hệ sinh th&aacute;i độc đ&aacute;o của Hội An.</li>\r\n</ul>\r\n\r\n<p>Buổi chiều, đo&agrave;n tham quan&nbsp;<strong>CSO Gallery&nbsp;</strong>- trải nghiệm văn h&oacute;a Việt Nam v&agrave; thế giới qua những vật phẩm được trưng b&agrave;y sống động:</p>\r\n\r\n<p>&bull; Kh&aacute;m ph&aacute; bộ sưu tập tiền xu v&agrave; tiền giấy cổ của Việt Nam n&oacute;i ri&ecirc;ng v&agrave; thế giới.</p>\r\n\r\n<p>&bull; Chi&ecirc;m ngưỡng quốc bảo văn học qua bộ sưu tập truyện Kiều lớn nhất Việt Nam.</p>\r\n\r\n<p>&bull; Trở về hồi ức sắc m&agrave;u &ldquo;nghe những chiếc tem kể chuyện lịch sử của ch&iacute;nh m&igrave;nh&rdquo;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đo&agrave;n tiếp tục h&agrave;nh tr&igrave;nh tham quan<strong>&nbsp;Chillax Eatery &amp; Hangout</strong>&nbsp;- m&ocirc; phỏng theo phong c&aacute;ch nh&agrave; rường truyền thống của người d&acirc;n Quảng Nam được x&acirc;y dựng từ c&aacute;c vật liệu t&aacute;i chế</p>\r\n\r\n<p>&bull; Chi&ecirc;m ngưỡng khung cảnh đồng qu&ecirc; y&ecirc;n b&igrave;nh, khu vườn xanh mướt, c&aacute;nh đồng l&uacute;a đẹp như tranh theo m&ugrave;a, đặc biệt kh&ocirc;ng gian &quot;Nơi Đ&agrave;n Chim Trở Về&rdquo;</p>\r\n\r\n<p>&bull;&nbsp;<strong>Thả hồn c&ugrave;ng thi&ecirc;n nhi&ecirc;n trong l&agrave;nh v&agrave; thưởng thức bữa tối trong kh&ocirc;ng gian đồng qu&ecirc; xứ Quảng.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Sau khi d&ugrave;ng bữa tối, Qu&yacute; kh&aacute;ch tiếp tục đến với&nbsp;<strong>Phố cổ Hội An</strong>&nbsp;tham quan&nbsp;<strong>Ch&ugrave;a Cầ</strong>u cổ k&iacute;nh &ldquo;kho&aacute;c chiếc &aacute;o mới&rdquo;,&nbsp;<strong>Hội qu&aacute;n</strong>,<strong>&nbsp;dạo phố đ&egrave;n lồng</strong>&nbsp;cảm nhận sự y&ecirc;n b&igrave;nh, cổ k&iacute;nh của 1 trong 10 điểm đến du lịch h&agrave;ng đầu Ch&acirc;u &Aacute;. Tự do thả đ&egrave;n hoa đăng cầu sức khỏe b&igrave;nh an v&agrave; ph&uacute;c lộc tr&ecirc;n d&ograve;ng s&ocirc;ng Ho&agrave;i&hellip;. (chi ph&iacute; tự t&uacute;c)</p>\r\n\r\n<p>Quay về&nbsp;<strong>Đ&agrave; Nẵng</strong>&nbsp;nhận ph&ograve;ng kh&aacute;ch sạn. Buổi tối, Qu&yacute; kh&aacute;ch tự do kh&aacute;m ph&aacute; Đ&agrave; Nẵng về đ&ecirc;m hoặc nghỉ ngơi tại kh&aacute;ch sạn.</p>\r\n\r\n<p><strong>Nghỉ đ&ecirc;m tại Đ&agrave; Nẵng</strong></p>');
INSERT INTO `tbl_timeline` (`timeLineId`, `tourId`, `title`, `description`) VALUES
(94, 197, 'Đà Nẵng - Bà Nà Hills - Bán đảo Sơn Trà', '<p>Qu&yacute; kh&aacute;ch d&ugrave;ng bữa s&aacute;ng tại kh&aacute;ch sạn, xe đưa Qu&yacute; kh&aacute;ch đi tham quan:</p>\r\n\r\n<ul>\r\n	<li><strong>Khu du lịch B&agrave; N&agrave;&nbsp;</strong>(chi ph&iacute; c&aacute;p treo, tham quan &amp; ăn trưa tự t&uacute;c) - tận hưởng kh&ocirc;ng kh&iacute; se lạnh của Đ&agrave; Lạt tại miền Trung, đo&agrave;n tự do tham quan&nbsp;<strong>Xưởng bia thủ c&ocirc;ng c&oacute; vị tr&iacute; cao nhất Việt Nam</strong>,&nbsp;<strong>L&agrave;ng Ph&aacute;p</strong>&nbsp;<strong>- Hội Chợ Ch&acirc;u &Acirc;u,</strong>&nbsp;<strong>Cầu V&agrave;ng&nbsp;</strong>- độc nhất v&ocirc; nhị,&nbsp;<strong>Ch&ugrave;a Linh Ứng</strong>,&nbsp;<strong>Vườn hoa bốn m&ugrave;a</strong>,&nbsp;<strong>Khu vui chơi Fantasy Park</strong>, &hellip;</li>\r\n	<li>Trải nghiệm<strong>&nbsp;t&agrave;u hỏa leo n&uacute;i</strong>&nbsp;kh&aacute;m ph&aacute;&nbsp;<strong>L&acirc;u đ&agrave;i Mặt Trăng, Quảng trường Nhật Thực.</strong></li>\r\n</ul>\r\n\r\n<p><strong>Ăn trưa tại B&agrave; N&agrave; tự t&uacute;c,&nbsp;</strong>sau đ&oacute; đo&agrave;n tiếp tục tham quan vui chơi đến giờ xuống c&aacute;p.</p>\r\n\r\n<p>Buổi chiều, Qu&yacute; kh&aacute;ch kh&aacute;m ph&aacute;&nbsp;<strong>B&aacute;n đảo Sơn Tr&agrave;&nbsp;</strong>-<strong>&nbsp;</strong>chi&ecirc;m ngưỡng to&agrave;n cảnh th&agrave;nh phố, n&uacute;i rừng v&agrave; biển đảo Sơn Tr&agrave; một c&aacute;ch ho&agrave;n hảo nhất.</p>\r\n\r\n<ul>\r\n	<li><strong>Ch&ugrave;a Linh Ứng</strong>&nbsp;- c&oacute; tượng Phật Quan Thế &Acirc;m cao nhất Việt Nam.</li>\r\n	<li><strong>Sơn Tr&agrave; Marina</strong>&nbsp;- h&ograve;a v&agrave;o hơi thở của đại dương s&aacute;ng tạo bức ảnh &ldquo;triệu view&rdquo; với lối kiến tr&uacute;c được lấy cảm hứng từ &ldquo;thi&ecirc;n đường Santorini&rdquo; v&agrave; m&agrave;u trời ho&agrave;ng h&ocirc;n say đắm v&agrave;o những ng&agrave;y thời tiết đẹp (chi ph&iacute; nước uống v&agrave; tham quan tự t&uacute;c)</li>\r\n</ul>\r\n\r\n<p>Mua sắm đặc sản phố biển tại&nbsp;<strong>Thi&ecirc;n Ph&uacute;&nbsp;</strong>hoặc<strong>&nbsp;Giao Mart</strong>&nbsp;- với hơn 2000 sản phẩm đặc sản Đ&agrave; Nẵng, Miền Trung v&agrave; c&aacute;c v&ugrave;ng miền l&acirc;n cận, nổi bật nhất l&agrave; chả b&ograve;, b&ograve; kh&ocirc;, mực rim me, mực kh&ocirc;, ghẹ sữa sốt v&agrave; đầy đủ c&aacute;c loại kh&ocirc; c&aacute;, ...</p>\r\n\r\n<p>Sau bữa ăn tối, Qu&yacute; kh&aacute;ch tự do đi dạo phố thưởng ngoạn cảnh đẹp của Đ&agrave; Nẵng về đ&ecirc;m, ngắm nh&igrave;n Cầu Rồng, Cầu T&igrave;nh Y&ecirc;u, Cầu Trần Thị L&yacute;, trung t&acirc;m thương mại, khu phố ẩm thực, Caf&eacute; - Bar - Disco,&hellip;</p>\r\n\r\n<p>﻿</p>\r\n\r\n<p><strong>Nghỉ đ&ecirc;m tại Đ&agrave; Nẵng</strong></p>\r\n\r\n<p>&nbsp;</p>'),
(95, 197, 'Đà Nẵng - Biển Mỹ Khê - Ngũ Hành Sơn - TP. Hồ Chí Minh', '<p>Sau khi ăn s&aacute;ng Qu&yacute; kh&aacute;ch tự do dạo&nbsp;<strong>biển Mỹ Kh&ecirc;</strong>&nbsp;- một trong những b&atilde;i biển quyến rũ nhất h&agrave;nh tinh.</p>\r\n\r\n<p>Tiếp tục h&agrave;nh tr&igrave;nh tham quan</p>\r\n\r\n<ul>\r\n	<li><strong>Ngũ H&agrave;nh Sơn</strong>&nbsp;- kh&aacute;m ph&aacute; sự kỳ vĩ của hệ thống hang động v&agrave; n&uacute;i độc đ&aacute;o c&ugrave;ng c&aacute;c cơ sở t&acirc;m linh: Động T&agrave;ng Chơn, Động Hoa Nghi&ecirc;m, Ch&ugrave;a Non Nước, &hellip;</li>\r\n	<li><strong>L&agrave;ng đ&aacute; non nước Quốc Hiệ</strong>p - t&igrave;m hiểu c&aacute;c sản phẩm đ&aacute; mỹ nghệ.</li>\r\n</ul>\r\n\r\n<p>Xe v&agrave; Hướng dẫn tiễn Qu&yacute; kh&aacute;ch ra s&acirc;n bay Đ&agrave; Nẵng đ&oacute;n chuyến bay trở về TP. Hồ Ch&iacute; Minh v&agrave; kết th&uacute;c chương tr&igrave;nh.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>***KẾT TH&Uacute;C CHƯƠNG TR&Igrave;NH &ndash; TẠM BIỆT QU&Yacute; KH&Aacute;CH***</strong></p>\r\n\r\n<p><strong><em>Lưu &yacute;:</em></strong></p>\r\n\r\n<p>-&nbsp;H&agrave;nh tr&igrave;nh c&oacute; thể thay đổi thứ tự điểm đến t&ugrave;y v&agrave;o điều kiện thực tế.&nbsp;</p>\r\n\r\n<p>-&nbsp;Lịch tr&igrave;nh tham quan (tắm biển, ngắm hoa, trải nghiệm, ...) rất dễ bị ảnh hưởng bởi thời tiết. Đ&acirc;y l&agrave; trường hợp bất khả kh&aacute;ng mong Qu&yacute; kh&aacute;ch hiểu v&agrave; th&ocirc;ng cảm.</p>\r\n\r\n<p>-&nbsp;Kh&aacute;ch Sạn c&oacute; thể ở xa trung t&acirc;m th&agrave;nh phố v&agrave;o c&aacute;c m&ugrave;a Cao Điểm.</p>'),
(96, 198, 'HÀ NỘI - HÀ GIANG - QUẢN BẠ (ĂN TRƯA/TỐI)', '<p><strong>S&aacute;ng:</strong>&nbsp;Xe &ocirc; t&ocirc; v&agrave; Hướng Dẫn Vi&ecirc;n (HDV)&nbsp;<strong><a href=\"https://www.vietnambooking.com/\" target=\"_blank\">Vietnam Booking</a></strong>&nbsp;đ&oacute;n qu&yacute; kh&aacute;ch tại điểm hẹn trong&nbsp;<strong>khu vực Phố Cổ</strong>&nbsp;v&agrave;<strong>&nbsp;Nh&agrave; h&aacute;t Lớn</strong>. Sau đ&oacute;, cả đo&agrave;n khởi h&agrave;nh chuyến&nbsp;du lịch kh&aacute;m ph&aacute;&nbsp;<strong>Tour&nbsp;H&agrave; Giang Cao Bằng 5 Ng&agrave;y</strong>. Tr&ecirc;n h&agrave;nh tr&igrave;nh, qu&yacute; kh&aacute;ch c&oacute; thể tranh thủ chi&ecirc;m ngưỡng ngắm nh&igrave;n n&uacute;i rừng&nbsp;Đ&ocirc;ng bắc v&ocirc; c&ugrave;ng h&ugrave;ng vĩ v&agrave; hoang sơ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em><strong>Lưu &yacute;:</strong>&nbsp;Xe c&oacute; thể đ&oacute;n Qu&yacute; kh&aacute;ch tại Ng&atilde; 3 Kim Anh (đầu cao tốc H&agrave; Nội - L&agrave;o Cai, c&aacute;ch s&acirc;n bay Nội B&agrave;i 03km, gần khu vực c&aacute;c kh&aacute;ch sạn ở Nội B&agrave;i v&agrave;o khoảng 07h30 s&aacute;ng). Do đ&oacute; nếu kh&aacute;ch h&agrave;ng bay ra Nội B&agrave;i muộn c&oacute; thể kh&ocirc;ng book ph&ograve;ng kh&aacute;ch sạn gần s&acirc;n bay để tiết kiệm thời gian nghỉ ngơi v&agrave; tiết kiệm chi ph&iacute; taxi về phố Cổ.</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Trưa:</strong>&nbsp;Qu&yacute; kh&aacute;ch d&ugrave;ng cơm&nbsp;trưa tại thị trấn T&acirc;n Y&ecirc;n (H&agrave;n Y&ecirc;n, Tuy&ecirc;n Quang).</p>\r\n\r\n<p><strong>Chiều:</strong>&nbsp;Tiếp tục h&agrave;nh tr&igrave;nh<em>&nbsp;tour H&agrave; Giang từ H&agrave; Nội</em>, đo&agrave;n sẽ dừng ch&acirc;n tham quan c&aacute;c điểm kh&ocirc;ng thể bỏ qua khi&nbsp;<strong><a href=\"https://www.vietnambooking.com/du-lich/tour-du-lich/du-lich-ha-giang.html\" target=\"_blank\">du lịch H&agrave; Giang</a></strong>:</p>\r\n\r\n<ul>\r\n	<li><strong>Đền Đ&ocirc;i C&ocirc; Cầu M&aacute;:&nbsp;</strong>L&agrave; một trong năm đền đ&ocirc;i c&ocirc; lớn nhất&nbsp;của Việt Nam, thuộc hệ thống đền thờ trong t&iacute;n ngưỡng đạo Mẫu. Nhiều du kh&aacute;ch đến địa điểm t&acirc;m linh n&agrave;y để cầu t&agrave;i lộc, b&igrave;nh an, hạnh ph&uacute;c cho gia đ&igrave;nh. Qu&yacute; kh&aacute;ch c&oacute; thể đến đ&acirc;y v&atilde;n cảnh, chi&ecirc;m b&aacute;i.</li>\r\n	<li>Đến th&agrave;nh phố H&agrave; Giang, qu&yacute; kh&aacute;ch chụp ảnh kỷ niệm tại&nbsp;<strong>Km0 của H&agrave; Giang</strong>.</li>\r\n	<li>Dừng ch&acirc;n gh&eacute; thăm&nbsp;<strong>Cổng trời Quản Bạ&nbsp;</strong>v&agrave; chụp h&igrave;nh với<strong>&nbsp;N&uacute;i đ&ocirc;i C&ocirc; Ti&ecirc;n</strong>&nbsp;(c&ograve;n được gọi l&agrave; n&uacute;i đ&ocirc;i Quản Bạ). Đến đ&acirc;y, qu&yacute; kh&aacute;ch sẽ được ngắm nh&igrave;n to&agrave;n cảnh thị trấn Tam Sơn từ&nbsp;tr&ecirc;n cao.</li>\r\n</ul>\r\n\r\n<p>Sau đ&oacute;, xe v&agrave; HDV đưa Qu&yacute; kh&aacute;ch nhận ph&ograve;ng nghỉ ngơi tại&nbsp;<strong>Y&ecirc;n Minh</strong>.&nbsp;</p>\r\n\r\n<p><strong>Tối:&nbsp;</strong>Đo&agrave;n d&ugrave;ng bữa&nbsp;tối với c&aacute;c m&oacute;n ăn thơm ngon của&nbsp;miền n&uacute;i. Sau đ&oacute;, qu&yacute; kh&aacute;ch&nbsp;tự do kh&aacute;m ph&aacute; &quot;cực Bắc của tổ quốc&quot; về đ&ecirc;m trong chuyến h&agrave;nh tr&igrave;nh&nbsp;<strong>tour H&agrave; Giang Cao Bằng 5 ng&agrave;y 4 đ&ecirc;m</strong>.</p>\r\n\r\n<p><strong>Đo&agrave;n nghỉ đ&ecirc;m tại Y&ecirc;n Minh.</strong></p>'),
(97, 198, 'YÊN MINH - LŨNG CÚ - ĐỒNG VĂN (ĂN SÁNG/TRƯA/TỐI)', '<p><strong>S&aacute;ng:</strong>&nbsp;Đo&agrave;n d&ugrave;ng điểm t&acirc;m&nbsp;s&aacute;ng, l&agrave;m thủ tục trả ph&ograve;ng v&agrave; khởi h&agrave;nh đi chi&ecirc;m ngưỡng những cảnh đẹp h&ugrave;ng vĩ&nbsp;của C&ocirc;ng vi&ecirc;n địa chất to&agrave;n cầu&nbsp;<strong>Cao nguy&ecirc;n đ&aacute; Đồng Văn</strong>. Dọc đường đi trong&nbsp;<strong>tour H&agrave; Giang Cao Bằng 5 ng&agrave;y 4 đ&ecirc;m</strong>, Qu&yacute; kh&aacute;ch dừng gh&eacute; thăm:</p>\r\n\r\n<ul>\r\n	<li><strong>Phố C&aacute;o</strong>&nbsp;- Nơi sinh sống của đồng b&agrave;o d&acirc;n tộc H&#39;M&ocirc;ng. Đến đ&acirc;y, du kh&aacute;ch sẽ được chi&ecirc;m ngưỡng những ng&ocirc;i nh&agrave; r&ecirc;u phong được bao bọc bởi lớp h&agrave;ng r&agrave;o đ&aacute; xung quanh nh&agrave;.</li>\r\n	<li><strong>Thăm bản Sủng L&agrave;</strong>&nbsp;- Địa điểm được sử dụng l&agrave;m bối cảnh quay bộ phim điện ảnh &quot;Chuyện của Pao&quot; năm 2006, chuyển thể từ truyện ngắn &quot;Tiếng đ&agrave;n m&ocirc;i sau bờ đ&aacute;&quot; của nh&agrave; văn Đỗ B&iacute;ch Thủy. Bộ phim đ&atilde; xuất sắc gi&agrave;nh được gần 4 giải C&aacute;nh Diều V&agrave;ng. Đến thăm bản l&agrave;ng, qu&yacute; kh&aacute;ch tha&nbsp;hồ check-in với c&aacute;nh đồng hoa tam gi&aacute;c mạch lung linh trong nắng mới gần dốc ch&iacute;n khoanh.</li>\r\n	<li><strong>Dinh Vua M&egrave;o&nbsp;hay&nbsp;Vương Ch&iacute;nh Đức</strong>&nbsp;nằm trong một thung lũng của x&atilde; S&agrave; Ph&igrave;n.&nbsp;Đ&acirc;y l&agrave; d&ograve;ng họ gi&agrave;u c&oacute; v&agrave; quyền uy nhất Ch&acirc;u Đồng Văn v&agrave;o đầu thế kỷ 20. Biệt phủ tọa lạc tại một thung lũng của x&atilde; S&agrave; Ph&igrave;n. Kh&ocirc;ng chỉ l&agrave; địa điểm tham quan nổi tiếng Đồng Văn, Dinh Thự Vua M&egrave;o c&ograve;n ẩn chứa nhiều c&acirc;u chuyện huyền b&iacute; xoay quanh gia tộc họ Vương ở thế kỷ trước. Ngo&agrave;i ra, Dinh thự được x&acirc;y dựng theo lối kiến tr&uacute;c truyền thống M&ocirc;ng kết hợp với Trung Hoa, Việt Nam v&agrave; nằm ngay vị tr&iacute; phong thủy đắc địa bậc nhất Đồng Văn.&nbsp;</li>\r\n	<li>Check-in&nbsp;<strong>Cột Cờ Lũng C&uacute;</strong>&nbsp;- nơi địa đầu Tổ quốc, điểm c&oacute; vĩ độ cao nhất tr&ecirc;n bản đồ của Việt Nam, nơi m&agrave; ai cũng ao ước được đặt ch&acirc;n đến.</li>\r\n</ul>\r\n\r\n<p><strong>Trưa:</strong>&nbsp;Đo&agrave;n d&ugrave;ng bữa&nbsp;trưa tại nh&agrave; h&agrave;ng, nghỉ ngơi v&agrave; tiếp tục h&agrave;nh tr&igrave;nh.</p>\r\n\r\n<p><strong>Chiều:</strong>&nbsp;Qu&yacute; kh&aacute;ch tiếp tục h&agrave;nh tr&igrave;nh<em>&nbsp;tour H&agrave; Giang s&ocirc;ng Nho Quế</em>&nbsp;hấp dẫn với c&aacute;c hoạt động:</p>'),
(98, 198, 'BẢO LẠC - THÁC BẢN GIỐC - ĐỘNG NGƯỜM NGAO - CAO BẰNG (ĂN SÁNG/TRƯA/TỐI)', '<p><strong>S&aacute;ng:</strong>&nbsp;Đo&agrave;n trả ph&ograve;ng, sau đ&oacute; ăn s&aacute;ng v&agrave; l&ecirc;n xe khởi h&agrave;nh đi&nbsp;<strong>Cao Bằng</strong>&nbsp;để tiếp tục h&agrave;nh tr&igrave;nh&nbsp;<strong>tour H&agrave; Giang 5 ng&agrave;y 4 đ&ecirc;m</strong>. Tr&ecirc;n đường đi, Qu&yacute; kh&aacute;ch dừng ch&acirc;n chụp h&igrave;nh với những cảnh đẹp kỳ vĩ của n&uacute;i non, cung đường uốn lượn.</p>\r\n\r\n<p><strong>Trưa:</strong>&nbsp;Đo&agrave;n d&ugrave;ng cơm trưa tại&nbsp;<strong>Quảng Uy&ecirc;n</strong>. Sau bữa trưa, Qu&yacute; kh&aacute;ch tiếp tục l&ecirc;n xe đi tới&nbsp;<strong>Bản Giốc&nbsp;</strong>để tham quan cảnh sắc thi&ecirc;n nhi&ecirc;n kỳ vĩ.</p>\r\n\r\n<p><strong>Chiều:</strong>&nbsp;Cả đo&agrave;n kh&aacute;m ph&aacute; những địa danh nổi tiếng của Bản Giốc:</p>\r\n\r\n<ul>\r\n	<li><strong>Động Ngườm Ngao (động Hố)</strong>&nbsp;- Một trong hang động đẹp nhất tỉnh th&agrave;nh miền Bắc. Hang động tọa lạc ở bản Gun, x&atilde; Đ&agrave;m Thủy, tỉnh Cao Bằng. Đến động, qu&yacute; kh&aacute;ch c&oacute; cơ hội chi&ecirc;m ngưỡng những nhũ đ&aacute;, măng đ&aacute; ph&aacute;t s&aacute;ng với c&aacute;c h&igrave;nh th&ugrave; đa dạng, phong ph&uacute;. Hang động c&oacute; tổng chiều d&agrave;i 2144 m&eacute;t, 3 cửa ch&iacute;nh, bao gồm: Ngườm Ngao, Ngườm Lồm, Bản Thu&ocirc;n.&nbsp;</li>\r\n	<li><strong>Th&aacute;c Bản Giốc&nbsp;</strong>- Được mệnh danh l&agrave; &quot;một trong những ngọn th&aacute;c đẹp nhất Việt Nam. Đồng thời, th&aacute;c cũng nằm trong Top 4 c&aacute;c th&aacute;c nước kỳ vĩ nhất tr&ecirc;n thế giới.&nbsp;Th&aacute;c được chia th&agrave;nh 3 tầng th&aacute;c ch&iacute;nh, c&oacute; độ cao 53 m&eacute;t. Ngo&agrave;i ra, th&aacute;c Bản Giốc l&agrave; th&aacute;c nước hiếm hoi nằm tr&ecirc;n đường bi&ecirc;n giới giữa Việt Nam v&agrave; Trung Quốc.</li>\r\n</ul>\r\n\r\n<p>Tối: Đo&agrave;n về lại th&agrave;nh phố Cao Bằng. Qu&yacute; kh&aacute;ch ăn tối rồi nhận ph&ograve;ng kh&aacute;ch sạn v&agrave; nghỉ ngơi. V&agrave;o buổi tối, qu&yacute; kh&aacute;ch tự do tham quan v&agrave; mua sắm tại khu vực chợ trung t&acirc;m th&agrave;nh phố.<strong>&nbsp;Nghỉ đ&ecirc;m tại Cao Bằng.</strong></p>'),
(99, 198, 'PÁC BÓ - PÁC BÓ - HỒ BA BỂ (ĂN SÁNG/TRƯA/TỐI)', '<p><strong>S&aacute;ng:</strong>&nbsp;Đo&agrave;n l&agrave;m thủ tục trả ph&ograve;ng v&agrave;&nbsp;ăn s&aacute;ng. Sau đ&oacute;,&nbsp;tiếp tục h&agrave;nh tr&igrave;nh chinh phục<strong>&nbsp;</strong><strong>P&aacute;c B&oacute;</strong><em>&nbsp;</em>- điểm đến nổi tiếng nhất&nbsp;<strong><a href=\"https://www.vietnambooking.com/du-lich/blog-du-lich/du-lich-cao-bang.html\" target=\"_blank\">du lịch Cao Bằng</a></strong>.</p>\r\n\r\n<p>Đến P&aacute;c B&oacute;, qu&yacute; kh&aacute;ch đi tham quan&nbsp;<strong>Khu di t&iacute;ch lịch sử P&aacute;c B&oacute;&nbsp;</strong>- &quot;Tọa độ&quot; n&agrave;y từng l&agrave; nơi hoạt động c&aacute;ch mạng của Chủ tịch Hồ Ch&iacute; Minh v&agrave; c&aacute;c chiến sĩ trong những năm kh&aacute;ng chiến chống Ph&aacute;p từ năm 1914 đến năm 1945. Đến đ&acirc;y, qu&yacute; kh&aacute;ch c&oacute; thể chụp ảnh lưu niệm tại Suối L&ecirc; Nin - N&uacute;i C&aacute;c M&aacute;c, thăm hang Cốc B&oacute;.</p>\r\n\r\n<p>Rời khỏi P&aacute;c B&oacute;, xe đưa qu&yacute; kh&aacute;ch đi Ba Bể.&nbsp;<strong>Tour H&agrave; Giang Cao Bằng 5 ng&agrave;y 4 đ&ecirc;m&nbsp;</strong>tiếp tục<strong>&nbsp;</strong>chuyến&nbsp;tham quan, ngắm nh&igrave;n cảnh đẹp rừng n&uacute;i qua&nbsp;những con đ&egrave;o như: đ&egrave;o T&agrave;i Hồ S&igrave;n, đ&egrave;o Cao Bắc, đ&egrave;o Khau Khang, đ&egrave;o Gi&oacute;.&nbsp;</p>\r\n\r\n<p><strong>Trưa:</strong>&nbsp;Đo&agrave;n dừng xe nghỉ ngơi v&agrave; ăn trưa tr&ecirc;n Đ&egrave;o Gi&oacute;.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Chiều:</strong>&nbsp;Sau khi đ&atilde; đến&nbsp;<strong>Ba Bể</strong>, qu&yacute; kh&aacute;ch nhận ph&ograve;ng tại nh&agrave; s&agrave;n của người T&agrave;y ven Hồ Ba Bể.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Tối:</strong>&nbsp;Đo&agrave;n d&ugrave;ng cơm&nbsp;tối v&agrave;&nbsp;nghỉ đ&ecirc;m tại&nbsp;<strong>Ba Bể</strong>. Ngo&agrave;i&nbsp;ra,&nbsp;Qu&yacute; kh&aacute;ch c&oacute; thể&nbsp;lựa chọn giao lưu văn nghệ, đốt lửa trại hoặc nghe biểu diễn h&aacute;t Then&nbsp;của người T&agrave;y&nbsp;<strong><em>(chi ph&iacute; tự t&uacute;c)</em>.</strong></p>'),
(100, 198, 'CAO BẰNG - HỒ BA BỂ - TP. HÀ NỘI (ĂN SÁNG/TRƯA)', '<p><strong>S&aacute;ng:</strong>&nbsp;Đo&agrave;n d&ugrave;ng điểm t&acirc;m s&aacute;ng tại nh&agrave; s&agrave;n v&agrave; thư gi&atilde;n ngắm cảnh hồ trong sương sớm.&nbsp;Sau đ&oacute;, qu&yacute; kh&aacute;ch tự do kh&aacute;m ph&aacute;&nbsp;<strong>Hồ Ba Bể</strong>&nbsp;tại những địa điểm l&yacute; th&uacute; sau:</p>\r\n\r\n<ul>\r\n	<li><strong>Ao ti&ecirc;n&nbsp;</strong>-<strong>&nbsp;</strong>Ao Ti&ecirc;n hiện l&ecirc;n l&agrave; một hồ nước nhỏ giữa n&uacute;i cao. Sở dĩ c&oacute; t&ecirc;n gọi n&agrave;y l&agrave; v&igrave; tương truyền trước kia, c&aacute;c vị ti&ecirc;n thường xuống đ&acirc;y ngồi chơi cờ&nbsp;n&ecirc;n người d&acirc;n đ&atilde; đặt t&ecirc;n gọi như vậy.</li>\r\n	<li><strong>Đền An Mạ&nbsp;</strong>-&nbsp;Ng&ocirc;i đền tọa lạc&nbsp;tr&ecirc;n một ngọn n&uacute;i nhỏ giữa l&ograve;ng hồ. Ng&ocirc;i đền giữa chốn uy nghi&ecirc;m của khung cảnh n&uacute;i rừng đ&atilde; t&ocirc;n l&ecirc;n vẻ đẹp trầm mặc, cổ k&iacute;nh nơi n&agrave;y. Tương truyền trong chiến tranh phong kiến thời L&ecirc; - Mạc, nh&agrave; Mạc đ&atilde; thất trận v&agrave; chạy đến Động Pu&ocirc;ng rồi tuẫn tiết&nbsp;tại đ&oacute;. Cảm động trước tinh thần hy sinh v&igrave; nghĩa lớn, một ng&ocirc;i đền thời họ Mạc được nh&acirc;n d&acirc;n dựng l&ecirc;n. Song, v&igrave; lo sợ bị qu&acirc;n nh&agrave; L&ecirc; ph&aacute;t hiện v&agrave; ph&aacute; hủy đền n&ecirc;n người d&acirc;n đ&atilde; đổi t&ecirc;n th&agrave;nh Đền An Mạ. Hai từ &quot;An Mạ&quot; trong tiếng d&acirc;n tộc T&agrave;y c&oacute; nghĩa l&agrave; &quot;mồ y&ecirc;n mả&nbsp;đẹp&quot;. Chắc c&oacute; lẽ đ&acirc;y l&agrave; l&ograve;ng th&agrave;nh k&iacute;nh m&agrave; nh&acirc;n d&acirc;n muốn gửi gắm đến c&aacute;c trung thần họ Mạc.</li>\r\n	<li><strong>Đảo B&agrave; G&oacute;a&nbsp;</strong>- Một h&ograve;n đảo&nbsp;tọa lạc ngay vị tr&iacute; trung t&acirc;m của hồ đ&atilde; tạo điểm nhấn th&ecirc;m cho nơi n&agrave;y th&ecirc;m phần hữu t&igrave;nh, thơ mộng. Thuyền sẽ&nbsp;đi chậm một v&ograve;ng quanh đảo để Qu&yacute; kh&aacute;ch chụp h&igrave;nh khi trải nghiệm&nbsp;<strong>tour H&agrave; Giang Cao Bằng 5 ng&agrave;y 4 đ&ecirc;m.</strong></li>\r\n</ul>\r\n\r\n<p><strong>Trưa:</strong>&nbsp;Đo&agrave;n về đến&nbsp;nh&agrave; h&agrave;ng d&ugrave;ng cơm trưa v&agrave; tiếp tục về H&agrave; Nội.</p>\r\n\r\n<p><strong>Chiều:</strong>&nbsp;Sau bữa trưa, qu&yacute; kh&aacute;ch ra&nbsp;xe đi&nbsp;về&nbsp;<strong>H&agrave; Nội</strong>.</p>\r\n\r\n<p><strong>Tối:</strong>&nbsp;Về đến&nbsp;<strong>H&agrave; Nội</strong>, xe trả Qu&yacute; kh&aacute;ch về điểm đ&oacute;n ban đầu trong&nbsp;<strong>tour H&agrave; Giang Cao Bằng 5 ng&agrave;y 4 đ&ecirc;m</strong>,&nbsp;kết th&uacute;c chương tr&igrave;nh du lịch đi&nbsp;<strong>H&agrave; Giang Cao Bằng 5 ng&agrave;y 4 đ&ecirc;m&nbsp;</strong>từ H&agrave; Nội đầy &yacute; nghĩa. Hướng dẫn vi&ecirc;n gửi ch&agrave;o tạm biệt v&agrave; hẹn gặp lại Qu&yacute; kh&aacute;ch trong những&nbsp;<a href=\"https://www.vietnambooking.com/du-lich\" target=\"_blank\"><strong>tour du lịch hấp dẫn</strong></a>&nbsp;kh&aacute;c một ng&agrave;y kh&ocirc;ng xa!</p>\r\n\r\n<p><em><strong>LƯU &Yacute;:</strong>&nbsp;Thứ tự v&agrave; chi tiết trong chương tr&igrave;nh c&oacute; thể thay đổi cho ph&ugrave; hợp với t&igrave;nh h&igrave;nh thực tế, nhưng vẫn đảm bảo đủ điểm đến tham quan!</em></p>'),
(101, 199, 'HÀ NỘI – MỘC CHÂU – BẮC YÊN (ĂN TRƯA/TỐI)', '<p><strong>S&aacute;ng:</strong>&nbsp;Xe &ocirc; t&ocirc; v&agrave; hướng dẫn vi&ecirc;n (HDV)<strong>&nbsp;Vietnam Booking</strong>&nbsp;đ&oacute;n qu&yacute; kh&aacute;ch tại Nh&agrave; H&aacute;t Lớn H&agrave; Nội&nbsp;khởi h&agrave;nh đi&nbsp;<strong>tour&nbsp;du lịch&nbsp;Mộc Ch&acirc;u T&agrave; X&ugrave;a 2 ng&agrave;y 1 đ&ecirc;m</strong>. Đo&agrave;n dừng ch&acirc;n d&ugrave;ng bữa s&aacute;ng tự t&uacute;c tại nh&agrave; h&agrave;ng địa phương.</p>\r\n\r\n<p>Tiếp tục h&agrave;nh tr&igrave;nh qu&yacute; kh&aacute;ch dừng ch&acirc;n ven đường tr&ecirc;n&nbsp;<strong>đ&egrave;o Thung Khe</strong>&nbsp;để&nbsp;chụp ảnh v&agrave; c&ugrave;ng đ&oacute;n những tia nắng đầu ti&ecirc;n của buổi b&igrave;nh minh nơi n&uacute;i rừng.</p>\r\n\r\n<p><strong>Trưa:&nbsp;</strong>Đo&agrave;n đến&nbsp;<strong>Mộc Ch&acirc;u</strong>&nbsp;d&ugrave;ng bữa trưa tại nh&agrave; h&agrave;ng với những m&oacute;n ăn đặc sắc nhất của v&ugrave;ng cao T&acirc;y Bắc.</p>\r\n\r\n<p>Qu&yacute; kh&aacute;ch khởi h&agrave;nh đi&nbsp;<a href=\"https://www.vietnambooking.com/du-lich/tour-du-lich/du-lich-moc-chau.html\"><strong>du lịch Mộc Ch&acirc;u</strong></a>&nbsp;tham quan:</p>\r\n\r\n<p>Xe dừng ch&acirc;n tại Mường Sang. Tại đ&acirc;y qu&yacute; kh&aacute;ch lựa chọn 1 trong 2 điểm đến sau:</p>\r\n\r\n<p><em><strong>Lựa chọn 1:</strong></em></p>\r\n\r\n<p><strong>Vườn hoa Happy land - Happy land:&nbsp;</strong>Sở hữu&nbsp;diện t&iacute;ch gần 5ha, du kh&aacute;ch đến đ&acirc;y sẽ được đ&oacute;n ch&agrave;o bởi những thảm hoa trồng từ đủ loại hoa kh&aacute;c nhau, trong đ&oacute; c&oacute; những lo&agrave;i hoa đặc trưng của v&ugrave;ng T&acirc;y Bắc.&nbsp;<em>(</em><em>Chương tr&igrave;nh th&aacute;ng 1</em><em>0,11,12).</em></p>\r\n\r\n<p><em><strong>Lựa chọn 2:</strong></em></p>\r\n\r\n<p><strong>Cầu k&iacute;nh Bạch Long:</strong>&nbsp;Chiếc cầu k&iacute;nh ngoạn mục được bắc qua hai ngọn n&uacute;i c&oacute; khoảng c&aacute;ch 285 m&eacute;t, rộng 2,4 m&eacute;t. Tại đ&acirc;y, qu&yacute; kh&aacute;ch sẽ được trải nghiệm đi tr&ecirc;n&nbsp;<strong>Glass Skywalk</strong>&nbsp;- Điểm tiếp nối cầu chạy d&agrave;i 370m tr&ecirc;n v&aacute;ch đ&aacute; (vực s&acirc;u 150m).</p>\r\n\r\n<p>Sau đ&oacute; đo&agrave;n tiếp tục&nbsp;khởi h&agrave;nh đi Bắc Y&ecirc;n, tr&ecirc;n qu&atilde;ng đường v&ocirc; c&ugrave;ng thơ mộng&nbsp;với vẻ đẹp s&ocirc;ng Đ&agrave; c&ugrave;ng c&aacute;c bản l&agrave;ng người Th&aacute;i chạy dọc s&ocirc;ng.&nbsp;</p>\r\n\r\n<p><strong>Tối:</strong>&nbsp;Qu&yacute; kh&aacute;ch d&ugrave;ng bữa v&agrave;&nbsp;nghỉ đ&ecirc;m tại Bắc Y&ecirc;n.&nbsp;</p>'),
(102, 199, 'BẮC YÊN – TÀ XÙA SĂN MÂY – HÀ NỘI (ĂN SÁNG/TRƯA)', '<p><strong>S&aacute;ng:</strong>&nbsp;Xe đưa đo&agrave;n đi ngắm b&igrave;nh minh tr&ecirc;n&nbsp;<strong>đỉnh n&uacute;i T&agrave; X&ugrave;a</strong>.&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Đo&agrave;n trải nghiệm kh&uacute;c đường ngoằn ngh&egrave;o, chinh phục đỉnh n&uacute;i T&agrave; X&ugrave;a ở độ cao 2.650m.</li>\r\n	<li>C&ugrave;ng ăn mừng v&agrave; chụp ảnh chiến thắng ngắm b&igrave;nh minh l&ecirc;n.</li>\r\n	<li><strong>Săn m&acirc;y</strong>&nbsp;tr&ecirc;n đỉnh n&uacute;i T&agrave; X&ugrave;a tận hưởng cảm gi&aacute;c như đứng giữa chốn thần ti&ecirc;n.</li>\r\n</ul>\r\n\r\n<p>Qu&yacute; kh&aacute;ch d&ugrave;ng bữa s&aacute;ng tại nh&agrave; h&agrave;ng địa phương.</p>\r\n\r\n<p>Đo&agrave;n tiếp tục h&agrave;nh tr&igrave;nh tham quan c&aacute;c điểm đến kh&aacute;c ở T&agrave; X&ugrave;a như:</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li><strong>Sống lưng Khủng Long:&nbsp;</strong>Nh&igrave;n từ tr&ecirc;n cao, sống lưng khủng long trở n&ecirc;n huyền ảo hơn khi l&agrave;n m&acirc;y nhẹ tr&ocirc;i l&atilde;ng đ&atilde;ng c&ugrave;ng những cơn gi&oacute; m&aacute;t lạnh.</li>\r\n	<li><strong>Đỉnh Gi&oacute;:&nbsp;</strong>Ngắm phong cảnh h&ugrave;ng vỹ nơi đ&acirc;y.</li>\r\n	<li><strong>Gh&eacute; Th&agrave;o Coffee:&nbsp;</strong>thưởng thức t&aacute;ch c&agrave; ph&ecirc; n&oacute;ng hổi (chi ph&iacute; đồ uống tự t&uacute;c).</li>\r\n	<li>Nếu c&ograve;n thời gian, hướng dẫn vi&ecirc;n sẽ hẹn giờ v&agrave; hướng dẫn Qu&yacute; kh&aacute;ch c&oacute; thể thu&ecirc; xe &ocirc;m để tự do đi thăm quan v&agrave; chụp h&igrave;nh tại: C&acirc;y C&ocirc; Đơn, Mỏm c&aacute; heo,...</li>\r\n</ul>\r\n\r\n<p><strong>Trưa:</strong>&nbsp;Đo&agrave;n&nbsp;l&agrave;m thủ tục trả ph&ograve;ng sau đ&oacute; d&ugrave;ng bữa trưa tại nh&agrave; h&agrave;ng.</p>\r\n\r\n<p>Sau bữa trưa qu&yacute; kh&aacute;ch l&ecirc;n xe về H&agrave; Nội. Tr&ecirc;n đường về qu&yacute; kh&aacute;ch d&ugrave;ng ch&acirc;n nghỉ ngơi, thưởng thức v&agrave; mua đặc sản Sữa Ba V&igrave;.</p>\r\n\r\n<p><strong>Tối:</strong>&nbsp;<strong>19h00&nbsp;</strong>đo&agrave;n về đến H&agrave; Nội, kết th&uacute;c lịch tr&igrave;nh&nbsp;<strong><em>tour Mộc Ch&acirc;u &ndash; T&agrave; X&ugrave;a 2 ng&agrave;y 1 đ&ecirc;m</em></strong>&nbsp;với nhiều điều mới lạ. Hướng dẫn vi&ecirc;n Vietnam Booking chia tay v&agrave; hẹn gặp lại qu&yacute; kh&aacute;ch trong những chương tr&igrave;nh&nbsp;<a href=\"https://www.vietnambooking.com/du-lich\"><strong>tour du lịch gi&aacute; rẻ</strong></a>&nbsp;tiếp theo.</p>\r\n\r\n<p><strong>LƯU &Yacute;</strong>: Thứ tự v&agrave; chi tiết trong chương tr&igrave;nh c&oacute; thể thay đổi cho ph&ugrave; hợp với t&igrave;nh h&igrave;nh thực tế, nhưng vẫn đảm bảo đủ điểm đến tham quan !</p>'),
(103, 200, 'TP.HCM - MỸ THO - BẾN TRE - CẦN THƠ (ĂN TRƯA/TỐI)', '<p>S&aacute;ng: Xe v&agrave; hướng dẫn vi&ecirc;n của Vietnam Booking đ&oacute;n qu&yacute; kh&aacute;ch tại 164 L&ecirc; Th&aacute;nh T&ocirc;n, Bến Th&agrave;nh, Quận 1. Sau đ&oacute;, chuyến h&agrave;nh tr&igrave;nh tour Mỹ Tho Cần Thơ 2 ng&agrave;y 1 đ&ecirc;m ch&iacute;nh thức bắt đầu. Xe sẽ đi theo lộ tr&igrave;nh đường cao tốc TP.HCM - Trung Lương.</p>\r\n\r\n<p>Sau khi đến Mỹ Tho, đo&agrave;n gh&eacute; qua trạm Mekong Rest Stop để d&ugrave;ng bữa s&aacute;ng (chi ph&iacute; tự t&uacute;c) v&agrave; chụp h&igrave;nh tập thể lưu niệm. Tiếp tục chuyến h&agrave;nh tr&igrave;nh, xe sẽ gh&eacute; chi&ecirc;m b&aacute;i Ch&ugrave;a Vĩnh Tr&agrave;ng.&nbsp;</p>\r\n\r\n<p>Ch&ugrave;a Vĩnh Tr&agrave;ng: Nơi đ&acirc;y được xem kiệt t&aacute;c kiến tr&uacute;c độc đ&aacute;o, được khởi dựng từ thế kỷ XIX bởi đ&ocirc;i vợ chồng B&ugrave;i C&ocirc;ng Đạt. Đến nay, c&ocirc;ng tr&igrave;nh n&agrave;y kh&ocirc;ng chỉ l&agrave; một địa điểm h&agrave;nh hương t&acirc;m linh m&agrave; c&ograve;n l&agrave; minh chứng sống động cho sự giao thoa tinh hoa nghệ thuật &Aacute; - &Acirc;u.</p>\r\n\r\n<p>Sau đ&oacute;, đo&agrave;n sẽ tiếp tục h&agrave;nh tr&igrave;nh đến bến t&agrave;u 30/4. Tại đ&acirc;y, to&agrave;n thể qu&yacute; kh&aacute;ch sẽ l&ecirc;n t&agrave;u để bắt đầu kh&aacute;m ph&aacute; vẻ đẹp n&ecirc;n thơ của miền s&ocirc;ng nước. Chuyến h&agrave;nh tr&igrave;nh tour Mỹ Tho Cần Thơ 2 ng&agrave;y 1 đ&ecirc;m kh&aacute;m ph&aacute; miền T&acirc;y sẽ đưa qu&yacute; kh&aacute;ch đến những địa điểm hấp dẫn sau:</p>\r\n\r\n<p>Tham quan C&ugrave; Lao Thới Sơn (Cồn L&acirc;n): Qu&yacute; kh&aacute;ch c&oacute; thể trải nghiệm việc tự tay h&aacute;i tr&aacute;i c&acirc;y, thưởng thức c&aacute;c loại quả theo m&ugrave;a (chi ph&iacute; tự t&uacute;c) v&agrave; c&ograve;n c&oacute; cơ hội lắng nghe đờn ca t&agrave;i tử Nam Bộ.<br />\r\nGh&eacute; thăm trại nu&ocirc;i ong mật: Tại đ&acirc;y, qu&yacute; kh&aacute;ch được trải nghiệm hương vị tr&agrave; mật ong chanh đặc trưng của miền T&acirc;y v&agrave; kh&aacute;m ph&aacute; c&aacute;c con rạch nhỏ bằng xuồng, ngắm nh&igrave;n khung cảnh thi&ecirc;n nhi&ecirc;n hữu t&igrave;nh của v&ugrave;ng s&ocirc;ng nước.<br />\r\nĐo&agrave;n tiếp tục h&agrave;nh tr&igrave;nh bằng thuyền lớn, đến Bến Tre - &quot;Đảo dừa Việt Nam&quot; v&agrave; tham quan c&aacute;c cơ sở sản xuất kẹo dừa, b&aacute;nh tr&aacute;ng ở T&acirc;n Thạch.<br />\r\nSau đ&oacute;, xe ngựa đưa du kh&aacute;ch kh&aacute;m ph&aacute; l&agrave;ng x&atilde; T&acirc;n Thạch, chi&ecirc;m ngưỡng vườn c&acirc;y tr&aacute;i v&agrave; sinh hoạt thường ng&agrave;y của người d&acirc;n địa phương.<br />\r\n​Trưa: Qu&yacute; kh&aacute;ch sẽ được thưởng thức bữa trưa với c&aacute;c m&oacute;n đặc sản s&ocirc;ng nước tại khu du lịch sinh th&aacute;i miệt vườn.</p>\r\n\r\n<p>Chiều: Sau bữa trưa, đo&agrave;n tiếp tục chương tr&igrave;nh tour Mỹ Tho Bến Tre Cần Thơ 2 ng&agrave;y 1 đ&ecirc;m với những hoạt động sau:</p>\r\n\r\n<p>Tham quan trại nu&ocirc;i c&aacute; sấu, kh&aacute;m ph&aacute; l&agrave;ng nghề thủ c&ocirc;ng mỹ nghệ dừa, chi&ecirc;m ngưỡng hồ ếch, hồ nh&iacute;m, vườn cacao v&agrave; trải nghiệm cảm gi&aacute;c mạnh khi đi cầu khỉ.<br />\r\nTham gia một số tr&ograve; chơi d&acirc;n gian (miễn ph&iacute;) như: nhảy bao bố, đi c&agrave; kheo, k&eacute;o co, đi d&acirc;y, bơi xuồng trong hồ, chạy xe đạp qua mương,...<br />\r\nTrải nghiệm nằm v&otilde;ng thư gi&atilde;n v&agrave; nghỉ ngơi tại khu du lịch.<br />\r\nĐo&agrave;n trở lại thuyền về lại Mỹ Tho để khởi h&agrave;nh đi Cần Thơ. Đến th&agrave;nh phố Cần Thơ, đo&agrave;n nhận ph&ograve;ng kh&aacute;ch sạn nghỉ ngơi.</p>\r\n\r\n<p>Tối: Đo&agrave;n đến bến Ninh Kiều, qu&yacute; kh&aacute;ch sẽ c&oacute; cơ hội trải nghiệm một bữa tối thượng hạng tr&ecirc;n du thuyền 5 sao, thưởng thức những đặc sản s&ocirc;ng nước miền T&acirc;y giữa kh&ocirc;ng gian &acirc;m nhạc truyền thống.</p>\r\n\r\n<p>Sau khi t&agrave;u cập bến, xe đưa đo&agrave;n trở về kh&aacute;ch sạn để nhận ph&ograve;ng v&agrave; nghỉ ngơi.</p>'),
(104, 200, 'CẦN THƠ - CHỢ NỔI CÁI RĂNG - KHU DU LỊCH MỸ KHÁNH - CĂN NHÀ MÀU TÍM - TP.HCM (ĂN SÁNG/TRƯA)', '<p><strong>S&aacute;ng:</strong>&nbsp;Sau khi d&ugrave;ng bữa s&aacute;ng tại kh&aacute;ch sạn, đo&agrave;n sẽ di chuyển đến bến Ninh Kiều để tham gia chuyến du ngoạn s&ocirc;ng Cần Thơ, gh&eacute; thăm chợ Cần Thơ, cảng c&aacute;, cầu Quang Trung v&agrave; chợ nổi C&aacute;i Răng. Lịch tr&igrave;nh&nbsp;<strong>tour Mỹ Tho Bến Tre Cần Thơ 2N1Đ&nbsp;</strong>đưa qu&yacute; kh&aacute;ch tham quan:</p>\r\n\r\n<ul>\r\n	<li>Kh&aacute;m ph&aacute; l&agrave;ng nghề hủ tiếu.</li>\r\n	<li><strong>Khu du lịch Mỹ Kh&aacute;nh:&nbsp;</strong>qu&yacute; kh&aacute;ch c&oacute; thể kh&aacute;m ph&aacute; vườn tr&aacute;i c&acirc;y, vườn hoa, vườn th&uacute;, tận hưởng những m&agrave;n tr&igrave;nh diễn độc đ&aacute;o của động vật v&agrave; tham gia c&aacute;c tr&ograve; chơi d&acirc;n gian hấp dẫn.</li>\r\n	<li>T&igrave;m hiểu về kiến tr&uacute;c độc đ&aacute;o của&nbsp;<strong>Thiền viện Tr&uacute;c L&acirc;m Phương Nam</strong>.</li>\r\n</ul>\r\n\r\n<p><strong>Trưa:</strong>&nbsp;Q&uacute;y kh&aacute;ch d&ugrave;ng&nbsp;cơm trưa tại&nbsp;nh&agrave; h&agrave;ng của&nbsp;<strong>KDL Mỹ Kh&aacute;nh</strong>. Sau bữa trưa, qu&yacute; kh&aacute;ch được&nbsp;nghỉ ngơi tự do.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Chiều:</strong>&nbsp;Đo&agrave;n l&ecirc;n đường tiếp tục&nbsp;khởi h&agrave;nh tham quan:</p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><strong>Coffee - phim trường Căn nh&agrave; m&agrave;u t&iacute;m Cần Thơ</strong>&nbsp;<em>(v&eacute; tự t&uacute;c):</em><strong>&nbsp;</strong>Đ&acirc;y l&agrave; một trong những điểm đến du lịch đang l&agrave;m mưa l&agrave;m gi&oacute; tr&ecirc;n mạng x&atilde; hội gần đ&acirc;y ở miền T&acirc;y. Nơi n&agrave;y thu h&uacute;t du kh&aacute;ch bởi kiến tr&uacute;c độc đ&aacute;o, mới lạ với sắc t&iacute;m l&atilde;ng mạn.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p>Đo&agrave;n tạm biệt th&agrave;nh phố Cần Thơ để trở về th&agrave;nh phố Hồ Ch&iacute; Minh. Khi về đến nơi, xe đưa đ&oacute;n kh&aacute;ch tận t&igrave;nh tại điểm hẹn. Chuyến h&agrave;nh tr&igrave;nh&nbsp;<strong>tour Mỹ Tho Bến Tre Cần Thơ 2 ng&agrave;y 1 đ&ecirc;m</strong>&nbsp;đ&atilde; kh&eacute;p lại một c&aacute;ch trọn vẹn. Thay mặt c&ocirc;ng ty, HDV xin gửi lời cảm ơn ch&acirc;n th&agrave;nh đến qu&yacute; kh&aacute;ch v&agrave; hẹn gặp lại trong những&nbsp;<a href=\"https://www.vietnambooking.com/du-lich\" target=\"_blank\"><strong>tour du lịch gi&aacute; rẻ</strong></a>&nbsp;sắp tới.</p>\r\n\r\n<p><strong><em>Lưu &yacute;:&nbsp;Thứ tự v&agrave; chi tiết trong chương tr&igrave;nh c&oacute; thể thay đổi cho ph&ugrave; hợp với t&igrave;nh h&igrave;nh thực tế, nhưng vẫn đảm bảo đủ điểm đến tham quan!</em></strong></p>'),
(105, 201, 'HÀ NỘI - KHAU PHẠ - MÙ CANG CHẢI (ĂN TRƯA / TỐI)', '<p><strong>S&aacute;ng:</strong>&nbsp;Xe v&agrave; Hướng Dẫn Vi&ecirc;n Vietnam Booking đ&oacute;n Qu&yacute; kh&aacute;ch tại Nh&agrave; h&aacute;t Lớn H&agrave; Nội bắt đầu chuyến đi&nbsp;<strong>tour&nbsp;M&ugrave; Cang Chải 2 ng&agrave;y 1 đ&ecirc;m</strong>. Dọc đường, qu&yacute; kh&aacute;ch dừng ch&acirc;n tại&nbsp;<strong>đồi ch&egrave; Thanh Sơn</strong>&nbsp;để&nbsp;chụp ảnh.</p>\r\n\r\n<p><strong>Trưa:</strong>&nbsp;Đến thị x&atilde; Nghĩa Lộ, đo&agrave;n d&ugrave;ng bữa&nbsp;trưa. Tiếp tục&nbsp;<strong>tour M&ugrave; Căng Chải 2 Ng&agrave;y</strong>, đo&agrave;n l&ecirc;n xe đi T&uacute; Lệ.&nbsp;Tr&ecirc;n xe, qu&yacute; kh&aacute;ch sẽ c&oacute; cơ hội chi&ecirc;m ngưỡng vẻ đẹp của&nbsp;<strong>c&aacute;nh đồng Mường L&ograve;</strong>&nbsp;trải d&agrave;i bất tận.</p>\r\n\r\n<p><strong>Chiều:</strong>&nbsp;Đo&agrave;n chinh phục đ&egrave;o&nbsp;<strong>Khau Phạ</strong>&nbsp;- một trong tứ đại đỉnh đ&egrave;o&nbsp;nổi tiếng của&nbsp;<strong><a href=\"https://www.vietnambooking.com/du-lich/tour-du-lich/du-lich-mien-bac.html\" target=\"_blank\">du lịch&nbsp;miền Bắc</a></strong>. Tại đ&acirc;y, đo&agrave;n tham quan chi&ecirc;m ngưỡng to&agrave;n bộ khu vực&nbsp;<strong>thung lũng Khau Phạ, bản L&igrave;m Th&aacute;i, L&igrave;m M&ocirc;ng&nbsp;</strong>v&agrave; c&oacute; cơ hội chứng kiến<strong>&nbsp;</strong>c&aacute;c vận động vi&ecirc;n chuy&ecirc;n nghiệp nhảy d&ugrave;<em>&nbsp;(v&agrave;o th&aacute;ng 9 h&agrave;ng năm)</em>.</p>\r\n\r\n<p>Tiếp tục h&agrave;nh tr&igrave;nh, qu&yacute; kh&aacute;ch tự t&uacute;c thu&ecirc; xe &ocirc;m do ch&iacute;nh những người H&#39;m&ocirc;ng bản địa nơi đ&acirc;y điều khiển để&nbsp;đến với&nbsp;<strong>ruộng bậc thang M&acirc;m X&ocirc;i</strong>&nbsp;-&nbsp;biểu tượng đặc trưng nhất l&agrave;m n&ecirc;n t&ecirc;n tuổi cho ruộng bậc thang M&ugrave; Cang Chải.</p>\r\n\r\n<p><strong>Tối:</strong>&nbsp;Xe đưa Qu&yacute; kh&aacute;ch di chuyển về thị trấn M&ugrave; Cang Chải nhận ph&ograve;ng kh&aacute;ch sạn, nghỉ ngơi. Sau đ&oacute; đo&agrave;n ăn&nbsp;tối v&agrave;&nbsp;tự do vui chơi.</p>\r\n\r\n<p>Nghỉ đ&ecirc;m tại M&ugrave; Cang Chải.</p>'),
(106, 201, 'MÙ CANG CHẢI - TÚ LỆ - HÀ NỘI (ĂN SÁNG / TRƯA)', '<p><strong>S&aacute;ng:</strong>&nbsp;Qu&yacute; kh&aacute;ch trả ph&ograve;ng kh&aacute;ch sạn, ăn s&aacute;ng tại nh&agrave; h&agrave;ng. Sau đ&oacute;, đo&agrave;n sẽ c&oacute; thời gian tự do thu&ecirc;&nbsp;xe m&aacute;y hoặc ngồi sau xe &ocirc;m để&nbsp;kh&aacute;m ph&aacute; M&ugrave; Cang Chải xinh đf&nbsp; ẹp. Du kh&aacute;ch c&oacute; thể chọn một v&agrave;i trong những&nbsp;điểm gợi &yacute; sau:</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li><strong>Chợ M&ugrave; Cang Chải:</strong>&nbsp;Nơi c&aacute;c đồng b&agrave;o trao đổi mua b&aacute;n sản vật tấp nập.&nbsp;Qu&yacute; kh&aacute;ch c&oacute; thể t&igrave;m thấy ở đ&acirc;y những sản vật đặc trưng như: gạo nếp, t&aacute;o m&egrave;o, c&aacute;c loại rau củ quả tươi sạch, v&agrave; đặc biệt l&agrave; c&aacute;c sản phẩm thủ c&ocirc;ng mỹ nghệ của người Th&aacute;i.</li>\r\n	<li><strong>Bản Kim Nọi:</strong>&nbsp;Một bản nhỏ b&igrave;nh y&ecirc;n đầy thơ mộng của người d&acirc;n tộc Th&aacute;i.</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><strong>Rừng tr&uacute;c:&nbsp;</strong>Với những h&agrave;ng tr&uacute;c xanh mướt, thẳng tắp, rừng tr&uacute;c M&ugrave; Cang Chải l&agrave; điểm đến l&yacute; tưởng để qu&yacute; kh&aacute;ch&nbsp;thư gi&atilde;n, tản bộ v&agrave; tận hưởng kh&ocirc;ng kh&iacute; trong l&agrave;nh.</li>\r\n	<li><strong>Ruộng bậc thang h&igrave;nh m&oacute;ng ngựa</strong>:&nbsp;Kiệt t&aacute;c nghệ thuật của người d&acirc;n địa phương, tạo n&ecirc;n một khung cảnh h&ugrave;ng vĩ, độc đ&aacute;o. V&agrave;o m&ugrave;a l&uacute;a ch&iacute;n, ruộng bậc thang kho&aacute;c l&ecirc;n m&igrave;nh một tấm &aacute;o v&agrave;ng &oacute;ng, tạo n&ecirc;n một khung cảnh v&ocirc; c&ugrave;ng ấn tượng.</li>\r\n</ul>\r\n\r\n<p>Tiếp tục&nbsp;<strong>tour&nbsp;M&ugrave; Cang Chải 2 ng&agrave;y 1 đ&ecirc;m</strong>,&nbsp;xe đưa Qu&yacute; kh&aacute;ch tới khu vực Cầu Ba Nh&agrave;, chụp h&igrave;nh tự do với&nbsp;<strong>ruộng bậc thang h&igrave;nh mũi gi&agrave;y</strong>.</p>\r\n\r\n<p>Sai đ&oacute;, hướng dẫn vi&ecirc;n đưa đo&agrave;n&nbsp;đi tham quan&nbsp;<strong>chợ T&uacute; Lệ</strong>, t&igrave;m hiểu về văn h&oacute;a địa phương v&agrave; mua sắm c&aacute;c sản phẩm thủ c&ocirc;ng mỹ nghệ, đặc biệt l&agrave;&nbsp;<strong>cốm xanh</strong>&nbsp;nổi tiếng.</p>\r\n\r\n<p><strong>Trưa:</strong>&nbsp;Thưởng thức bữa trưa tại Nghĩa Lộ.</p>\r\n\r\n<p><strong>Chiều:</strong>&nbsp;Đo&agrave;n sẽ khởi h&agrave;nh về H&agrave; Nội. Kh&aacute;ch đi s&acirc;n bay Nội B&agrave;i sẽ được đưa đi theo tuyến đường Ng&atilde; 3 Ba Khe - Cao tốc L&agrave;o Cai - Nội B&agrave;i.</p>\r\n\r\n<p><strong>Tối:</strong>&nbsp;Về đến H&agrave; Nội, kết th&uacute;c chương tr&igrave;nh&nbsp;<strong>tour M&ugrave; Cang Chải&nbsp;2 Ng&agrave;y 1 Đ&ecirc;m</strong>&nbsp;đ&aacute;ng nhớ. HDV chia tay v&agrave; hẹn gặp lại Qu&yacute; kh&aacute;ch trong&nbsp;<strong><a href=\"https://www.vietnambooking.com/du-lich\" target=\"_blank\">c&aacute;c tour du lịch</a>&nbsp;</strong>gi&aacute; rẻ&nbsp;tiếp theo.</p>\r\n\r\n<p><em><strong>LƯU &Yacute;:</strong>&nbsp;Thứ tự v&agrave; chi tiết trong chương tr&igrave;nh c&oacute; thể thay đổi cho ph&ugrave; hợp với t&igrave;nh h&igrave;nh thực tế, nhưng vẫn đảm bảo đủ điểm đến tham quan!</em></p>'),
(107, 203, 'TP HỒ CHÍ MINH - HÀ NỘI - SAPA (ĂN TRƯA, TỐI)', '<p><strong>S&aacute;ng</strong>: Nh&acirc;n vi&ecirc;n của&nbsp;<strong><a href=\"https://www.vietnambooking.com/\" target=\"_blank\">Vietnam Booking</a>&nbsp;</strong>đ&oacute;n đo&agrave;n tại s&acirc;n bay T&acirc;n Sơn Nhất l&agrave;m thủ tục bay đi H&agrave; Nội, tr&ecirc;n chuyến bay dự kiến l&uacute;c 05h00.</p>\r\n\r\n<p>Đến s&acirc;n bay Nội B&agrave;i, xe khởi h&agrave;nh đưa đo&agrave;n đi Sapa.</p>\r\n\r\n<p><strong>Trưa</strong>: Đo&agrave;n d&ugrave;ng cơm trưa tại L&agrave;o Cai.</p>\r\n\r\n<p><strong>Chiều:</strong>&nbsp;Qu&yacute; kh&aacute;ch tự do tham quan v&agrave; chụp h&igrave;nh tại:</p>\r\n\r\n<ul>\r\n	<li><strong>Cột mốc Hữu Nghị&nbsp;Việt Nam &ndash; Trung Quốc</strong>&nbsp;được khởi c&ocirc;ng năm 2001 v&agrave; kh&aacute;nh th&agrave;nh năm 2002. Cột mốc khối lượng 1 tấn, đ&aacute;nh dấu một vị tr&iacute; lịch sử m&agrave; cha &ocirc;ng ta đ&atilde; hy sinh xương m&aacute;u trong suốt từ thế kỷ 19 đến nay để giữ g&igrave;n cho thế hệ mai sau.</li>\r\n	<li>Mua sắm đồ lưu niệm v&agrave; điện tử tại&nbsp;<strong>chợ Cốc Lếu L&agrave;o Cai&nbsp;</strong>(nếu c&ograve;n thời gian). Đ&acirc;y l&agrave; chợ lớn nhất của tỉnh L&agrave;o Cai cũng như c&aacute;c tỉnh T&acirc;y Bắc n&oacute;i chung, v&igrave; chợ Cốc Lếu nhập h&agrave;ng h&oacute;a trực tiếp tại cửa khẩu n&ecirc;n h&agrave;ng h&oacute;a tại đ&acirc;y cực kỳ đa dạng&hellip;</li>\r\n</ul>\r\n\r\n<p>Tiếp đ&oacute;, đo&agrave;n khởi h&agrave;nh đi Sapa nhận ph&ograve;ng nghỉ ngơi.</p>\r\n\r\n<p><strong>Tối:</strong>&nbsp;Qu&yacute; kh&aacute;ch d&ugrave;ng bữa tối tại nh&agrave; h&agrave;ng, sau đ&oacute; dạo phố Sapa, tận hưởng kh&ocirc;ng gian như một g&oacute;c ch&acirc;u &Acirc;u với h&agrave;ng th&ocirc;ng cổ thụ v&agrave; những ng&ocirc;i nh&agrave; lợp gỗ Pơ mu.</p>'),
(108, 203, 'SAPA - FANSIPAN - BẢN CÁT CÁT (ĂN SÁNG/TRƯA)', '<p><strong>S&aacute;ng</strong>: Đo&agrave;n d&ugrave;ng điểm t&acirc;m s&aacute;ng tại kh&aacute;ch sạn. Sau đ&oacute; khởi h&agrave;nh chinh phục:</p>\r\n\r\n<ul>\r\n	<li>\r\n	<p><strong>Đỉnh Fansipan &ndash; N&oacute;c nh&agrave; Đ&ocirc;ng Dương</strong>&nbsp;cao 3.143m. Qu&yacute; kh&aacute;ch chụp ảnh tại vườn hoa trung t&acirc;m, sau đ&oacute; đi c&aacute;p treo 3 d&acirc;y d&agrave;i nhất thế giới (6.292,5m) l&ecirc;n độ cao 2.900m&nbsp;(chi ph&iacute; tự t&uacute;c). Đo&agrave;n tiếp tục b&aacute;ch bộ đến&nbsp;<strong>Thanh V&acirc;n Đắc Lộ</strong>&nbsp;v&agrave;&nbsp;<strong>B&iacute;ch V&acirc;n Thiền Tự</strong>, d&acirc;ng hương ở độ cao 3.037m.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p><strong>Trưa</strong>:&nbsp;Đo&agrave;n di chuyển xuống ga c&aacute;p treo, xe v&agrave; HDV đưa đo&agrave;n đến nh&agrave; h&agrave;ng d&ugrave;ng cơm trưa.</p>\r\n\r\n<p><strong>Chiều:&nbsp;Tour du lịch H&agrave; Nội Sapa Hạ Long Ninh B&igrave;nh</strong>&nbsp;5N4Đ sẽ tiếp tục diễn ra với những điểm tham quan:</p>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Tham quan&nbsp;<strong>Bản C&aacute;t C&aacute;t - S&iacute;n Chải</strong>&nbsp;của người H&rsquo;M&ocirc;ng. Qu&yacute; kh&aacute;ch kh&aacute;m ph&aacute; phong tục tập qu&aacute;n giản dị, đậm đ&agrave; bản sắc d&acirc;n tộc. Bản hấp dẫn với cảnh sắc hoang sơ, trữ t&igrave;nh c&ugrave;ng văn h&oacute;a đặc sắc.</p>\r\n	</li>\r\n	<li>\r\n	<p>Gh&eacute; thăm&nbsp;<strong>Th&aacute;c C&aacute;t C&aacute;t</strong>&nbsp;v&agrave;&nbsp;<strong>Nh&agrave; M&aacute;y Thủy Điện</strong>&nbsp;do người Ph&aacute;p x&acirc;y dựng từ đầu thế kỷ XX, chụp ảnh v&agrave; vui chơi.</p>\r\n	</li>\r\n	<li>\r\n	<p><strong>Nh&agrave; thờ Đ&aacute;&nbsp;</strong>-&nbsp;dấu ấn kiến tr&uacute;c Ph&aacute;p nguy&ecirc;n vẹn nhất ở Sapa, nằm tại trung t&acirc;m thị trấn. Đ&acirc;y l&agrave; nơi người d&acirc;n tộc tập trung bu&ocirc;n b&aacute;n hằng ng&agrave;y v&agrave; l&agrave; điểm diễn ra&nbsp;<strong>Chợ T&igrave;nh</strong>&nbsp;v&agrave;o tối thứ 7, với tiếng kh&egrave;n gọi bạn của những đ&ocirc;i trai g&aacute;i, đậm chất văn h&oacute;a T&acirc;y Bắc.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p><strong>Tối:</strong>&nbsp;Đo&agrave;n tự do ăn tối thưởng thức ẩm thực v&ugrave;ng cao tại chợ đ&ecirc;m Sapa. Đo&agrave;n nghỉ đ&ecirc;m tại Sapa.&nbsp;</p>'),
(109, 203, 'SAPA - HÀ NỘI (ĂN SÁNG/TRƯA/TỐI)', '<p><strong>S&aacute;ng:</strong>&nbsp;Sau bữa s&aacute;ng, qu&yacute; kh&aacute;ch l&ecirc;n xe khởi h&agrave;nh đi H&agrave; Nội.</p>\r\n\r\n<p><strong>Trưa:</strong>&nbsp;Đo&agrave;n d&ugrave;ng cơm trưa tại&nbsp;<strong>Ph&uacute; Thọ</strong>.</p>\r\n\r\n<p><strong>Chiều:</strong>&nbsp;Xe đưa đo&agrave;n về đến H&agrave; Nội, Qu&yacute; kh&aacute;ch tham quan:</p>\r\n\r\n<ul>\r\n	<li><strong>Ch&ugrave;a Trấn Quốc:</strong>&nbsp;Ng&ocirc;i ch&ugrave;a Trấn Bắc cổ k&iacute;nh nhất Việt Nam với 1.500 năm tuổi nằm tr&ecirc;n b&aacute;n đảo cồn Quy linh thi&ecirc;ng với truyền thuyết v&agrave; huyền thoại về Hồ T&acirc;y, hồ Tr&uacute;c Bạch.</li>\r\n	<li><strong>Đền Ngọc Sơn, Cầu Th&ecirc; H&uacute;c, hồ Ho&agrave;n Kiếm</strong>: Trực tiếp chứng kiến cụ R&ugrave;a d&agrave;i 2,1m, ngang 1,2m được trưng b&agrave;y tại đền Ngọc Sơn.&nbsp;</li>\r\n</ul>\r\n\r\n<p>Kế tiếp, đo&agrave;n tham quan Cụm di t&iacute;ch lịch sử Phủ Chủ Tịch. Với c&aacute;c quần thể kiến tr&uacute;c:</p>\r\n\r\n<ul>\r\n	<li><strong>Quảng trường Ba Đ&igrave;nh</strong>:&nbsp;di t&iacute;ch lịch sử đặc biệt, l&agrave; nơi gắn liền với những dấu mốc quan trọng của d&acirc;n tộc Việt Nam. Tại đ&acirc;y, Chủ tịch Hồ Ch&iacute; Minh đ&atilde; đọc bản Tuy&ecirc;n ng&ocirc;n Độc lập, khai sinh nước Việt Nam, ghi dấu chiến thắng sau nhiều năm đấu tranh chống &aacute;ch đ&ocirc; hộ.</li>\r\n	<li><strong>Bảo t&agrave;ng Hồ Ch&iacute; Minh</strong>:&nbsp;Nằm trong khu vực c&oacute; nhiều di t&iacute;ch như: Lăng Chủ tịch Hồ Ch&iacute; Minh, Khu di t&iacute;ch Phủ chủ tịch, Ch&ugrave;a Một Cột...&nbsp;</li>\r\n	<li><strong>Nh&agrave; S&agrave;n</strong>&nbsp;giản dị nằm giữa thủ đ&ocirc; H&agrave; Nội l&agrave; nơi B&aacute;c Hồ đ&atilde; sống v&agrave; l&agrave;m việc l&acirc;u nhất trong cuộc đời hoạt động c&aacute;ch mạng của m&igrave;nh.</li>\r\n	<li><strong>Ch&ugrave;a Một Cột (Ch&ugrave;a Mật)</strong>:&nbsp;ng&ocirc;i ch&ugrave;a c&oacute; kiến tr&uacute;c độc đ&aacute;o, chỉ c&oacute; 1 gian dựng tr&ecirc;n 1 cột đ&aacute; nằm giữa l&ograve;ng hồ Linh Chiểu...</li>\r\n</ul>\r\n\r\n<p><strong>Tối</strong>: Qu&yacute; kh&aacute;ch tự t&uacute;c thưởng thức ẩm thực H&agrave; Nội. Đo&agrave;n nghỉ đ&ecirc;m tại thủ đ&ocirc;.</p>'),
(110, 203, 'HÀ NỘI - TRÀNG AN - HẠ LONG (ĂN SÁNG/TRƯA/TỐI)', '<p><strong>S&aacute;ng</strong>: Qu&yacute; kh&aacute;ch d&ugrave;ng điểm t&acirc;m s&aacute;ng v&agrave; trả ph&ograve;ng kh&aacute;ch sạn. Xe v&agrave; Hướng dẫn vi&ecirc;n đưa đo&agrave;n viếng<strong>&nbsp;Lăng Chủ Tịch</strong>.</p>\r\n\r\n<p>Đo&agrave;n tiếp tục h&agrave;nh tr&igrave;nh đến&nbsp;<strong>Ninh B&igrave;nh</strong>, thăm&nbsp;<strong>ch&ugrave;a B&aacute;i Đ&iacute;nh</strong>&nbsp;&ndash; ng&ocirc;i ch&ugrave;a lớn nhất Việt Nam, nổi bật với 500 pho tượng La H&aacute;n v&agrave; tượng Phật đồng lớn nhất cả nước&nbsp;(chi ph&iacute; xe điện tự t&uacute;c).</p>\r\n\r\n<p><strong>Trưa</strong>: D&ugrave;ng cơm trưa với đặc sản Cơm Ch&aacute;y - D&ecirc; n&uacute;i của Ninh B&igrave;nh.</p>\r\n\r\n<p><strong>Chiều:</strong>&nbsp;<strong>Tour du lịch H&agrave; Nội Sapa Hạ Long Ninh B&igrave;nh</strong>&nbsp;tiếp tục diễn ra với những điểm:</p>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Qu&yacute; kh&aacute;ch tham quan&nbsp;<strong>Quần thể Danh thắng Tr&agrave;ng An</strong>&nbsp;&ndash; Di sản văn h&oacute;a v&agrave; thi&ecirc;n nhi&ecirc;n thế giới được UNESCO c&ocirc;ng nhận, đ&aacute;nh dấu Việt Nam trong danh s&aacute;ch 30 di sản hỗn hợp Thế Giới. Tr&ecirc;n thuyền xu&ocirc;i d&ograve;ng S&agrave;o Kh&ecirc;, qu&yacute; kh&aacute;ch chi&ecirc;m ngưỡng cảnh sắc huyền ảo với n&uacute;i đ&aacute; v&ocirc;i, thung lũng, s&ocirc;ng ng&ograve;i đan xen v&agrave; kh&aacute;m ph&aacute;&nbsp;<strong>Hang S&aacute;ng, Hang Tối, Hang Nấu Rượu</strong>...</p>\r\n	</li>\r\n	<li>\r\n	<p>Rời Ninh B&igrave;nh, qu&yacute; kh&aacute;ch theo Quốc lộ 10 đến Hạ Long, chi&ecirc;m ngưỡng vẻ đẹp thanh b&igrave;nh của l&agrave;ng qu&ecirc; Đồng Bằng&nbsp;Bắc Bộ qua c&aacute;c tỉnh Nam Định, Th&aacute;i B&igrave;nh v&agrave; Hải Ph&ograve;ng.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p><strong>Tối</strong>:&nbsp;Qu&yacute; kh&aacute;ch tự do dạo chơi&nbsp;<strong>C&ocirc;ng vi&ecirc;n Ho&agrave;ng Gia dọc biển B&atilde;i Ch&aacute;y</strong>, đi mua sắm h&agrave;ng h&oacute;a tại khu&nbsp;<strong>Chợ Đ&ecirc;m Hạ Long</strong>&nbsp;l&ecirc;n cầu B&atilde;i Ch&aacute;y &ndash; c&acirc;y cầu d&acirc;y văng d&agrave;i nhất Việt Nam ngắm Hạ Long về đ&ecirc;m. &nbsp;Nghỉ đ&ecirc;m tại Hạ Long.</p>'),
(111, 203, 'HẠ LONG - SÂN BAY NỘI BÀI - TPHCM (ĂN SÁNG/TRƯA)', '<p><strong>S&aacute;ng</strong>:&nbsp;Qu&yacute; kh&aacute;ch ăn s&aacute;ng v&agrave; trả ph&ograve;ng kh&aacute;ch sạn.</p>\r\n\r\n<p>Đo&agrave;n đến bến thuyền Tuần Ch&acirc;u, bắt đầu h&agrave;nh tr&igrave;nh kh&aacute;m ph&aacute;&nbsp;<strong>Di sản thi&ecirc;n nhi&ecirc;n Thế giới tại Việt Nam</strong>&nbsp;với c&aacute;c danh thắng nổi tiếng:</p>\r\n\r\n<ul>\r\n	<li><strong>Động Thi&ecirc;n Cung:&nbsp;</strong>nằm tr&ecirc;n đảo Đầu Gỗ, c&aacute;ch cảng t&agrave;u 4km, ở độ cao 25m so với mực nước biển. Hang động rộng gần 10.000m&sup2; với&nbsp;cấu tr&uacute;c phức tạp, v&aacute;ch đ&aacute; cao v&agrave; những khối nhũ, măng đ&aacute; mang h&igrave;nh th&ugrave; kỳ lạ.</li>\r\n	<li><strong>L&agrave;ng Ch&agrave;i:&nbsp;</strong>ngắm&nbsp;vẻ đẹp của những ghe thuyền, những chiếc mủng tre đậu trước cửa nh&agrave;, những ng&ocirc;i nh&agrave; k&egrave;o cột v&agrave;o nhau chống gi&ocirc;ng b&atilde;o.</li>\r\n</ul>\r\n\r\n<p><strong>Trưa:</strong>&nbsp;Đo&agrave;n ăn trưa tr&ecirc;n du thuyền, sau đ&oacute; tiếp tục tham quan:</p>\r\n\r\n<ul>\r\n	<li>Ngắm&nbsp;<strong>h&ograve;n Đỉnh Hương</strong>&nbsp;khổng lồ giữa biển khơi, biểu tượng thi&ecirc;ng li&ecirc;ng của Hạ Long, như chiếc lư hương d&acirc;ng đất trời, thể hiện l&ograve;ng biết ơn tổ ti&ecirc;n v&agrave; mẹ Rồng trong truyền thuyết.</li>\r\n	<li>Ngắm&nbsp;<strong>h&ograve;n R&ugrave;a, h&ograve;n Ch&oacute; Đ&aacute;, h&ograve;n&nbsp;G&agrave; Chọi.</strong></li>\r\n</ul>\r\n\r\n<p><strong>Chiều:</strong>&nbsp;T&agrave;u về cập bến, xe đưa đo&agrave;n khởi h&agrave;nh về lại H&agrave; Nội.&nbsp;</p>\r\n\r\n<p><strong>Tối</strong>: Về đến H&agrave; Nội,&nbsp;<strong>tour du lịch H&agrave; Nội Sapa Hạ Long Ninh B&igrave;nh</strong>&nbsp;đến đ&acirc;y l&agrave; kết th&uacute;c. HDV gửi lời ch&agrave;o tạm biệt v&agrave; hẹn gặp lại qu&yacute; kh&aacute;ch trong những&nbsp;<strong><a href=\"https://vietnambooking.com/du-lich\" target=\"_blank\">tour du lịch gi&aacute; rẻ</a></strong>&nbsp;tiếp theo.</p>\r\n\r\n<p><em>LƯU &Yacute;: Thứ tự v&agrave; chi tiết trong chương tr&igrave;nh c&oacute; thể thay đổi cho ph&ugrave; hợp với t&igrave;nh h&igrave;nh kh&aacute;ch quan nhưng vẫn đảm bảo đủ những điểm đ&atilde; n&ecirc;u trong lịch tr&igrave;nh.</em></p>'),
(112, 204, 'HÀ NỘI – TP.HCM', '<p>Qu&yacute; kh&aacute;ch tập trung tại văn ph&ograve;ng Vietravel (03 Hai B&agrave; Trưng, Ho&agrave;n Kiếm), xe đưa Qu&yacute; kh&aacute;ch ra s&acirc;n bay Nội B&agrave;i (Ga nội địa), HDV hỗ trợ kh&aacute;ch l&agrave;m thủ tục đ&aacute;p chuyến bay đi TP.HCM. Đến s&acirc;n bay T&acirc;n Sơn Nhất, xe v&agrave; HDV Vietravel đ&oacute;n Qu&yacute; kh&aacute;ch đi tham quan:</p>\r\n\r\n<p><strong>- Hội Trường Thống Nhất:</strong>&nbsp;c&oacute; t&ecirc;n gọi cũ l&agrave; Dinh Độc Lập</p>\r\n\r\n<p><strong>- Nh&agrave; thờ Đức B&agrave; v&agrave; Bưu điện trung t&acirc;m Th&agrave;nh Phố</strong>&hellip; Check-in Kh&ocirc;ng gian đường s&aacute;ch</p>\r\n\r\n<p>Sau bữa trưa, Đo&agrave;n nhận ph&ograve;ng kh&aacute;ch sạn v&agrave; nghỉ ngơi.</p>\r\n\r\n<p>Chiều đo&agrave;n tiếp tục tham quan:</p>\r\n\r\n<p><strong>- Nh&agrave; h&aacute;t Th&agrave;nh Phố, C&ocirc;ng vi&ecirc;n Bến Bạch Đằn</strong>g - Cột cờ Thủ Ngữ</p>\r\n\r\n<p>- Dọc theo bến Bạch Đằng,&nbsp;Qu&yacute; kh&aacute;ch chi&ecirc;m ngưỡng vẻ đẹp của&nbsp;Ng&acirc;n h&agrave;ng nh&agrave; nước Việt Nam, Cầu Mống đến&nbsp;Bến Nh&agrave; Rồng - Bảo T&agrave;ng Hồ Ch&iacute; Minh</p>\r\n\r\n<p>- Phố đi bộ Nguyễn Huệ, tượng Đ&agrave;i Chủ tịch Hồ ch&iacute; Minh, Ủy Ban Nh&acirc;n D&acirc;n Th&agrave;nh Phố.</p>\r\n\r\n<p>- Tự do thưởng thức ẩm thực từ đường phố đến sang trọng tại phố đi bộ nhộn nhịp bậc nhất TP.HCM.</p>\r\n\r\n<p>Kết th&uacute;c chương tr&igrave;nh, xe đưa qu&yacute; kh&aacute;ch d&ugrave;ng bữa tối tại nh&agrave; h&agrave;ng địa phương v&agrave; trở về kh&aacute;ch sạn nghỉ ngơi.</p>\r\n\r\n<p>Nghỉ đ&ecirc;m tại TP.HCM</p>');
INSERT INTO `tbl_timeline` (`timeLineId`, `tourId`, `title`, `description`) VALUES
(113, 204, 'TP.HỒ CHÍ MINH – PHAN THIẾT', '<p>Qu&yacute; kh&aacute;ch d&ugrave;ng bữa s&aacute;ng tại kh&aacute;ch sạn. Đo&agrave;n khởi h&agrave;nh đi Phan Thiết. Ngồi tr&ecirc;n xe, Qu&yacute; kh&aacute;ch sẽ được chi&ecirc;m ngưỡng cảnh đẹp của v&ugrave;ng đất B&igrave;nh Thuận như: Lầu &Ocirc;ng Ho&agrave;ng, Th&aacute;p chăm Poshanu, B&atilde;i Đ&aacute; &Ocirc;ng Địa, Rạng Dừa H&agrave;m Tiến,&hellip; Đo&agrave;n d&ugrave;ng bữa trưa v&agrave; tham quan, trải nghiệm tại:</p>\r\n\r\n<p>- Trung t&acirc;m B&ugrave;n Kho&aacute;ng Mũi N&eacute;:<strong>&nbsp;Tận hưởng cảm gi&aacute;c tắm kho&aacute;ng thư gi&atilde;n, tắm b&ugrave;n hoặc massage l&agrave; một liệu&nbsp;</strong>ph&aacute;p l&agrave;m đẹp từ thi&ecirc;n nhi&ecirc;n gi&uacute;p cải thiện l&agrave;n da tươi trẻ, mịn m&agrave;ng&nbsp;(<em><strong>đ&atilde; bao gồm chi ph&iacute; tắm kho&aacute;ng, Chi ph&iacute; tắm b&ugrave;n tự t&uacute;c).</strong></em></p>\r\n\r\n<p>Sau đ&oacute;, xe v&agrave; HDV đưa qu&yacute; kh&aacute;ch về nhận ph&ograve;ng nghỉ ngơi.</p>\r\n\r\n<p>Buổi chiều, đo&agrave;n đến tham quan tại:</p>\r\n\r\n<p><strong>- L&agrave;ng Ch&agrave;i Xưa Mũi N&eacute;:</strong>&nbsp;với lịch sử 300 năm c&aacute;i n&ocirc;i của nghề l&agrave;m nước mắm, trải nghiệm cảm gi&aacute;c lao động tr&ecirc;n đồng muối, đi tr&ecirc;n con đường rạng xưa, thăm phố cổ Phan Thiết, , thăm nh&agrave; lều của h&agrave;m hộ nước mắm xưa, đắm ch&igrave;m cảm x&uacute;c trong biển 3D v&agrave; th&iacute;ch th&uacute; khi đi chợ l&agrave;ng ch&agrave;i xưa với b&agrave;n t&iacute;nh tay, b&agrave;n c&acirc;n xưa th&uacute; vị,&hellip;</p>\r\n\r\n<p>Qu&yacute; kh&aacute;ch d&ugrave;ng bữa tối với m&oacute;n&nbsp;<em><strong>lẩu thả</strong></em>&nbsp;nổi tiếng khu vực B&igrave;nh Thuận v&agrave; tự do kh&aacute;m ph&aacute; th&agrave;nh phố biển Mũi N&eacute; về đ&ecirc;m.</p>\r\n\r\n<p>Nghỉ đ&ecirc;m tại Phan Thiết</p>\r\n\r\n<p>&nbsp;</p>'),
(114, 204, 'PHAN THIẾT – MŨI NÉ – BÀU TRẮNG', '<p>Qu&yacute; kh&aacute;ch d&ugrave;ng bữa s&aacute;ng tại kh&aacute;ch sạn. Sau đ&oacute; xe đưa đo&agrave;n khởi h&agrave;nh theo cung đường ven biển<strong>&nbsp;Mũi N&eacute; - B&agrave;u Trắng</strong>&nbsp;thẳng tắp, check-in con đường đ&ocirc;i cong v&uacute;t &amp; chiếc xe tải b&aacute;n nước m&agrave;u v&agrave;ng cam nổi bật được điểm xuyến bởi hoang mạc c&aacute;t hai b&ecirc;n c&ugrave;ng m&agrave;u xanh của biển trời.</p>\r\n\r\n<p><strong>- Đến B&agrave;u Trắng:</strong>&nbsp;ngắm nh&igrave;n biển hồ lấp l&aacute;nh &aacute;nh bạc dưới &aacute;nh mặt trời rực rỡ c&ugrave;ng sự k&igrave; vỹ của những cồn c&aacute;t trắng x&oacute;a. Cứ độ th&aacute;ng 05 - 08 những c&aacute;nh hoa sen hồng lấp l&oacute; sau chiếc l&aacute; xanh mướt&nbsp; tạo n&ecirc;n một khung cảnh thi&ecirc;n nhi&ecirc;n thơ mộng. Qu&yacute; kh&aacute;ch thử sức với xe Jeep tốc độ cao, chinh phục đồi c&aacute;t &ldquo;n&oacute;ng bỏng&rdquo; nhất Việt Nam mang t&ecirc;n Trinh Nữ (chi ph&iacute; xe Jeep tự t&uacute;c).</p>\r\n\r\n<p>Đo&agrave;n d&ugrave;ng bữa trưa tại nh&agrave; h&agrave;ng địa phương, sau đ&oacute; tự do nghỉ ngơi, tắm biển hoặc hồ bơi tại kh&aacute;ch sạn/resort.</p>\r\n\r\n<p>Buổi chiều, xe đưa qu&yacute; kh&aacute;ch d&ugrave;ng bữa tối tại nh&agrave; h&agrave;ng địa phương v&agrave; trở về kh&aacute;ch sạn nghỉ ngơi. Qu&yacute; kh&aacute;ch tự do dạo phố đ&ecirc;m v&agrave; thưởng thức c&aacute;c m&oacute;n ăn hải sản tươi ngon của v&ugrave;ng biển n&agrave;y.</p>\r\n\r\n<p>Nghỉ đ&ecirc;m tại Phan Thiết.</p>\r\n\r\n<p>&nbsp;</p>'),
(115, 204, 'PHAN THIẾT - TP HỒ CHÍ MINH – HÀ NỘI', '<p>Qu&yacute; kh&aacute;ch d&ugrave;ng bữa s&aacute;ng tại kh&aacute;ch sạn v&agrave; l&agrave;m thủ tục trả ph&ograve;ng. Đo&agrave;n khởi h&agrave;nh về TP. Hồ Ch&iacute; Minh. Tr&ecirc;n đường, đo&agrave;n dừng ch&acirc;n tham quan:</p>\r\n\r\n<p>- Mua sắm đặc sản Phan Thiết (chi ph&iacute; tự t&uacute;c) - nước mắm Phan Thiết, c&aacute;c loại hản sản kh&ocirc; như: mực kh&ocirc; c&acirc;u, mực một nắng, c&aacute; cơm r&uacute;t xương, c&aacute; thu một nắng, c&aacute; đ&ugrave; một nắng&hellip;.. về l&agrave;m qu&agrave; cho người th&acirc;n v&agrave; bạn b&egrave;.</p>\r\n\r\n<p><strong>- L&acirc;u đài Rượu Vang RD</strong>: Nghe giới thiệu về quy tr&igrave;nh sản xuất, đ&oacute;ng chai v&agrave; thưởng thức một trong những loại rượu vang hảo hạng nổi tiếng thế giới được xuất xứ từ Thung Lũng Napa (Mỹ).</p>\r\n\r\n<p>Qu&yacute; kh&aacute;ch d&ugrave;ng bữa trưa tr&ecirc;n đường về.</p>\r\n\r\n<p>Đến s&acirc;n bay T&acirc;n Sơn Nhất, đo&agrave;n l&agrave;m thủ tục đ&aacute;p chuyến bay về H&agrave; Nội. Đến s&acirc;n bay Nội B&agrave;i, xe đưa đo&agrave;n về điểm tập trung ban đầu, chia tay đo&agrave;n v&agrave; kết th&uacute;c chương tr&igrave;nh&nbsp;du lịch.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>LƯU &Yacute;</strong></p>\r\n\r\n<p><em>- Thời gian bay c&oacute; thể thay đổi theo th&ocirc;ng tin cập nhật của h&atilde;ng h&agrave;ng kh&ocirc;ng &ndash; nh&acirc;n vi&ecirc;n giao dịch c&oacute; tr&aacute;ch nhiệm b&aacute;o lại cho kh&aacute;ch h&agrave;ng ngay khi nhận được th&ocirc;ng b&aacute;o nếu c&oacute;</em></p>\r\n\r\n<p><em>- H&agrave;nh tr&igrave;nh c&oacute; thể thay đổi&nbsp; thứ tự điểm đến t&ugrave;y thuộc v&agrave;o điều kiện thực tế - lịch tr&igrave;nh thăm quan (tắm biển, trải nghiệm) rất dễ bị ảnh hưởng bởi thời tiết. Đ&acirc;y l&agrave; trường hợp bất khả kh&aacute;ng mong qu&yacute; kh&aacute;ch hiểu v&agrave; th&ocirc;ng cảm.</em></p>\r\n\r\n<p><em>- Trong trường hợp đo&agrave;n gom dưới 10 kh&aacute;ch th&igrave; tour kh&ocirc;ng khởi h&agrave;nh. C&ocirc;ng ty sẽ tư vấn cho Q&uacute;y kh&aacute;ch chuyển tour sang ng&agrave;y khởi h&agrave;nh l&acirc;n cận hoặc th&ocirc;ng b&aacute;o chi ph&iacute; ph&aacute;t sinh để khởi h&agrave;nh được đo&agrave;n.</em></p>\r\n\r\n<p>&nbsp;</p>'),
(116, 205, 'CẦN THƠ – TP. HCM - NHA TRANG', '<p><strong>Đ&Ecirc;M 1: CẦN THƠ &ndash; TP. HCM &ndash; NHA TRANG</strong></p>\r\n\r\n<p><strong>22:30</strong>&nbsp;Xe v&agrave; HDV đ&oacute;n đo&agrave;n tại điểm hẹn, khởi h&agrave;nh chuyến tham quan Nha Trang 3N2Đ, qu&yacute; kh&aacute;ch nghỉ ngơi tr&ecirc;n xe.</p>\r\n\r\n<p><strong>NG&Agrave;Y 1: KH&Aacute;M PH&Aacute; NHA TRANG</strong></p>\r\n\r\n<p><strong>S&Aacute;NG:</strong>&nbsp;D&ugrave;ng bữa s&aacute;ng, đo&agrave;n di chuyển tắm kho&aacute;ng tại:</p>\r\n\r\n<ul>\r\n	<li><strong>I &ndash; Resort Nha Trang:</strong>&nbsp;với lối kiến tr&uacute;c truyền thống đậm n&eacute;t d&acirc;n d&atilde;, mộc mạc mang đến cho qu&yacute; kh&aacute;ch cảm gi&aacute;c sảng kho&aacute;i, thư gi&atilde;n c&ugrave;ng với những trải nghiệm mới về cuộc sống th&ocirc;ng qua những g&oacute;i dịch vụ ng&acirc;m tắm b&ugrave;n kho&aacute;ng độc đ&aacute;o từ thi&ecirc;n nhi&ecirc;n mang đậm phong c&aacute;ch hiện đại.</li>\r\n</ul>\r\n\r\n<p><strong>TRƯA:</strong>&nbsp;D&ugrave;ng bữa trưa tại nh&agrave; h&agrave;ng, nhận ph&ograve;ng kh&aacute;ch sạn v&agrave; nghỉ ngơi</p>\r\n\r\n<p><strong>CHIỀU:</strong>&nbsp;Khởi h&agrave;nh tham quan:</p>\r\n\r\n<ul>\r\n	<li><strong>Viện Hải dương học Nha Trang</strong>&nbsp;&ndash; Thuỷ cung giữa th&agrave;nh phố biển: L&agrave; nơi nghi&ecirc;n cứu về đời sống sinh vật biển lớn nhất Đ&ocirc;ng Nam &Aacute;. Đến thăm Viện, du kh&aacute;ch sẽ được chi&ecirc;m ngưỡng tận mắt bảo t&agrave;ng lưu trữ sinh vật biển với hơn 4.000 loại sinh vật v&agrave; tr&ecirc;n 20.000 mẫu vật đ&atilde; được g&igrave;n giữ từ nhiều năm. B&ecirc;n cạnh đ&oacute;, nơi đ&acirc;y c&ograve;n c&oacute; rất nhiều lo&agrave;i sinh vật biển sống được nu&ocirc;i thả trong bể k&iacute;nh.<br />\r\n	&nbsp;</li>\r\n</ul>\r\n\r\n<p><strong>TỐI:</strong>&nbsp;D&ugrave;ng bữa tối tại nh&agrave; h&agrave;ng, tự do kh&aacute;m ph&aacute; th&agrave;nh phố biển về đ&ecirc;m.</p>'),
(117, 205, 'DU NGOẠN VỊNH NHA TRANG', '<p><strong>S&Aacute;NG:</strong>&nbsp;Qu&yacute; kh&aacute;ch d&ugrave;ng bữa s&aacute;ng, di chuyển sang bến cảng bắt đầu h&agrave;nh tr&igrave;nh kh&aacute;m ph&aacute; vịnh San H&ocirc;:</p>\r\n\r\n<ul>\r\n	<li>Đến<strong>&nbsp;Vịnh san h&ocirc;:</strong>&nbsp;Qu&yacute; kh&aacute;ch c&oacute; thể tự do tắm biển với l&agrave;n nước trong xanh tại đ&acirc;y c&oacute; ch&ograve;i ghế nằm nghỉ ngơi ngo&agrave;i ra c&ograve;n c&aacute;c tr&ograve; chơi tr&ecirc;n biển, d&ugrave; bay, moto nước, phao chuối, lặn biển đội mũ đi bộ ngắm san h&ocirc;, lặn biển bằng b&igrave;nh kh&iacute;, vv.. (đăng k&yacute; tr&ograve; chơi th&igrave; ph&iacute; tự t&uacute;c)</li>\r\n	<li>Tham quan một hệ thống b&egrave; nu&ocirc;i trồng hải sản c&oacute; quy m&ocirc; lớn bậc nhất tại Vịnh.<br />\r\n	&nbsp;</li>\r\n</ul>\r\n\r\n<p><strong>TRƯA</strong>: D&ugrave;ng bữa trưa tại Vịnh, di chuyển về lại kh&aacute;ch sạn nghỉ ngơi.&nbsp;</p>\r\n\r\n<p><strong>CHIỀU</strong>:&nbsp;Xe v&agrave; HDV đưa qu&yacute; kh&aacute;ch tham quan:</p>\r\n\r\n<ul>\r\n	<li><strong>L&agrave;ng Nghề Trường Sơn</strong>&nbsp;&ndash; Nơi lưu giữ hồn qu&ecirc; giữa l&ograve;ng phố biển: Tham quan, kh&aacute;m ph&aacute; kh&ocirc;ng gian văn h&oacute;a truyền thống với v&ocirc; số những t&aacute;c phẩm thủ c&ocirc;ng mỹ nghệ độc đ&aacute;o.</li>\r\n</ul>\r\n\r\n<p>D&ugrave;ng bữa chiều tại nh&agrave; h&agrave;ng với&nbsp;<em><strong>đặc sản Nem Nướng Nha Trang.</strong></em>&nbsp;</p>\r\n\r\n<p><strong>TỐI:</strong>&nbsp;Qu&yacute; kh&aacute;ch tự do kh&aacute;m ph&aacute; Nha Trang hoặc chọn chương tr&igrave;nh vui chơi giải tr&iacute; tại Vinpearl Harbour (Phụ thu 200.000VND/Người)</p>\r\n\r\n<ul>\r\n	<li>Trải nghiệm C&aacute;p treo vượt biển d&agrave;i hơn 3.300m đưa từ th&agrave;nh phố Nha Trang ra đảo H&ograve;n Tre, tuyến c&aacute;p treo sẽ mang đến cơ hội chi&ecirc;m ngưỡng vẻ đẹp say đắm của một trong 29 vịnh biển đẹp nhất thế giới.</li>\r\n	<li>Thưởng ngoạn trọn vẹn kỳ quan từ g&oacute;c nh&igrave;n 360 độ. được đ&aacute;nh gi&aacute; l&agrave; cung đường biển tuyệt đẹp.<br />\r\n	&nbsp;</li>\r\n</ul>\r\n\r\n<p>Đến&nbsp;<strong>Vinpearl Harbour</strong>&nbsp;địa điểm cực mới tại Nha Trang. Nơi đ&acirc;y c&oacute; nhiều gian h&agrave;ng đa dạng từ phong c&aacute;ch thiết kế đến c&aacute;c loại mặt h&agrave;ng.&nbsp;</p>\r\n\r\n<ul>\r\n	<li><strong>Trung t&acirc;m thương mại:</strong>&nbsp;kh&ocirc;ng gian mua sắm sầm uất, nhộn nhịp với nhiều mặt h&agrave;ng kh&aacute;c nhau, sẵn s&agrave;ng đ&aacute;p ứng nhu cầu của kh&aacute;ch</li>\r\n	<li><strong>Đại lộ mua sắm ven đường biển</strong>: đ&acirc;y l&agrave; nơi hội tụ của c&aacute;c thương hiệu thời trang &ldquo;on trend&rdquo; như Lug.vn, Beverly Hills Polo Club, Tagins, Skechers, Mipa Golf.</li>\r\n	<li><strong>Stunt Show Rise of the Mermaid</strong>: Thưởng thức show h&agrave;nh động thực cảnh tr&ecirc;n biển độc đ&aacute;o với 6 chương biểu diễn v&agrave; 2 m&agrave;n chiếu khổng lồ. Stunt Show Nha Trang c&oacute; t&ecirc;n gọi Huyền thoại biển xanh v&agrave; t&ecirc;n tiếng Anh l&agrave; Rise of the Ocean Princess.</li>\r\n</ul>\r\n\r\n<p><strong>22:00</strong>&nbsp;Di chuyển xuống c&aacute;p treo về đất liền, xe đ&oacute;n đo&agrave;n về lại kh&aacute;ch sạn, nghỉ ngơi.</p>'),
(118, 205, 'TẠM BIỆT NHA TRANG', '<p><strong>S&Aacute;NG</strong>: Qu&yacute; kh&aacute;ch tự do đi dạo, tắm biển, d&ugrave;ng bữa s&aacute;ng tại kh&aacute;ch sạn. Trả ph&ograve;ng kh&aacute;ch sạn, di chuyển về lại Cần Thơ</p>\r\n\r\n<p>Dừng chụp h&igrave;nh check in tại C&agrave; N&aacute; &ndash; &ldquo;n&agrave;ng thơ&rdquo; của miền đất Phan Rang</p>\r\n\r\n<p><strong>TRƯA:</strong>&nbsp;D&ugrave;ng bữa trưa tại Phan Thiết</p>\r\n\r\n<p><strong>CHIỀU:</strong>&nbsp;D&ugrave;ng bữa nhẹ tại Tiền Giang, tiếp tục di chuyển về lại Cần Thơ</p>\r\n\r\n<p><strong>TỐI:&nbsp;</strong>Đến Cần Thơ, xe v&agrave; HDV đưa đo&agrave;n về lại điểm đ&oacute;n ban đầu, kết th&uacute;c chương tr&igrave;nh, hẹn gặp lại qu&yacute; kh&aacute;ch!</p>\r\n\r\n<p><em><strong>C&aacute;c mốc thời gian c&oacute; gi&aacute; trị tham khảo, t&ugrave;y theo đi&ecirc;̀u kiện thực tế m&agrave; lịch tr&igrave;nh c&oacute; thể thay đổi cho ph&ugrave; hợp.</strong></em></p>'),
(119, 206, 'CẦN THƠ – TP. HCM - ĐÀ LẠT', '<p><strong>Đ&Ecirc;M 1: CẦN THƠ &ndash; TP. HCM &ndash; Đ&Agrave; LẠT</strong></p>\r\n\r\n<p><strong>22:30</strong>&nbsp;Tập trung tại văn ph&ograve;ng Lửa Việt (677 Trần Hưng Đạo, P1, Q5, Tp. HCM). Xe v&agrave; HDV Lửa Việt đ&oacute;n Đo&agrave;n tại điểm hẹn, khởi đi Đ&agrave; Lạt, Đo&agrave;n nghỉ đ&ecirc;m tr&ecirc;n xe.&nbsp;</p>\r\n\r\n<p><strong>NG&Agrave;Y 1: CUNG NAM PHƯƠNG &ndash; DINH BẢO ĐẠI</strong></p>\r\n\r\n<p><strong>S&Aacute;NG:</strong>&nbsp;Đo&agrave;n d&ugrave;ng bữa s&aacute;ng, tham quan:</p>\r\n\r\n<ul>\r\n	<li><strong>Đ&agrave; Lạt View</strong>: với h&igrave;nh ảnh Cổng trời thứ 2 ph&aacute; c&aacute;ch, độc đ&aacute;o. Qu&yacute; kh&aacute;ch c&oacute; thể thưởng thức một ly caf&eacute; thơm ngon, chụp ngay v&agrave;i bức h&igrave;nh checkin tr&ecirc;n chiếc cầu t&igrave;nh duy&ecirc;n m&agrave;u đỏ bắt ngang qua rừng th&ocirc;ng tuyệt đẹp để khoe với bạn b&egrave;, người th&acirc;n.</li>\r\n	<li><strong>Thiền Viện Tr&uacute;c L&acirc;m</strong>: Ngắm to&agrave;n cảnh hồ Tuyền L&acirc;m v&agrave; n&uacute;i đồi giữa đại ng&agrave;n rừng th&ocirc;ng.</li>\r\n	<li><strong>Cung Nam Phương:</strong>&nbsp;vừa nghe giới thiệu về những n&eacute;t văn h&oacute;a độc đ&aacute;o, đặc trưng của nhiều d&acirc;n tộc cộng cư tr&ecirc;n v&ugrave;ng đất L&acirc;m Đồng tại bảo t&agrave;ng L&acirc;m Đồng &ndash; vừa chi&ecirc;m ngưỡng v&agrave; chụp ảnh lưu niệm tại một trong những dinh thự cổ kiểu ph&aacute;p đẹp bật nhất phố n&uacute;i Đ&agrave; Lạt.<br />\r\n	&nbsp;</li>\r\n</ul>\r\n\r\n<p><strong>TRƯA:</strong>&nbsp;D&ugrave;ng bữa trưa, nhận ph&ograve;ng nghỉ ngơi.</p>\r\n\r\n<ul>\r\n	<li><strong>L&agrave;ng Hoa Vạn Th&agrave;nh</strong>: Khi đến nơi đ&acirc;y bạn sẽ được tận hưởng bầu kh&ocirc;ng kh&iacute; trong l&agrave;nh thư gi&atilde;n ch&igrave;m đắm trong những hương thơm dịu nhẹ tỏa ra từ c&aacute;c lo&agrave;i hoa, kh&ocirc;ng những thế bạn c&ograve;n được tận mắt nh&igrave;n thấy những người n&ocirc;ng d&acirc;n chăm s&oacute;c n&acirc;ng niu những c&agrave;nh hoa từng ch&uacute;t một nhẹ nh&agrave;ng như sợ l&agrave;m hư đi c&aacute;i vẻ đẹp vốn c&oacute; của n&oacute;.</li>\r\n	<li><strong>Fresh garden</strong>: nơi sở hữu c&aacute;nh đồng hoa khổng lồ, đa dạng với nhiều m&agrave;u sắc rực rỡ. Qu&yacute; kh&aacute;ch tham quan, chụp h&igrave;nh với ch&uacute; gấu Teddy, cối xoay gi&oacute; khổng lồ, ng&ocirc;i nh&agrave; với m&aacute;i l&agrave;m từ hoa độc đ&aacute;o&hellip;<br />\r\n	&nbsp;</li>\r\n</ul>\r\n\r\n<p><strong>TỐI:</strong>&nbsp;D&ugrave;ng bữa tối, nhận ph&ograve;ng kh&aacute;ch sạn, tự do kh&aacute;m ph&aacute; Đ&agrave; Lạt về đ&ecirc;m.&nbsp;</p>'),
(120, 206, 'BẢO TÀNG SOCOLA - PUPPY FARM', '<p><strong>S&Aacute;NG:</strong>&nbsp;Đo&agrave;n d&ugrave;ng bữa s&aacute;ng, khởi h&agrave;nh tham quan:</p>\r\n\r\n<ul>\r\n	<li><strong>Puppy Farm</strong>: du kh&aacute;ch thỏa th&iacute;ch tạo d&aacute;ng c&ugrave;ng những ch&uacute; c&uacute;n si&ecirc;u dễ thương hay chọn cho m&igrave;nh những g&oacute;c chụp đẹp b&ecirc;n c&aacute;c lo&agrave;i hoa với đủ m&agrave;u sắc rực rỡ, vườn b&iacute; ng&ocirc;, d&acirc;u t&acirc;y, c&agrave; chua bi,&hellip; (theo m&ugrave;a) tựa như l&agrave; một Đ&agrave; Lạt thu nhỏ, v&ocirc; c&ugrave;ng thơ mộng v&agrave; l&atilde;ng mạn.&nbsp;</li>\r\n	<li><strong>L&agrave;ng Nấm</strong>: l&agrave; nơi nghi&ecirc;n cứu, nu&ocirc;i trồng, thử nghiệm nhiều giống nấm bản địa v&agrave; ngoại nhập nhiều dinh dưỡng, ph&ugrave; hợp với kh&iacute; hậu tự nhi&ecirc;n m&aacute;t mẻ v&agrave; trong l&agrave;nh quanh năm của Đ&agrave; Lạt.<br />\r\n	&nbsp;</li>\r\n</ul>\r\n\r\n<p><strong>TRƯA:</strong>&nbsp;Qu&yacute; kh&aacute;ch d&ugrave;ng Buffet Rau Leguda, sau đ&oacute; quay về kh&aacute;ch sạn nghỉ ngơi.</p>\r\n\r\n<ul>\r\n	<li><strong>Lang Biang</strong>: Nghe c&acirc;u chuyện t&igrave;nh l&atilde;ng mạn vượt thời gian giữa ch&agrave;ng Lang v&agrave; n&agrave;ng H&rsquo;Biang. Tr&ecirc;n đỉnh n&uacute;i Langbiang qu&yacute; kh&aacute;ch c&oacute; thể ngắm nh&igrave;n to&agrave;n cảnh Th&aacute;c Anknoet, hồ Dankia ,suối V&agrave;ng- suối Bạc, Cao Nguy&ecirc;n L&acirc;m Vi&ecirc;n mộng mơ hiện r&otilde; trong tầm mắt với đồi th&ocirc;ng trải d&agrave;i, nơi từng in dấu ch&acirc;n nh&agrave; y học Yersin trong h&agrave;nh tr&igrave;nh kh&aacute;m ph&aacute; Đ&agrave; Lạt thi&ecirc;n đường.</li>\r\n	<li><strong>Bảo t&agrave;ng Socola</strong>: Qu&yacute; kh&aacute;ch sẽ được tham quan những t&aacute;c phẩm đi&ecirc;u khắc bằng socola nắm giữ nhiều kỷ luật ở Việt Nam. Sẽ được nếm thử socola miễn ph&iacute;.</li>\r\n	<li><strong>Ph&acirc;n viện sinh học</strong>: được biết đến như 1 tọa độ sống ảo mới tại Đ&agrave; Lạt với g&oacute;c ảnh b&ecirc;n những bức tường cổ k&iacute;nh. Kh&ocirc;ng chỉ vậy, đến đ&acirc;y, Qu&yacute; kh&aacute;ch c&ograve;n được t&igrave;m hiểu về m&ocirc;i trường sống, những nguồn gen qu&yacute; qua hiện vật v&ocirc; c&ugrave;ng phong ph&uacute;, khu trưng b&agrave;y xương, sừng, m&ocirc; h&igrave;nh của c&aacute;c lo&agrave;i động vật qu&yacute; hiếm như khủng long, voi, hổ, gấu, vượn,&hellip; hay t&igrave;m hiểu về sự vận h&agrave;nh của vũ trụ.<br />\r\n	&nbsp;</li>\r\n</ul>\r\n\r\n<p><strong>TỐI:</strong>&nbsp;D&ugrave;ng bữa tối tại nh&agrave; h&agrave;ng địa phương. Đo&agrave;n tự do dạo phố kh&aacute;m ph&aacute; Đ&agrave; Lạt về đ&ecirc;m.</p>'),
(121, 206, 'ĐÀ LẠT – TP. HCM', '<p><strong>S&Aacute;NG</strong>: D&ugrave;ng bữa s&aacute;ng, l&agrave;m thủ tục trả ph&ograve;ng, khởi h&agrave;nh tham quan:</p>\r\n\r\n<ul>\r\n	<li><strong>Th&aacute;c Datanla</strong>: l&agrave; một trong những địa điểm du lịch l&yacute; tưởng của du kh&aacute;ch. Được tham gia chơi những tr&ograve; chơi mạo hiểm. tại đ&acirc;y qu&yacute; kh&aacute;ch c&oacute; thể tham quan th&aacute;c Datanla bằng hệ thống m&aacute;ng trượt d&agrave;i nhất Đ&ocirc;ng Nam &Aacute; với chiều d&agrave;i 2.400m.</li>\r\n</ul>\r\n\r\n<p>Khởi h&agrave;nh về lại Cần Thơ</p>\r\n\r\n<p><strong>TRƯA:</strong>&nbsp;D&ugrave;ng bữa tại&nbsp;<strong>T&acirc;m Ch&acirc;u Lộc An.&nbsp;</strong></p>\r\n\r\n<p><strong>CHIỀU:</strong>&nbsp;Về đến&nbsp;<strong>Tp. HCM,</strong>&nbsp;xe v&agrave; HDV Lửa Việt đưa Đo&agrave;n về điểm hẹn ban đầu, kết th&uacute;c chương tr&igrave;nh.</p>\r\n\r\n<p><em><strong>C&aacute;c mốc thời gian c&oacute; gi&aacute; trị tham khảo, t&ugrave;y theo điều kiện thực tế m&agrave; lịch tr&igrave;nh c&oacute; thể thay đổi cho ph&ugrave; hợp.</strong></em></p>'),
(122, 207, 'TP. HỒ CHÍ MINH – HÀ NỘI', '<p>Tập trung tại s&acirc;n bay T&acirc;n Sơn Nhất đ&aacute;p chuyến bay TP. HCM &ndash; H&Agrave; NỘI. (<em>C&aacute;c chuyến bay dự kiến từ 06:00 &ndash; 09:00</em>). Xe v&agrave; HDV đ&oacute;n kh&aacute;ch tại s&acirc;n bay Nội B&agrave;i, đo&agrave;n di chuyển về trung t&acirc;m H&agrave; Nội.</p>\r\n\r\n<p>Ăn trưa. Nhận ph&ograve;ng kh&aacute;ch sạn. Khởi h&agrave;nh tham quan:</p>\r\n\r\n<p><strong>&bull; Hồ T&acirc;y</strong>&nbsp;&ndash; Hồ lớn nhất H&agrave; Nội,&nbsp;<strong>ch&ugrave;a Trấn Quốc</strong>,&nbsp;<strong>đền Qu&aacute;n Th&aacute;nh</strong>.</p>\r\n\r\n<p><strong>&bull;&nbsp;Văn Miếu &ndash; Quốc Tử Gi&aacute;m</strong>&nbsp;&ndash; trường đại học đầu ti&ecirc;n của Việt Nam; tại đ&acirc;y c&oacute; hoạt động xin chữ đầu xu&acirc;n bởi c&aacute;c &Ocirc;ng Đồ &ndash; tiếp nối truyền thống văn h&oacute;a Tết của Việt Nam.</p>\r\n\r\n<p><strong>&bull;&nbsp;</strong>Ngồi xe điện tham quan&nbsp;<strong>phố cổ H&agrave; Nội</strong>&nbsp;với phố H&agrave;ng Ngang, H&agrave;ng Đ&agrave;o, H&agrave;ng B&ocirc;ng&hellip; ngắm&nbsp;<strong>Hồ Gươm, cầu Th&ecirc; H&uacute;c, đền Ngọc Sơn</strong>.</p>\r\n\r\n<p>Ăn tối. Tự do kh&aacute;m ph&aacute; thủ đ&ocirc; H&agrave; Nội về đ&ecirc;m. Nghỉ đ&ecirc;m tại H&agrave; Nội.</p>'),
(123, 207, 'HÀ NỘI – TRÀNG AN – BÁI ĐÍNH – HẠ LONG', '<p>Ăn s&aacute;ng. L&agrave;m thủ tục trả ph&ograve;ng. Khởi h&agrave;nh tham quan:</p>\r\n\r\n<p><strong>&bull; Khu du lịch Tr&agrave;ng An &ndash; Ninh B&igrave;nh</strong>&nbsp;đ&atilde; được UNESCO c&ocirc;ng nhận l&agrave; Di sản Văn h&oacute;a v&agrave; Thi&ecirc;n nhi&ecirc;n Thế giới v&agrave;o năm 2014. Du kh&aacute;ch ngồi thuyền thưởng ngoạn vẻ đẹp hoang sơ được v&iacute; như &ldquo;Vịnh Hạ Long tr&ecirc;n cạn&rdquo; với v&ocirc; v&agrave;n c&aacute;c hạng động, hệ thống s&ocirc;ng, suối chảy tr&agrave;n trong c&aacute;c thung lũng, c&aacute;c hang xuy&ecirc;n thủy động, c&aacute;c d&atilde;y n&uacute;i đ&aacute; v&ocirc;i tr&ugrave;ng điệp&hellip;</p>\r\n\r\n<p><em>(Lưu &yacute;: Trường hợp Tr&agrave;ng An qu&aacute; tải, kh&ocirc;ng đảm bảo thời gian cho du kh&aacute;ch xuống Hạ Long, c&ocirc;ng ty sẽ đối ứng điểm tham quan thay thế:&nbsp;<strong>Tam Cốc &ndash; được mệnh danh l&agrave; &ldquo;Hạ Long tr&ecirc;n cạn</strong>&rdquo;)</em></p>\r\n\r\n<p>Ăn trưa với đặc sản&nbsp;<strong>cơm ch&aacute;y d&ecirc; n&uacute;i</strong>.</p>\r\n\r\n<p><strong>&bull;</strong>&nbsp;Tham quan&nbsp;<strong>ch&ugrave;a B&aacute;i Đ&iacute;nh</strong>&nbsp;&ndash; ng&ocirc;i ch&ugrave;a lớn nổi tiếng tại Miền Bắc, l&agrave;m lễ cầu ph&uacute;c l&agrave;nh.</p>\r\n\r\n<p>Rời&nbsp;<strong>Ninh B&igrave;nh</strong>, theo quốc lộ 10 đi Hạ Long. Tr&ecirc;n đường đi qu&yacute; kh&aacute;ch sẽ được cảm nhận cuộc sống, phong cảnh đặc trưng của đồng bằng Bắc Bộ với cảnh l&agrave;ng qu&ecirc; thanh b&igrave;nh qua c&aacute;c tỉnh Nam Định, Th&aacute;i B&igrave;nh, Hải Ph&ograve;ng.</p>\r\n\r\n<p>Ăn tối tại Hạ Long. Tự do kh&aacute;m ph&aacute;<strong>&nbsp;chợ đ&ecirc;m Hạ Long</strong>.&nbsp;Nghỉ đ&ecirc;m tại Hạ Long.</p>'),
(124, 207, 'HẠ LONG – YÊN TỬ – HÀ NỘI', '<p>Ăn s&aacute;ng. Trả ph&ograve;ng kh&aacute;ch sạn. Khởi h&agrave;nh tham quan:</p>\r\n\r\n<p><strong>&bull;</strong>&nbsp;<strong>Vịnh Hạ Long</strong>&nbsp;&ndash; Di sản thi&ecirc;n nhi&ecirc;n Thế giới được UNESCO c&ocirc;ng nhận. Chi&ecirc;m ngưỡng vẻ đẹp huyền b&iacute; của h&agrave;ng ng&agrave;n đảo đ&aacute; v&agrave; c&aacute;c hang động kỳ th&uacute;. Tham quan động Thi&ecirc;n Cung, ngắm cảnh L&agrave;ng Ch&agrave;i, h&ograve;n Ấm, h&ograve;n R&ugrave;a, h&ograve;n Đỉnh Hương, h&ograve;n Ch&oacute; Đ&aacute;, h&ograve;n G&agrave; Chọi&hellip;</p>\r\n\r\n<p>Ăn trưa. Chiều tham quan:</p>\r\n\r\n<p><strong>&bull;&nbsp;Khu danh thắng đất Phật Y&ecirc;n Tử</strong>&nbsp;&ndash; đất tổ của Thiền Ph&aacute;i Tr&uacute;c L&acirc;m. Qu&yacute; kh&aacute;ch đi c&aacute;p treo, tham quan Vườn Th&aacute;p Tổ, Ch&ugrave;a Hoa Y&ecirc;n<em><strong>&nbsp;(đ&atilde; bao gồm v&eacute; c&aacute;p treo Y&ecirc;n Tử chặng Hoa Y&ecirc;n).</strong></em></p>\r\n\r\n<p>Di chuyển về H&agrave; Nội, tr&ecirc;n đường dừng ch&acirc;n thưởng thức đặc sản b&aacute;nh đậu xanh Hải Dương.</p>\r\n\r\n<p>Ăn tối. Nhận ph&ograve;ng. Nghỉ đ&ecirc;m tại H&agrave; Nội.</p>'),
(125, 208, 'as', '<p>as</p>'),
(126, 209, 'as', '<p>ngay 1</p>');

-- --------------------------------------------------------

--
-- Table structure for table `tbl_tours`
--

CREATE TABLE `tbl_tours` (
  `tourId` int(11) NOT NULL,
  `title` varchar(255) NOT NULL,
  `time` varchar(255) NOT NULL,
  `description` text NOT NULL,
  `quantity` int(11) NOT NULL,
  `priceAdult` double NOT NULL,
  `priceChild` double NOT NULL,
  `destination` varchar(255) NOT NULL,
  `domain` enum('b','t','n') NOT NULL COMMENT '''b'' : Mienbac,\r\n''t'' : MienTrung,\r\n''n'' MienNam',
  `availability` tinyint(1) NOT NULL,
  `startDate` date NOT NULL,
  `endDate` date NOT NULL,
  `reviews` varchar(255) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

