-- Dumping data for table `tbl_tours`
--

INSERT INTO `tbl_tours` (`tourId`, `title`, `time`, `description`, `quantity`, `priceAdult`, `priceChild`, `destination`, `domain`, `availability`, `startDate`, `endDate`, `reviews`) VALUES
(176, 'BIỂN ĐẢO 4N3Đ | PHÚ QUỐC (Khởi hành mỗi ngày)', '4 ngày 3 đêm', '<p><strong>ĐIỂM NHẤN</strong></p>\n\n<p><strong>&bull;&nbsp;Tham quan</strong>:&nbsp;Cơ sở sản xuất Rượu Sim, Thị Trấn Ho&agrave;ng H&ocirc;n, Vườn Ti&ecirc;u Ph&uacute; Quốc, Dinh Cậu, Dinh B&agrave;, Tượng Đ&agrave;i B&aacute;c Hồ, Chương tr&igrave;nh T&agrave;u c&acirc;u c&aacute; &ndash; lặn ngắm san h&ocirc;,&hellip;&nbsp;</p>\n\n<p><strong>&bull;&nbsp;Lưu tr&uacute;</strong>: Kh&aacute;ch sạn chuẩn 3 sao, resort 3 sao &ndash; 4 sao.&nbsp;</p>\n\n<p><strong>&bull;&nbsp;Hoạt động kh&aacute;c</strong>: tắm biển, lặn biển, ngắm san h&ocirc;, c&acirc;u c&aacute; v&agrave; thử sức với c&aacute;c tr&ograve; chơi biển.</p>', 43, 2990000, 2240000, 'PHÚ QUỐC', 'n', 1, '2025-05-03', '2025-05-06', NULL),
(177, 'MIỀN TRUNG 3N2Đ | ĐÀ NẴNG – CÙ LAO CHÀM – HỘI AN – BÀ NÀ (TẾT ÂM LỊCH 2025)', '2 ngày 1 đêm', '<p><strong>ĐIỂM NHẤN</strong></p>\n\n<p><strong>&bull; Tham quan</strong>: B&aacute;n đảo Sơn Tr&agrave;, Linh Ứng Tự, C&ugrave; Lao Ch&agrave;m, B&agrave; N&agrave; Hills, Ch&ugrave;a Cầu Nhật Bản,&hellip;</p>\n\n<p><strong>&bull; Lưu tr&uacute;</strong>: Kh&aacute;ch sạn chuẩn 3 &ndash; 4 sao.&nbsp;</p>\n\n<p><strong>&bull; Ăn uống</strong>: B&aacute;nh tr&aacute;ng thịt heo 2 đầu da v&agrave; m&igrave; Quảng Đ&agrave; Nẵng, đặc sản Cao lầu Hội An.&nbsp;</p>\n\n<p><strong>&bull; Hoạt động kh&aacute;c:</strong>&nbsp;Tắm biển Mỹ Kh&ecirc;, vui chơi tại Fantasy Park với hơn 100 tr&ograve; chơi hấp dẫn.&nbsp;</p>', 43, 3490000, 2620000, 'ĐÀ NẴNG – CÙ LAO CHÀM – HỘI AN – BÀ NÀ', 't', 1, '2025-04-30', '2025-05-01', NULL),
(179, 'MIỀN TRUNG 4N3Đ | ĐÀ NẴNG – HỘI AN – BÀ NÀ – HUẾ', '4 ngày 3 đêm', '<p><strong>ĐIỂM NHẤN</strong></p>\n\n<p><strong>&bull; Tham quan</strong>: B&aacute;n đảo Sơn Tr&agrave;, Linh Ứng Tự, đền thờ B&agrave; Ch&uacute;a Mẫu Thượng Ng&agrave;n, Cầu T&igrave;nh Y&ecirc;u, Đại Nội &ndash; Ho&agrave;ng Cung Triều Nguyễn,&hellip;</p>\n\n<p><strong>&bull; Lưu tr&uacute;</strong>: Kh&aacute;ch sạn chuẩn 3 &ndash; 4 sao.&nbsp;</p>\n\n<p><strong>&bull; Ăn uống</strong>: B&aacute;nh tr&aacute;ng thịt heo 2 đầu da v&agrave; m&igrave; Quảng Đ&agrave; Nẵng, đặc sản Cao lầu Hội An, đặc sản xứ Huế: B&aacute;nh b&egrave;o, lọc, nậm, kho&aacute;i&hellip;</p>\n\n<p><strong>&bull; Hoạt động kh&aacute;c:</strong>&nbsp;Tự do trải nghiệm C&ocirc;ng vi&ecirc;n Ch&acirc;u &Aacute; &ndash; Asia Park, thưởng ngoạn du thuyền tr&ecirc;n s&ocirc;ng H&agrave;n, vui chơi tại Fantasy Park với hơn 100 tr&ograve; chơi hấp dẫn, thưởng thức ca Huế tr&ecirc;n s&ocirc;ng Hương,&hellip;</p>', 33, 3990000, 2090000, 'ĐÀ NẴNG – HỘI AN – BÀ NÀ – HUẾ', 't', 1, '2025-05-10', '2025-05-13', NULL),
(181, 'MIỀN BẮC 6N5Đ | HÀ NỘI – NINH BÌNH – HẠ LONG – YÊN TỬ – SAPA', '6 ngày 5 đêm', '<p><strong>ĐIỂM NHẤN CHƯƠNG TR&Igrave;NH</strong></p>\n\n<p><strong>&bull; Tham quan</strong>: Lăng Chủ Tịch Hồ Ch&iacute; Minh, Phủ Chủ Tịch, ch&ugrave;a Một Cột, Vịnh Hạ Long, Chinh phục đỉnh Fansipan, Văn Miếu Quốc Tử Gi&aacute;m, Quần thể danh thắng Tr&agrave;ng An,&hellip;&nbsp;</p>\n\n<p><strong>&bull; Lưu tr&uacute;:&nbsp;</strong>Kh&aacute;ch sạn chuẩn 3*.&nbsp;</p>\n\n<p><strong>&bull; Ăn uống:&nbsp;</strong>Thưởng thức đặc sản Miền Bắc: B&aacute;nh cốm H&agrave; Nội, đặc sản d&ecirc; n&uacute;i Ninh B&igrave;nh, b&aacute;nh đậu xanh Hải Dương v&agrave; c&aacute;c m&oacute;n ăn đặc sản của v&ugrave;ng n&uacute;i T&acirc;y Bắc: Thắng Cố, cơm lam, lợn cắp n&aacute;ch&hellip;</p>\n\n<p><strong>&bull; Hoạt động kh&aacute;c:&nbsp;</strong>Thư gi&atilde;n với liệu tr&igrave;nh massage ch&acirc;n thảo dược của người Dao Đỏ, Tự do kh&aacute;m ph&aacute; SaPa,&hellip;&nbsp;</p>', 44, 8490000, 6370000, 'HÀ NỘI – NINH BÌNH – HẠ LONG – YÊN TỬ – SAPA', 'b', 1, '2025-05-03', '2025-05-08', NULL),
(183, 'MIỀN BẮC 5N4Đ | HÀ NỘI – HÀ GIANG – CAO BẰNG – LẠNG SƠN', '5 ngày 4 đêm', '<p>ĐIỂM NHẤN CHƯƠNG TR&Igrave;NH&nbsp;</p>\n\n<p><strong>&bull; Tham quan</strong>: Phố C&aacute;o, Cột cờ Lũng C&uacute;, đ&egrave;o Mả Pia, Động Ngườm Ngao, Th&aacute;c Bản Giốc, Khu di t&iacute;ch danh thắng Nhị &ndash; Tam Thanh, Dinh thự vua M&egrave;o Vương Ch&iacute;nh Đức,&hellip;&nbsp;</p>\n\n<p><strong>&bull; Lưu tr&uacute;:&nbsp;</strong>Kh&aacute;ch sạn chuẩn 3 &ndash; 4 sao, Homestay cao cấp.&nbsp;</p>\n\n<p><strong>&bull; Hoạt động kh&aacute;c:&nbsp;</strong>Chụp h&igrave;nh lưu niệm với cột mốc bi&ecirc;n giới 836 (02) chủ quyền thi&ecirc;ng li&ecirc;ng của Tổ Quốc, Mua sắm tại Chợ Đ&ocirc;ng Kinh &ndash; ng&ocirc;i chợ sầm uất nhộn nhịp v&agrave; lớn nhất Lạng Sơn.&nbsp;</p>', 45, 7590000, 5890000, 'HÀ NỘI – HÀ GIANG – CAO BẰNG – LẠNG SƠN', 'b', 1, '2025-05-07', '2025-05-11', NULL),
(184, 'MIỀN TRUNG 4N3Đ | ĐÀ NẴNG – HỘI AN – BÀ NÀ – HUẾ – PHONG NHA', '4 ngày 3 đêm', '<p><strong>ĐIỂM NHẤN</strong></p>\n\n<p><strong>&bull; Tham quan</strong>: Cầu T&igrave;nh Y&ecirc;u, Bảo T&agrave;ng Phật Học, ch&ugrave;a Linh Ứng, Ch&ugrave;a Quan Thế &Acirc;m, Th&aacute;nh địa La Vang, Động Phong Nha,&hellip;</p>\n\n<p><strong>&bull; Lưu tr&uacute;</strong>: Kh&aacute;ch sạn chuẩn 3 &ndash; 4 sao.&nbsp;</p>\n\n<p><strong>&bull; Ăn uống</strong>: Đặc sản Cao lầu Hội An, đặc sản xứ Huế: B&aacute;nh b&egrave;o, lọc, nậm, kho&aacute;i&hellip;</p>\n\n<p><strong>&bull; Hoạt động kh&aacute;c</strong>:&nbsp; Thưởng thức ca Huế tr&ecirc;n s&ocirc;ng Hương, kh&aacute;m ph&aacute; cố đ&ocirc; Huế về đ&ecirc;m, tản bộ kh&aacute;m ph&aacute; Hội An,&hellip;</p>', 47, 4550000, 2290000, 'ĐÀ NẴNG – HỘI AN – BÀ NÀ – HUẾ – PHONG NHA', 't', 1, '2025-04-30', '2025-05-03', NULL),
(185, 'MIỀN BẮC 4N3Đ | HÀ NỘI – LÀO CAI – SAPA – FANSIPAN', '4 ngày 3 đêm', '<p><strong>ĐIỂM NHẤN CHƯƠNG TR&Igrave;NH</strong></p>\n\n<p><strong>&bull; Tham quan</strong>: Hồ T&acirc;y, ch&ugrave;a Trấn Quốc, Hồ Gươm, thăm đền Ngọc Sơn, cầu Th&ecirc; H&uacute;c, đỉnh Fansipan, Bản C&aacute;t C&aacute;t, ch&ugrave;a Trấn Quốc, Hồ Gươm, đền Ngọc Sơn, cầu Th&ecirc; H&uacute;c,&hellip;</p>\n\n<p><strong>&bull; Lưu tr&uacute;</strong>: Kh&aacute;ch sạn chuẩn 3*.&nbsp;</p>\n\n<p><strong>&bull; Ăn uống</strong>: B&aacute;nh cốm H&agrave;ng Than, kem Tr&agrave;ng Tiền,&hellip;</p>\n\n<p><strong>&bull; Hoạt động kh&aacute;c</strong>: Tự do dạo chơi, ngắm cảnh H&agrave; Th&agrave;nh trong đ&ecirc;m, dạo chơi TT.Sapa, chinh phục đỉnh Fansipan bằng hệ thống c&aacute;p treo lớn v&agrave; hiện đại,&hellip;</p>', 49, 4790000, 3790000, 'HÀ NỘI – LÀO CAI – SAPA – FANSIPAN', 'b', 1, '2025-05-01', '2025-05-04', NULL),
(187, 'Du lịch mùa Thu - Tour Du lịch Quy Nhơn - Phú Yên từ Sài Gòn 2025', '4 ngày 3 đêm', '<p><em><strong><a href=\"https://dulichviet.com.vn/du-lich-quy-nhon\">Du lịch Quy Nhơn</a></strong>&nbsp;l&agrave; một địa điểm du lịch nổi tiếng ở miền Trung, du lịch Quy Nhơn th&agrave;nh phố ven biển thuộc tỉnh B&igrave;nh Định với cảnh sắc tuyệt đẹp, được bao quanh bởi nhiều b&atilde;i c&aacute;t trắng hoang sơ, những ngọn n&uacute;i xanh tươi b&aacute;t ng&aacute;t v&agrave; những h&ograve;n đảo y&ecirc;n b&igrave;nh. Kh&ocirc;ng giống như những b&atilde;i biển nhộn nhịp kh&aacute;c ở Vũng T&agrave;u hay Nha Trang, Quy Nhơn lại l&agrave; một b&atilde;i biển thi&ecirc;n đường du lịch mang vẻ đẹp y&ecirc;n b&igrave;nh với l&agrave;n nước trong vắt. H&atilde;y c&ugrave;ng&nbsp;<a href=\"https://dulichviet.com.vn/\"><strong>Du Lịch Việt</strong></a>&nbsp;kh&aacute;m ph&aacute; vẻ đẹp c&ugrave;ng những trải nghiệm th&uacute; vị tại Quy Nhơn m&agrave; du kh&aacute;ch sẽ được trải nghiệm khi đến với thi&ecirc;n đường du lịch m&ugrave;a Thu&nbsp;trong nước tại đ&acirc;y. Đ&oacute;n b&igrave;nh minh tr&ecirc;n Eo Gi&oacute; Nằm tr&ecirc;n địa b&agrave;n thuộc x&atilde; Nhơn L&yacute;, c&aacute;ch th&agrave;nh phố Quy Nhơn khoảng 20km, Eo Gi&oacute; l&agrave; địa danh m&agrave; bất cứ ai khi đến&nbsp;<strong><a href=\"https://dulichviet.com.vn/du-lich-quy-nhon\">du lịch Quy Nhơn</a></strong>&nbsp;cũng đều muốn đến thăm, đừng trước khung cảnh tuyệt đẹp tại Eo gi&oacute; du kh&aacute;ch sẽ chỉ muốn thả d&aacute;ng v&agrave; check-in. Từ mỏm đ&aacute; tr&ecirc;n cao khung cảnh nh&igrave;n xuống, bạn thu v&agrave;o tầm mắt một eo biển nhỏ thật b&igrave;nh y&ecirc;n được &ocirc;m ấp trong d&atilde;y n&uacute;i, như một v&ograve;ng tay &ocirc;m đang &ocirc;m gọn bờ biển. Gh&eacute; thăm Eo Gi&oacute; trong tiết trời se se lạnh của một buổi s&aacute;ng sớm tinh mơ m&ugrave;a Thu săn &aacute;nh b&igrave;nh minh l&agrave; một trải nghiệm đi tour m&ugrave;a Thu&nbsp;Quy Nhơn nhất định bạn phải lưu giữ. Lắng nghe tiếng s&oacute;ng biển x&ocirc;, c&ugrave;ng tiếng gi&oacute; vi vu nhẹ nh&agrave;ng như kh&uacute;c t&igrave;nh ca, ngắm nh&igrave;n &aacute;nh mặt trời l&oacute; dạng, v&eacute;n dần m&agrave;n sương mờ ảo huyền diệu&nbsp;tr&ecirc;n biển th&igrave; thật tuyệt vời biết bao.</em></p>', 48, 6599000, 4599000, 'Quy Nhơn - Phú Yên - Mũi Điện - Gành Đá Dĩa - Kỳ Co - Eo Gió', 't', 1, '2025-05-05', '2025-05-08', NULL),
(188, 'Côn Đảo Thiên Đường Của Biển', '3 ngày 2 đêm', '<p>Qu&yacute; kh&aacute;ch tập trung tại s&acirc;n bay T&acirc;n Sơn Nhất (ga trong nước), Hướng dẫn vi&ecirc;n l&agrave;m thủ tục cho đo&agrave;n đ&aacute;p chuyến bay đến C&ocirc;n Đảo.&nbsp;<br />\n<br />\nĐến nơi, xe v&agrave; Hướng dẫn vi&ecirc;n đưa đo&agrave;n<strong>&nbsp;theo con đường quanh co trải d&agrave;i h&agrave;ng c&acirc;y xanh</strong>&nbsp;nối liền từ s&acirc;n bay về trung t&acirc;m thị trấn, chi&ecirc;m ngưỡng cảnh quan thi&ecirc;n nhi&ecirc;n của n&uacute;i cao h&ugrave;ng vĩ, xa xa l&agrave; những h&ograve;n đảo ẩn hiện trong sắc xanh của biển cả &hellip;</p>', 38, 7490000, 5500000, 'Côn Đảo', 'n', 1, '2025-04-28', '2025-04-30', NULL),
(189, 'Du Lịch Miền Nam: Hà Nội - Sài Gòn - Mũi Né - Vũng Tàu 4 Ngày 3 Đêm', '4 ngày 3 đêm', '<p><strong>TRẢI NGHIỆM CHUYẾN DU LỊCH MŨI N&Eacute;</strong></p>\n\n<p>&nbsp;</p>\n\n<p>Rời xa th&agrave;nh phố ồn &agrave;o n&aacute;o nhiệt, rời xa những vất vả của c&ocirc;ng việc, để h&ograve;a m&igrave;nh v&agrave;o với thi&ecirc;n nhi&ecirc;n, nơi chỉ c&oacute; nắng c&oacute; gi&oacute;, những h&agrave;ng c&acirc;y phi lao cao v&uacute;t, c&oacute; tiếng chim mu&ocirc;ng r&iacute;u r&iacute;t v&agrave;o mỗi s&aacute;ng, nơi c&oacute; s&oacute;ng biển r&igrave; r&agrave;o với những khung cảnh tuyệt đẹp. C&oacute; lẽ tất cả chỉ c&oacute; ở&nbsp;Mũi N&eacute;. Du kh&aacute;ch sẽ được trải nghiệm việc ngư d&acirc;n đ&aacute;nh c&aacute; v&ocirc; c&ugrave;ng vất vẻ. Tham quan th&aacute;p ch&agrave;m Poshanư, trường Dục Thanh, b&atilde;i đ&aacute; &Ocirc;ng Địa hay l&agrave;ng cổ Mũi N&eacute;.</p>', 50, 6590000, 3990000, 'Hà Nội - Sài Gòn - Mũi Né - Vũng Tàu', 'n', 1, '2025-05-15', '2025-05-18', NULL),
(192, 'Châu Đốc - Miếu Bà Chúa Xứ - Rừng Tràm Trà Sư - Hà Tiên - Rạch Giá - Cần Thơ', '4 ngày 3 đêm', '<p>Điểm nhấn của chương tr&igrave;nh</p>\n\n<ul>\n	<li>Chinh phục thảm trượt phao kh&ocirc; &quot;Cầu Vồng&quot; d&agrave;i 160m đầu ti&ecirc;n của miền T&acirc;y.</li>\n	<li>Tắm biển v&agrave; đ&oacute;n ho&agrave;ng h&ocirc;n tr&ecirc;n biển Mũi Nai.</li>\n	<li>Trải nghiệm đi t&agrave;u tr&ecirc;n s&ocirc;ng Hậu kh&aacute;m ph&aacute; L&agrave;ng B&egrave; Sắc M&agrave;u v&agrave; L&agrave;ng Chăm An Giang.</li>\n	<li>Tham quan &ldquo;Rừng Tr&agrave;m Tr&agrave; Sư đẹp, nổi tiếng nhất Việt Nam v&agrave; c&acirc;y cầu tre d&agrave;i nhất Việt Nam&rdquo;; check-in phim trường Đất Rừng Phương Nam.</li>\n	<li>T&igrave;m hiểu t&iacute;n ngưỡng thờ mẫu ở Miếu B&agrave; Ch&uacute;a Xứ linh thi&ecirc;ng.</li>\n	<li>Du ngoạn Chợ nổi C&aacute;i Răng - một h&igrave;nh thức họp chợ đặc trưng tại c&aacute;c ng&atilde; s&ocirc;ng của v&ugrave;ng ĐBSCL.</li>\n</ul>', 26, 4090000, 3090000, 'Châu Đốc - Miếu Bà Chúa Xứ - Rừng Tràm Trà Sư - Hà Tiên - Rạch Giá - Cần Thơ', 'n', 1, '2025-04-29', '2025-05-02', NULL),
(193, 'Đà Lạt - Thác Dambri - Samten Hills Đà Lạt - Đồi Chè Cầu Đất - Mongo Land - Hầm Vang Đà Lạt', '4 ngày 3 đêm', '<p>Qu&yacute; kh&aacute;ch tập trung tại Vietravel (190 Pasteur, P. V&otilde; Thị S&aacute;u, Q 3, TP.HCM), xe đưa đo&agrave;n khởi h&agrave;nh đi&nbsp;<strong>Đ&agrave; Lạt</strong>. Tr&ecirc;n đường đo&agrave;n dừng ch&acirc;n tham quan v&agrave; ăn trưa tại Bảo Lộc:</p>\n\n<p><strong>&bull;&nbsp;Th&aacute;c DamBri:</strong>&nbsp;được coi l&agrave; ngọn th&aacute;c lớn nhất v&ugrave;ng đất L&acirc;m Đồng với chiều cao 60m tạo th&agrave;nh 2 d&ograve;ng chảy cao thấp rất h&ugrave;ng vĩ. Khu du lịch th&aacute;c Đamb&#39;ri l&agrave; một quần thể du lịch bao gồm khu vui chơi, hồ, th&aacute;c v&agrave; rừng nguy&ecirc;n sinh rộng h&agrave;ng ngh&igrave;n hecta. Nơi đ&acirc;y c&oacute; &ldquo;ống trượt d&agrave;i nhất Đ&ocirc;ng Nam &Aacute;&rdquo; với độ d&agrave;i 1.650m.</p>', 41, 3790000, 2890000, 'Đà Lạt - Thác Dambri - Samten Hills Đà Lạt - Đồi Chè Cầu Đất - Mongo Land - Hầm Vang Đà Lạt', 't', 1, '2025-05-10', '2025-05-13', NULL),
(194, 'Khám phá đảo Hòn Sơn', '2 ngày 1 đêm', '<p>Hướng dẫn vi&ecirc;n Travela đ&oacute;n kh&aacute;ch tại&nbsp;<strong>bến t&agrave;u Rạch Gi&aacute;</strong>, sau đ&oacute; đo&agrave;n l&agrave;m thủ tục xuống t&agrave;u đi&nbsp;<strong>H&ograve;n Sơn</strong>&nbsp;thời gian dự kiến&nbsp;<strong>07h30 - 09h00.</strong></p>', 50, 1790000, 1390000, 'Hòn Sơn', 'n', 1, '2025-05-06', '2025-05-07', NULL),
(195, 'Quy Nhơn - Eo Gió - Phú Yên', '4 ngày 3 đêm', '<p>Điểm tham quan</p>\n\n<p>Quy Nhơn, Tuy H&ograve;a, Đầm Thị Nại</p>\n\n<p>Ẩm thực</p>\n\n<p>&nbsp;</p>\n\n<p>Đối tượng th&iacute;ch hợp</p>\n\n<p>Cặp đ&ocirc;i, Gia đ&igrave;nh nhiều thế hệ</p>\n\n<p>Thời gian l&yacute; tưởng</p>\n\n<p>&nbsp;</p>\n\n<p>Phương tiện</p>\n\n<p>M&aacute;y bay, Xe du lịch</p>\n\n<p>Khuyến m&atilde;i</p>', 52, 5990000, 4990000, 'Quy Nhơn - Eo Gió - Phú Yên', 't', 1, '2025-05-14', '2025-05-17', NULL),
(196, 'Đà Nẵng - Hội An - Rừng dừa Bảy Mẫu - Trải nghiệm trồng cây tái tạo rừng dừa - Sơn Trà - Bà Nà Hills - CSO Gallery - Chillax Eatery & Hangout', '3 ngày 2 đêm', '<p>Qu&yacute; kh&aacute;ch tập trung tại<strong>&nbsp;ga đi trong&nbsp;nước - s&acirc;n bay T&acirc;n Sơn Nhất</strong>, Hướng dẫn vi&ecirc;n Vietravel hỗ trợ Qu&yacute; kh&aacute;ch l&agrave;m thủ tục đ&aacute;p chuyến bay đi&nbsp;<strong>Đ&agrave; Nẵng</strong>.&nbsp;</p>\n\n<p>Xe v&agrave; Hướng dẫn vi&ecirc;n đ&oacute;n đo&agrave;n tham quan&nbsp;<strong>Rừng dừa Bảy Mẫu&nbsp;</strong>tr&ecirc;n những chiếc thuyền th&uacute;ng len lỏi qua c&aacute;c lạch dừa nước bạt ng&agrave;n, Qu&yacute; kh&aacute;ch trải nghiệm</p>', 50, 8690000, 6690000, 'Đà Nẵng - Hội An - Rừng dừa Bảy Mẫu - Trải nghiệm trồng cây tái tạo rừng dừa - Sơn Trà - Bà Nà Hills - CSO Gallery - Chillax Eatery & Hangout', 't', 0, '2025-05-12', '2025-05-14', NULL),
(197, '', '3 ngày 2 đêm', '<p>Qu&yacute; kh&aacute;ch tập trung tại<strong>&nbsp;ga đi trong&nbsp;nước - s&acirc;n bay T&acirc;n Sơn Nhất</strong>, Hướng dẫn vi&ecirc;n Vietravel hỗ trợ Qu&yacute; kh&aacute;ch l&agrave;m thủ tục đ&aacute;p chuyến bay đi&nbsp;<strong>Đ&agrave; Nẵng</strong>.&nbsp;</p>\n\n<p>Xe v&agrave; Hướng dẫn vi&ecirc;n đ&oacute;n đo&agrave;n tham quan&nbsp;<strong>Rừng dừa Bảy Mẫu&nbsp;</strong>tr&ecirc;n những chiếc thuyền th&uacute;ng len lỏi qua c&aacute;c lạch dừa nước bạt ng&agrave;n, Qu&yacute; kh&aacute;ch trải nghiệm</p>', 55, 8690000, 6690000, 'Đà Nẵng - Hội An - Rừng dừa Bảy Mẫu - Trải nghiệm trồng cây tái tạo rừng dừa - Sơn Trà - Bà Nà Hills - CSO Gallery - Chillax Eatery & Hangout', 't', 1, '2025-04-15', '2025-04-17', NULL),
(198, 'Tour Hà Giang Cao Bằng 5 Ngày 4 Đêm | Bản Giốc – Pác Bó – Động Ngườm Ngao – Ba Bể', '5 ngày 4 đêm', '<p><strong>Tour H&agrave; Giang Cao Bằng 5 Ng&agrave;y 4 Đ&ecirc;m</strong>&nbsp;khởi h&agrave;nh từ H&agrave; Nội sẽ mang đến cho qu&yacute; kh&aacute;ch những trải nghiệm kh&ocirc;ng thể n&agrave;o qu&ecirc;n tại v&ograve;ng cung Đ&ocirc;ng Bắc Tổ Quốc.</p>\n\n<p><strong>ĐIỂM HẤP DẪN NHẤT:</strong></p>\n\n<ul>\n	<li><strong>Tận mắt ngắm nh&igrave;n cao nguy&ecirc;n đ&aacute; Đồng Văn hoang sơ.</strong></li>\n	<li><strong>Đi thuyền tr&ecirc;n s&ocirc;ng Nho Quế qua hẻm vực Tu Sản s&acirc;u nhất Việt Nam.</strong></li>\n	<li><strong>Check-in Cột cờ Lũng C&uacute;, điểm cực Bắc của Tổ quốc.</strong></li>\n	<li><strong>Chi&ecirc;m ngưỡng Th&aacute;c Bản Giốc - Th&aacute;c lớn thứ 4 tr&ecirc;n thế giới nằm giữa bi&ecirc;n giới hai quốc gia.</strong></li>\n	<li><strong>Tham quan hang P&aacute;c B&oacute; - Hồ Ba Bể trong xanh.</strong></li>\n	<li><strong>Kh&aacute;m ph&aacute; những n&eacute;t ẩm thực độc đ&aacute;o của miền sơn cước.</strong></li>\n</ul>', 47, 5190000, 4190000, 'Bản Giốc – Pác Bó – Động Ngườm Ngao – Ba Bể', 'b', 1, '2025-04-10', '2025-04-15', NULL),
(199, 'Tour du lịch Mộc Châu Tà Xùa 2 ngày 1 đêm: Khám Phá Cao Nguyên và Săn Mây', '2 ngày 1 đêm', '<p><strong>Tour du lịch Mộc Ch&acirc;u T&agrave; X&ugrave;a 2 ng&agrave;y 1 đ&ecirc;m</strong>&nbsp;đưa bạn đến kh&aacute;m ph&aacute; đỉnh núi cao thứ 10 của Vi&ecirc;̣t Nam, cực th&iacute;ch hợp với những ai muốn đi trốn phố thị vào dịp cu&ocirc;́i tu&acirc;̀n và nghỉ l&ecirc;̃.&nbsp;</p>\n\n<p><strong>CHƯƠNG TR&Igrave;NH C&Oacute; G&Igrave; HẤP DẪN:</strong></p>\n\n<ul>\n	<li><strong>Đ&oacute;n nắng mai tr&ecirc;n&nbsp;đ&egrave;o Thung Khe.</strong></li>\n	<li><strong>Check-in c&aacute;c điểm đến si&ecirc;u HOT ở Mộc Ch&acirc;u:&nbsp;Vườn hoa Happy land, Cầu K&iacute;nh Bạch Long</strong></li>\n	<li><strong>Săn m&acirc;y T&agrave; X&ugrave;a ngắm b&igrave;nh minh l&ecirc;n, thưởng thức t&aacute;ch c&agrave; ph&ecirc; s&aacute;ng tr&ecirc;n đỉnh n&uacute;i.&nbsp;</strong></li>\n</ul>', 30, 1690000, 1290000, 'Mộc Châu Tà Xùa', 'b', 1, '2025-04-27', '2025-01-28', NULL),
(200, 'Tour du lịch miền Tây: Mỹ Tho – Bến Tre – Cần Thơ 2 ngày 1 đêm', '2 ngày 1 đêm', '<p><strong>Chuyến h&agrave;nh tr&igrave;nh 2 ng&agrave;y 1 đ&ecirc;m đến Mỹ Tho, Bến Tre v&agrave; Cần Thơ hứa hẹn sẽ đưa du kh&aacute;ch kh&aacute;m ph&aacute; trọn vẹn vẻ đẹp s&ocirc;ng nước miền T&acirc;y, c&ugrave;ng những trải nghiệm kh&oacute; qu&ecirc;n.</strong></p>\n\n<p><strong>*ĐIỂM NỔI BẬT CỦA TOUR:</strong></p>\n\n<ul>\n	<li><strong>L&ecirc;n thuyền kh&aacute;m ph&aacute; C&ugrave; Lao Thới Sơn, tự tay h&aacute;i những tr&aacute;i c&acirc;y tươi ngon ngay tại vườn.</strong></li>\n	<li><strong>Thưởng thức bữa tối sang trọng tr&ecirc;n du thuyền 5 sao đẳng cấp tại bến Ninh Kiều.</strong></li>\n	<li><strong>Kh&aacute;m ph&aacute; hương vị độc đ&aacute;o của ẩm thực miền T&acirc;y tại chợ nổi C&aacute;i Răng.</strong></li>\n	<li><strong>T&igrave;m hiểu về lịch sử v&agrave; văn h&oacute;a Việt Nam tại thiền viện Tr&uacute;c L&acirc;m Phương Nam.</strong></li>\n	<li><strong>Ghi lại những khoảnh khắc đẹp tại Căn Nh&agrave; M&agrave;u T&iacute;m với khung cảnh n&ecirc;n thơ.</strong></li>\n</ul>', 33, 2590000, 1790000, 'Mỹ Tho – Bến Tre – Cần Thơ', 'n', 1, '2025-05-08', '2025-05-09', NULL),
(201, 'Tour Mù Cang Chải 2 Ngày 1 Đêm: Đèo Khau Phạ – Tú Lệ | Mùa vàng trên Tây Bắc', '2 ngày 1 đêm', '<p>Tour M&ugrave; Cang Chải&nbsp;2 Ng&agrave;y 1&nbsp;Đ&ecirc;m của Vietnam Booking sẽ đưa bạn đến với những thửa ruộng bậc thang v&agrave;ng &oacute;ng &aacute;nh, trải d&agrave;i như v&ocirc; tận, những đồi ch&egrave; bao la xanh mướt c&ugrave;ng c&aacute;c bản l&agrave;ng d&acirc;n tộc thơ mộng.</p>\n\n<p>TOUR C&Oacute; G&Igrave; HOT?</p>\n\n<ul>\n	<li><strong>Kh&aacute;m ph&aacute; n&eacute;t đẹp văn h&oacute;a của đồng b&agrave;o d&acirc;n tộc Th&aacute;i.</strong></li>\n	<li><strong>Chinh phục đ&egrave;o Khau Phạ, một trong tứ đại đỉnh đ&egrave;o của miền Bắc.</strong></li>\n	<li><strong>Kh&aacute;m ph&aacute; bản Kim Nọi, chợ M&ugrave; Cang Chải, bản&nbsp;T&uacute; Lệ&nbsp;c&ograve;n hoang sơ giữa đại ng&agrave;n.</strong></li>\n	<li><strong>Ch&igrave;m đắm với: Đồi ch&egrave; Thanh Sơn, c&aacute;nh đồng Mường L&ograve;, thung lũng Khau Phạ.</strong></li>\n	<li><strong>Kh&aacute;m ph&aacute; một trong những nơi c&oacute; ruộng bậc thang đẹp nhất Việt Nam: ruộng bậc thang h&igrave;nh M&acirc;m X&ocirc;i, Mũi Gi&agrave;y</strong></li>\n</ul>', 25, 1490000, 990000, 'Đèo Khau Phạ – Tú Lệ', 'b', 1, '2025-04-24', '2025-04-25', NULL),
(202, 'Tour du lịch Hà Nội – Sapa – Hạ Long – Ninh Bình 5N4Đ | Khám phá danh thắng miền Bắc', '5 ngày 4 đêm', '<p><strong>Tour du lịch H&agrave; Nội Sapa Ninh B&igrave;nh&nbsp;Hạ Long 5N4Đ</strong>&nbsp;sẽ đưa qu&yacute; kh&aacute;ch đi kh&aacute;m ph&aacute; trọn vẹn những điểm đến nổi tiếng&nbsp;miền Bắc.</p>\n\n<p><strong>*ĐIỂM NỔI BẬT TRONG TOUR:</strong></p>\n\n<ul>\n	<li><strong>Viếng lăng Chủ tịch Hồ Ch&iacute; Minh</strong></li>\n	<li><strong>Tham quan Ch&ugrave;a Trấn Quốc &ndash; Hồ T&acirc;y</strong></li>\n	<li><strong>T&igrave;m hiểu lịch sử:&nbsp;</strong><strong>Văn Miếu &ndash; Quốc Tử Gi&aacute;m,&nbsp;Nh&agrave; t&ugrave; Hỏa L&ograve;,&nbsp;Bảo t&agrave;ng D&acirc;n tộc học</strong></li>\n	<li><strong>Kh&aacute;m ph&aacute; Ninh B&igrave;nh:&nbsp;Ch&ugrave;a B&aacute;i Đ&iacute;nh,&nbsp;khu du lịch Sinh th&aacute;i Tr&agrave;ng An</strong></li>\n	<li><strong>Vi vu Hạ Long:&nbsp;</strong><strong>Hang Sửng Sốt,</strong>&nbsp;<strong>Đảo Titop,&nbsp;Vịnh Hạ Long</strong></li>\n	<li><strong>Chinh phục Sapa:&nbsp;Đỉnh Fansipan, b</strong><strong>ản C&aacute;t C&aacute;t &ndash; Th&aacute;c Ti&ecirc;n Sa</strong></li>\n</ul>', 44, 5250000, 4350000, 'Hà Nội – Sapa – Hạ Long – Ninh Bình', 'b', 0, '2025-04-22', '2025-04-26', NULL),
(203, 'Tour du lịch Hà Nội – Sapa – Hạ Long – Ninh Bình 5N4Đ | Khám phá danh thắng miền Bắc', '5 ngày 4 đêm', '<p><strong>Tour du lịch H&agrave; Nội Sapa Ninh B&igrave;nh&nbsp;Hạ Long 5N4Đ</strong>&nbsp;sẽ đưa qu&yacute; kh&aacute;ch đi kh&aacute;m ph&aacute; trọn vẹn những điểm đến nổi tiếng&nbsp;miền Bắc.</p>\n\n<p><strong>*ĐIỂM NỔI BẬT TRONG TOUR:</strong></p>\n\n<ul>\n	<li><strong>Viếng lăng Chủ tịch Hồ Ch&iacute; Minh</strong></li>\n	<li><strong>Tham quan Ch&ugrave;a Trấn Quốc &ndash; Hồ T&acirc;y</strong></li>\n	<li><strong>T&igrave;m hiểu lịch sử:&nbsp;</strong><strong>Văn Miếu &ndash; Quốc Tử Gi&aacute;m,&nbsp;Nh&agrave; t&ugrave; Hỏa L&ograve;,&nbsp;Bảo t&agrave;ng D&acirc;n tộc học</strong></li>\n	<li><strong>Kh&aacute;m ph&aacute; Ninh B&igrave;nh:&nbsp;Ch&ugrave;a B&aacute;i Đ&iacute;nh,&nbsp;khu du lịch Sinh th&aacute;i Tr&agrave;ng An</strong></li>\n	<li><strong>Vi vu Hạ Long:&nbsp;</strong><strong>Hang Sửng Sốt,</strong>&nbsp;<strong>Đảo Titop,&nbsp;Vịnh Hạ Long</strong></li>\n	<li><strong>Chinh phục Sapa:&nbsp;Đỉnh Fansipan, b</strong><strong>ản C&aacute;t C&aacute;t &ndash; Th&aacute;c Ti&ecirc;n Sa</strong></li>\n</ul>', 41, 5250000, 4750000, 'Hà Nội – Sapa – Hạ Long – Ninh Bình', 'b', 1, '2025-04-18', '2025-03-22', NULL),
(204, 'Sài Gòn - Phan Thiết - Mũi Né', '4 ngày 3 đêm', '<p>Qu&yacute; kh&aacute;ch tập trung tại văn ph&ograve;ng Vietravel (03 Hai B&agrave; Trưng, Ho&agrave;n Kiếm), xe đưa Qu&yacute; kh&aacute;ch ra s&acirc;n bay Nội B&agrave;i (Ga nội địa), HDV hỗ trợ kh&aacute;ch l&agrave;m thủ tục đ&aacute;p chuyến bay đi TP.HCM. Đến s&acirc;n bay T&acirc;n Sơn Nhất, xe v&agrave; HDV Vietravel đ&oacute;n Qu&yacute; kh&aacute;ch đi tham quan:</p>\n\n<p><strong>- Hội Trường Thống Nhất:</strong>&nbsp;c&oacute; t&ecirc;n gọi cũ l&agrave; Dinh Độc Lập</p>\n\n<p><strong>- Nh&agrave; thờ Đức B&agrave; v&agrave; Bưu điện trung t&acirc;m Th&agrave;nh Phố</strong>&hellip; Check-in Kh&ocirc;ng gian đường s&aacute;ch</p>\n\n<p>Sau bữa trưa, Đo&agrave;n nhận ph&ograve;ng kh&aacute;ch sạn v&agrave; nghỉ ngơi.</p>\n\n<p>Chiều đo&agrave;n tiếp tục tham quan:</p>', 42, 7490000, 5690000, 'Sài Gòn - Phan Thiết - Mũi Né', 'n', 1, '2025-04-23', '2025-04-26', NULL),
(205, 'NHA TRANG 3N3Đ | DU XUÂN THÀNH PHỐ BIỂN', '3 ngày 2 đêm', '<p><strong>QU&Agrave; TẶNG</strong></p>\n\n<ul>\n	<li><strong><em>N&oacute;n du lịch Travela</em></strong></li>\n</ul>', 44, 3690000, 1845000, 'CẦN THƠ – TP. HCM - NHA TRANG', 'n', 1, '2025-04-28', '2025-03-30', NULL),
(206, 'ĐÀ LẠT 3N3Đ | FESTIVALS HOA 2024', '3 ngày 2 đêm', '<p><strong>QU&Agrave; TẶNG</strong></p>\n\n<ul>\n	<li><strong><em>N&oacute;n du lịch Travela</em></strong></li>\n</ul>', 50, 3590000, 1795000, 'ĐÀ LẠT', 't', 1, '2025-04-27', '2025-03-29', NULL),
(207, 'MIỀN BẮC 4N3Đ | HÀ NỘI – NINH BÌNH – HẠ LONG – YÊN TỬ (TẾT ÂM LỊCH 2025)', '3 ngày 2 đêm', '<p>ĐIỂM NHẤN CHƯƠNG TR&Igrave;NH&nbsp;</p>\n\n<p><strong>&bull; Tham quan</strong>: Hồ T&acirc;y, Văn Miếu &ndash; Quốc Tử Gi&aacute;m, Khu du lịch Tr&agrave;ng An &ndash; Ninh B&igrave;nh, Vịnh Hạ Long, L&agrave;ng gốm B&aacute;t Tr&agrave;ng,&hellip;&nbsp;</p>\n\n<p><strong>&bull; Lưu tr&uacute;:&nbsp;</strong>Kh&aacute;ch sạn chuẩn 3 sao.&nbsp;</p>\n\n<p><strong>&bull; Hoạt động kh&aacute;c:&nbsp;</strong>Trải nghiệm kh&aacute;m ph&aacute; cỗ Tết Miền Bắc tại l&agrave;ng gốm B&aacute;t Tr&agrave;ng, Ngồi xe điện kh&aacute;m ph&aacute; phố cổ H&agrave; Nội.&nbsp;</p>', 33, 6290000, 5000000, 'HÀ NỘI – NINH BÌNH – HẠ LONG – YÊN TỬ', 'b', 1, '2025-01-11', '2025-01-14', NULL),
